mapper.select:
SELECT
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT X.APC_NM FROM TB_APC_INFO X WHERE X.APC_CD = pt0.APC_CD) AS APC_NM , (SELECT FN_GET_PRDCR_NM(pt0.RPRS_PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_GRD', pt0.GDS_GRD) FROM DUAL) AS GDS_GRD_NM , (SELECT FN_GET_COM_CD_VL_NM('FCLT_CD', pt0.FCLT_CD, pt0.APC_CD) FROM DUAL) AS FCLT_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_SPMT_PCKG_UNIT_NM(pt0.SPMT_PCKG_UNIT_CD, pt0.APC_CD) FROM DUAL) AS SPMT_PCKG_UNIT_NM FROM ( SELECT /* com.at.apcss.am.invntr.mapper.GdsInvntrMapper.selectUpdateTrnsfGdsInvntrList */ GI.APC_CD AS APC_CD , GI.PCKGNO AS PCKGNO , GI.PCKG_SN AS PCKG_SN , GI.PCKG_YMD AS PCKG_YMD , GI.FCLT_CD AS FCLT_CD , GI.RPRS_PRDCR_CD AS RPRS_PRDCR_CD , GI.ITEM_CD AS ITEM_CD , GI.VRTY_CD AS VRTY_CD , GI.SPCFCT_CD AS SPCFCT_CD , GI.GDS_GRD AS GDS_GRD , GI.GDS_SE_CD AS GDS_SE_CD , GI.PRDCTN_YR AS PRDCTN_YR , GI.SPMT_PCKG_UNIT_CD AS SPMT_PCKG_UNIT_CD , GI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , GI.PCKG_QNTT AS PCKG_QNTT , GI.PCKG_WGHT AS PCKG_WGHT , GI.SPMT_QNTT AS SPMT_QNTT , GI.SPMT_WGHT AS SPMT_WGHT , GI.INVNTR_QNTT AS INVNTR_QNTT , GI.INVNTR_WGHT AS INVNTR_WGHT , GI.PLOR_CD AS PLOR_CD , GI.DEL_YN AS DEL_YN , NVL( (SELECT CMND_QNTT FROM TB_SPMT_CMND X WHERE X.APC_CD = GI.APC_CD AND X.ITEM_CD = GI.ITEM_CD AND X.VRTY_CD = GI.VRTY_CD AND X.SPCFCT_CD = GI.SPCFCT_CD AND X.GDS_GRD = GI.GDS_GRD ), 0) AS CMND_QNTT , NVL( (SELECT CMND_WGHT FROM TB_SPMT_CMND X WHERE X.APC_CD = GI.APC_CD AND X.ITEM_CD = GI.ITEM_CD AND X.VRTY_CD = GI.VRTY_CD AND X.SPCFCT_CD = GI.SPCFCT_CD AND X.GDS_GRD = GI.GDS_GRD ), 0) AS CMND_WGHT FROM TB_GDS_INVNTR GI WHERE 1=1 AND GI.APC_CD = #{apcCd}
AND GI.WAREHOUSE_SE_CD = #{warehouseSeCd}
AND GI.ITEM_CD = #{itemCd}
AND GI.VRTY_CD = #{vrtyCd}
AND GI.SPCFCT_CD = #{spcfctCd}
AND GI.GDS_SE_CD = #{gdsSeCd}
AND GI.RPRS_PRDCR_CD = #{rprsPrdcrCd}
AND GI.SPMT_PCKG_UNIT_CD = #{spmtPckgUnitCd}
AND GI.RPRS_PRDCR_CD = #{prdcrCd}
AND GI.WRHS_SE_CD = #{wrhsSeCd}
ORDER BY PCKG_YMD, PCKGNO, PCKG_SN ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:

		UPDATE /* com.at.apcss.am.invntr.mapper.GdsInvntrMapper.updateGdsInvntrList */
			TB_GDS_INVNTR
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}


			, WAREHOUSE_SE_CD			= #{warehouseSeCd}


			, INVNTR_QNTT			= #{invntrQntt}


			, INVNTR_Wght			= #{invntrWght}


		 WHERE APC_CD = #{apcCd}
		   AND PCKGNO = #{pckgno}
