mapper.select:
SELECT
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_APC_NM(pt0.APC_CD) FROM DUAL) AS APC_NM , (SELECT FN_GET_PRDCR_NM(pt0.PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_GRD_NM_RAWMTR(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD) FROM DUAL) AS GRD_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WRHS_SE_CD', pt0.WRHS_SE_CD) FROM DUAL) AS WRHS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('TRSPRT_SE_CD', pt0.TRSPRT_SE_CD) FROM DUAL) AS TRSPRT_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_GRD_NM(pt0.ITEM_CD, pt0.GRD_CD, pt0.APC_CD) FROM DUAL) AS GRD_NM , (pt0.INVNTR_QNTT - pt0.CMND_QNTT) AS PSBLTY_INVNTR_QNTT , (pt0.INVNTR_WGHT - pt0.CMND_WGHT) AS PSBLTY_INVNTR_WGHT FROM (
SELECT /* com.at.apcss.am.invntr.mapper.RawMtrInvntrMapper.selectTrnsfRawMtrInvntrList */ RI.APC_CD AS APC_CD , RI.WRHSNO AS WRHSNO , RI.PLTNO AS PLTNO , RI.WRHS_YMD AS WRHS_YMD , RI.PRDCR_CD AS PRDCR_CD , RI.ITEM_CD AS ITEM_CD , RI.VRTY_CD AS VRTY_CD , RI.GDS_SE_CD AS GDS_SE_CD , RI.WRHS_SE_CD AS WRHS_SE_CD , RI.TRSPRT_SE_CD AS TRSPRT_SE_CD , RI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , RI.BX_KND AS BX_KND , RI.GRD_CD AS GRD_CD , RI.WRHS_QNTT AS WRHS_QNTT , RI.WRHS_WGHT AS WRHS_WGHT , RI.INPT_QNTT AS INPT_QNTT , RI.INPT_WGHT AS INPT_WGHT , RI.INVNTR_QNTT AS INVNTR_QNTT , RI.INVNTR_WGHT AS INVNTR_WGHT , RI.RMRK AS RMRK , (SELECT NVL(SUM(CMND_QNTT),0) FROM TB_SORT_CMND X WHERE X.PLTNO = RI.PLTNO AND X.DEL_YN = 'N' ) AS CMND_QNTT , (SELECT NVL(SUM(CMND_WGHT), 0) FROM TB_SORT_CMND X WHERE X.PLTNO = RI.PLTNO AND X.DEL_YN = 'N' ) AS CMND_WGHT
FROM TB_RAW_MTR_INVNTR RI
WHERE RI.INVNTR_QNTT > 0
AND RI.APC_CD = #{apcCd}
AND RI.PRDCR_CD = #{prdcrCd}
AND RI.ITEM_CD = #{itemCd}
AND RI.VRTY_CD in (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{vrtyCd})))
AND RI.GDS_SE_CD = #{gdsSeCd}
AND RI.WRHS_SE_CD = #{wrhsSeCd}
AND RI.WAREHOUSE_SE_CD = #{warehouseSeCd}
ORDER BY APC_CD, WRHS_YMD, WRHSNO ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

SELECT pt0.* , (SELECT FN_GET_APC_NM(pt0.TRNSF_APC_CD) FROM DUAL) AS TRNSF_APC_NM , (SELECT FN_GET_PRDCR_NM(pt0.PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_GRD_NM_RAWMTR(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD) FROM DUAL) AS GRD_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WRHS_SE_CD', pt0.WRHS_SE_CD) FROM DUAL) AS WRHS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('TRSPRT_SE_CD', pt0.TRSPRT_SE_CD) FROM DUAL) AS TRSPRT_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM FROM (
SELECT RI.APC_CD AS APC_CD , RI.WRHSNO AS WRHSNO , RI.PLTNO AS PLTNO , RI.WRHS_YMD AS WRHS_YMD , RI.PRDCR_CD AS PRDCR_CD , RI.ITEM_CD AS ITEM_CD , RI.VRTY_CD AS VRTY_CD , RI.GDS_SE_CD AS GDS_SE_CD , RI.WRHS_SE_CD AS WRHS_SE_CD , RI.TRSPRT_SE_CD AS TRSPRT_SE_CD , RI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , RI.BX_KND AS BX_KND , RI.GRD_CD AS GRD_CD , RI.PRDCTN_YR AS PRDCTN_YR , RI.GRD_IDNTY_PRSN AS GRD_IDNTY_PRSN , RI.RMRK AS RMRK , RI.INPT_QNTT AS INPT_QNTT , RI.INPT_WGHT AS INPT_WGHT , RI.TRNSF_QNTT AS TRNSF_QNTT , RI.TRNSF_WGHT AS TRNSF_WGHT , RI.INPT_PRGRS_QNTT AS INPT_PRGRS_QNTT , RI.INPT_PRGRS_WGHT AS INPT_PRGRS_WGHT , RI.INVNTR_QNTT AS INVNTR_QNTT , RI.INVNTR_WGHT AS INVNTR_WGHT , RI.PRCS_NO AS PRCS_NO , RI.PRCS_TYPE AS PRCS_TYPE , TH.APC_CD AS TRNSF_APC_CD , TH.TRNSF_YMD AS TRNSF_YMD , TH.TRNSF_SN AS TRNSF_SN FROM TB_RAW_MTR_INVNTR RI , TB_INVNTR_TRNSF_HSTRY TH WHERE RI.APC_CD = TH.TRNSF_APC_CD AND RI.PRCS_NO = TH.PRCS_NO AND RI.DEL_YN = 'N' AND TH.DEL_YN = 'N' AND TH.INVNTR_SE_CD = '1'
AND RI.APC_CD = #{apcCd}
AND RI.ITEM_CD = #{itemCd}
AND RI.WRHS_YMD >= #{wrhsYmdFrom}
AND RI.WRHS_YMD <= #{wrhsYmdTo}
ORDER BY RI.WRHS_YMD DESC, RI.WRHSNO DESC ) pt0

mapper.update:

		UPDATE /* com.at.apcss.am.trnsf.mapper.TrnsfRawMtrInvntrMapper.updateTrnsfRawMtrInvntrList */
			TB_RAW_MTR_INVNTR
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}



			, WAREHOUSE_SE_CD			= #{trnsfWarehouse}


			, INVNTR_QNTT			= #{mvmnQntt}


			, INVNTR_Wght			= #{mvmnWght}


		 WHERE APC_CD = #{apcCd}
		   AND WRHSNO = #{wrhsno}
