mapper.select:
SELECT
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD) FROM DUAL) AS GRD_NM , (SELECT FN_GET_COM_CD_VL_NM('SORT_FCLT_CD', pt0.FCLT_CD, pt0.APC_CD) FROM DUAL) AS FCLT_NM , (SELECT FN_GET_PRDCR_NM(pt0.RPRS_PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD',pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (pt0.INVNTR_QNTT - pt0.CMND_QNTT) AS PSBLTY_INVNTR_QNTT , (pt0.INVNTR_WGHT - pt0.CMND_QNTT) AS PSBLTY_INVNTR_WGHT FROM ( SELECT /* com.at.apcss.am.invntr.mapper.SortInvntrMapper.selectTrnsfSortInvntrDsctnList */ SI.APC_CD AS APC_CD , SI.SORTNO AS SORTNO , SI.SORT_SN AS SORT_SN , SI.GRD_CD AS GRD_CD , SI.INPT_YMD AS INPT_YMD , SI.FCLT_CD AS FCLT_CD , SI.RPRS_PRDCR_CD AS RPRS_PRDCR_CD , SI.ITEM_CD AS ITEM_CD , SI.VRTY_CD AS VRTY_CD , SI.SPCFCT_CD AS SPCFCT_CD , SI.GDS_SE_CD AS GDS_SE_CD , SI.WRHS_SE_CD AS WRHS_SE_CD , SI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , SI.SORT_QNTT AS SORT_QNTT , SI.SORT_WGHT AS SORT_WGHT , SI.PCKG_QNTT AS PCKG_QNTT , SI.PCKG_WGHT AS PCKG_WGHT , SI.INVNTR_QNTT AS INVNTR_QNTT , SI.INVNTR_WGHT AS INVNTR_WGHT , SI.DEL_YN AS DEL_YN , SI.PRDCTN_YR AS PRDCTN_YR , (SELECT NVL(SUM(CMND_QNTT), 0) FROM TB_PCKG_CMND X WHERE X.SORTNO = SI.SORTNO AND X.SORT_SN = SI.SORT_SN AND X.DEL_YN = 'N' ) AS CMND_QNTT , (SELECT NVL(SUM(CMND_WGHT), 0) FROM TB_PCKG_CMND X WHERE X.SORTNO = SI.SORTNO AND X.SORT_SN = SI.SORT_SN AND X.DEL_YN = 'N' ) AS CMND_WGHT FROM TB_SORT_INVNTR SI WHERE SI.DEL_YN = 'N' AND SI.INVNTR_QNTT > 0 AND SI.APC_CD = #{apcCd}
AND SI.ITEM_CD = #{itemCd}
AND SI.VRTY_CD = #{vrtyCd}
AND SI.RPRS_PRDCR_CD = #{prdcrCd}
AND SI.WAREHOUSE_SE_CD = #{warehouseSeCd}
AND SI.GDS_SE_CD = #{gdsSeCd}
AND SI.WRHS_SE_CD = #{wrhsSeCd}
AND SI.SPCFCT_CD = #{spcfctCd}
ORDER BY APC_CD, INPT_YMD, SORTNO ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

SELECT pt0.* , (SELECT FN_GET_APC_NM(pt0.TRNSF_APC_CD) FROM DUAL) AS TRNSF_APC_NM , (SELECT FN_GET_PRDCR_NM(pt0.RPRS_PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD) FROM DUAL) AS GRD_NM , (SELECT FN_GET_COM_CD_VL_NM('SORT_FCLT_CD', pt0.FCLT_CD, pt0.APC_CD) FROM DUAL) AS FCLT_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WRHS_SE_CD', pt0.WRHS_SE_CD) FROM DUAL) AS WRHS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM FROM ( SELECT /* com.at.apcss.am.trnsf.mapper.TrnsfSortInvntrMapper.selectTrnsfCfmtnSortInvntrList */ SI.APC_CD AS APC_CD , SI.SORTNO AS SORTNO , SI.SORT_SN AS SORT_SN , SI.GRD_CD AS GRD_CD , SI.INPT_YMD AS INPT_YMD , SI.FCLT_CD AS FCLT_CD , SI.RPRS_PRDCR_CD AS RPRS_PRDCR_CD , SI.ITEM_CD AS ITEM_CD , SI.VRTY_CD AS VRTY_CD , SI.SPCFCT_CD AS SPCFCT_CD , SI.GDS_SE_CD AS GDS_SE_CD , SI.WRHS_SE_CD AS WRHS_SE_CD , SI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , SI.PRDCTN_YR AS PRDCTN_YR , SI.INVNTR_QNTT AS INVNTR_QNTT , SI.INVNTR_WGHT AS INVNTR_WGHT , SI.PCKG_QNTT AS PCKG_QNTT , SI.PCKG_WGHT AS PCKG_WGHT , SI.TRNSF_QNTT AS TRNSF_QNTT , SI.TRNSF_WGHT AS TRNSF_WGHT , SI.INPT_PRGRS_WGHT AS INPT_PRGRS_WGHT , SI.INPT_PRGRS_QNTT AS INPT_PRGRS_QNTT , SI.PRCSNO AS PRCSNO , SI.PRCS_SN AS PRCS_SN , SI.PRCS_TYPE AS PRCS_TYPE , TH.APC_CD AS TRNSF_APC_CD , TH.TRNSF_YMD AS TRNSF_YMD , TH.TRNSF_SN AS TRNSF_SN FROM TB_SORT_INVNTR SI , TB_INVNTR_TRNSF_HSTRY TH WHERE SI.APC_CD = TH.TRNSF_APC_CD AND SI.PRCSNO = TH.PRCSNO AND SI.PRCS_SN = TH.PRCS_SN AND SI.DEL_YN = 'N' AND TH.DEL_YN = 'N' AND TH.INVNTR_SE_CD = '2'
AND SI.APC_CD = #{apcCd}
AND SI.ITEM_CD = #{itemCd}
AND SI.INPT_YMD >= #{inptYmdFrom}
AND SI.INPT_YMD <= #{inptYmdTo}
ORDER BY SI.INPT_YMD DESC, SI.SORTNO DESC, SI.SORT_SN DESC ) pt0

mapper.update:

		UPDATE /* com.at.apcss.am.invntr.mapper.SortInvntrMapper.updateSortInvntrDsctnList */
			TB_SORT_INVNTR
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}



			, WAREHOUSE_SE_CD			= #{trnsfWarehouse}


			, INVNTR_QNTT			= #{mvmnQntt}


			, INVNTR_Wght			= #{mvmnWght}


		 WHERE APC_CD = #{apcCd}
		   AND SORTNO = #{sortno}
