mapper.select:
SELECT /* com.at.apcss.am.wgh.mapper.WghPrfmncMapper.selectWghPrfmncList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_PRDCR_NM(pt0.PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_GRD_NM_RAWMTR(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD) FROM DUAL) AS GRD_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WRHS_SE_CD', pt0.WRHS_SE_CD) FROM DUAL) AS WRHS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('TRSPRT_SE_CD', pt0.TRSPRT_SE_CD) FROM DUAL) AS TRSPRT_SE_NM , pt0.WHOL_WGHT - pt0.EMPT_VHCL_WGHT - pt0.RDCD_WGHT AS ACTL_WGHT , (SELECT FN_GET_STD_GRD_RAWMTR(pt0.APC_CD, pt0.WRHSNO) FROM DUAL) AS STD_GRD , (SELECT FN_GET_STD_GRD_RAWMTR(pt0.APC_CD, pt0.WRHSNO, 'CD') FROM DUAL) AS STD_GRD_CD FROM ( SELECT WC.APC_CD AS APC_CD , WC.WGHNO AS WGHNO , MAX(WP.WRHSNO) AS WRHSNO , MAX(WC.WGH_YMD) AS WGH_YMD , MAX(WC.VHCLNO) AS VHCLNO , MAX(WC.PRDCR_CD) AS PRDCR_CD , MAX(WC.WHOL_WGHT) AS WHOL_WGHT , MAX(WC.EMPT_VHCL_WGHT) AS EMPT_VHCL_WGHT , MAX(WC.RDCD_RT) AS RDCD_RT , MAX(WC.RDCD_WGHT) AS RDCD_WGHT , MAX(WC.WRHS_WGHT) AS WRHS_WGHT , MAX(WC.RMRK) AS RMRK , (SELECT X.APC_NM FROM TB_APC_INFO X WHERE X.APC_CD = WC.APC_CD) AS APC_NM , (SELECT MAX(X.GRD_CD) FROM TB_WGH_PRFMNC_DTL X WHERE X.APC_CD = WC.APC_CD AND X.WGHNO = WC.WGHNO AND X.DEL_YN = 'N') AS GRD_CD , (SELECT SUM(X.WGHT) FROM TB_WGH_PRFMNC_DTL X WHERE X.APC_CD = WC.APC_CD AND X.WGHNO = WC.WGHNO AND X.DEL_YN = 'N' AND X.PLT_BX_SE_CD = 'P') AS PLT_WGHT , (SELECT SUM(X.WGHT) FROM TB_WGH_PRFMNC_DTL X WHERE X.APC_CD = WC.APC_CD AND X.WGHNO = WC.WGHNO AND X.DEL_YN = 'N' AND X.PLT_BX_SE_CD = 'B') AS BX_WGHT , MAX(INV.WAREHOUSE_SE_CD) AS WAREHOUSE_SE_CD , MAX(INV.ITEM_CD) AS ITEM_CD , MAX(INV.VRTY_CD) AS VRTY_CD , MAX(INV.GDS_SE_CD) AS GDS_SE_CD , MAX(INV.WRHS_SE_CD) AS WRHS_SE_CD , MAX(INV.TRSPRT_SE_CD) AS TRSPRT_SE_CD , MAX(WVI.DRVR_NM) AS DRVR_NM FROM TB_WGH_PRFMNC_COM WC , TB_RAW_MTR_WRHS_PRFMNC WP , TB_RAW_MTR_INVNTR INV , TB_WRHS_VHCL_INFO WVI WHERE WC.APC_CD = WP.APC_CD AND WC.WGHNO = WP.WGHNO AND WP.APC_CD = INV.APC_CD AND WP.WRHSNO = INV.WRHSNO AND WC.DEL_YN = 'N' AND WP.DEL_YN = 'N' AND INV.DEL_YN = 'N' AND WC.APC_CD = WVI.APC_CD(+) AND WC.VHCLNO = WVI.VHCLNO(+)
AND WC.APC_CD = #{apcCd}
AND WC.WGHNO = #{wghno}
AND WC.WGH_YMD = #{wghYmd}
AND WC.WGH_YMD >= #{wghYmdFrom}
AND WC.WGH_YMD <= #{wghYmdTo}
GROUP BY WC.APC_CD, WC.WGHNO ORDER BY APC_CD, WGH_YMD, WGHNO ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.am.wgh.mapper.WghPrfmncMapper.selectWghPrfmncComList */ WC.APC_CD AS APC_CD , WC.WGHNO AS WGHNO , WC.WGH_YMD AS WGH_YMD , WC.VHCLNO AS VHCLNO , WC.WHOL_WGHT AS WHOL_WGHT , WC.EMPT_VHCL_WGHT AS EMPT_VHCL_WGHT , WC.RDCD_RT AS RDCD_RT , WC.RDCD_WGHT AS RDCD_WGHT , WC.WRHS_WGHT AS WRHS_WGHT , WC.RMRK AS RMRK , WC.DEL_YN AS DEL_YN , (SELECT X.APC_NM FROM TB_APC_INFO X WHERE X.APC_CD = WC.APC_CD) AS APC_NM FROM TB_WGH_PRFMNC_COM WC WHERE 1=1
AND WC.APC_CD = #{apcCd}
AND WC.WGHNO = #{wghno}
AND WC.DEL_YN = #{delYn}
AND WC.WGH_YMD = #{wghYmd}
AND WC.WGH_YMD >= #{wghYmdFrom}
AND WC.WGH_YMD <= #{wghYmdTo}
ORDER BY APC_CD, WGH_YMD, WGHNO
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

