mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.am.wgh.mapper.WghPrfmncMapper.selectWghPrfmncDtlList */ WD.APC_CD AS APC_CD , WD.WGHNO AS WGHNO , WD.WGH_SN AS WGH_SN , WD.PLT_BX_SE_CD AS PLT_BX_SE_CD , WD.PLT_BX_KND AS PLT_BX_KND , WD.GRD_CD AS GRD_CD , WD.UNIT_WGHT AS UNIT_WGHT , WD.QNTT AS QNTT , WD.QNTT * WD.UNIT_WGHT AS WGHT -- WD.WGHT , WD.WGHT AS CNVRT_WGHT , WD.DEL_YN AS DEL_YN , PB.UNIT_CD AS UNIT_CD , PB.PLT_BX_NM AS PLT_BX_NM , (SELECT FN_GET_COM_CD_VL_NM('UNIT_CD', PB.UNIT_CD) FROM DUAL) AS UNIT_NM FROM TB_WGH_PRFMNC_DTL WD
LEFT JOIN TB_PLT_BX_INFO PB ON PB.APC_CD = WD.APC_CD AND PB.PLT_BX_SE_CD = WD.PLT_BX_SE_CD AND PB.PLT_BX_CD = WD.PLT_BX_KND AND PB.DEL_YN = 'N'
WHERE 1=1
AND WD.DEL_YN = #{delYn}
AND WD.APC_CD = #{apcCd}
AND WD.WGHNO = #{wghno}
AND WD.WGH_SN = #{wghSn}
AND WD.PLT_BX_SE_CD = #{pltBxSeCd}
AND WD.PLT_BX_KND = #{pltBxKnd}
AND WD.GRD_CD = #{grdCd}
ORDER BY APC_CD, WGHNO, WGH_SN
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

