SELECT /* com.at.apcss.am.wgh.mapper.WghPrfmncMapper.selectWghPrfmncList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , pt0.WGHNO || '-' || pt0.WGH_SN AS WGHNO_INDCT , (SELECT FN_GET_APC_NM(pt0.APC_CD) FROM DUAL) AS APC_NM , (SELECT FN_GET_PRDCR_NM(pt0.PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_GRD_NM_RAWMTR(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD) FROM DUAL) AS GRD_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WRHS_SE_CD', pt0.WRHS_SE_CD) FROM DUAL) AS WRHS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('TRSPRT_SE_CD', pt0.TRSPRT_SE_CD) FROM DUAL) AS TRSPRT_SE_NM , pt0.WHOL_WGHT - pt0.EMPT_VHCL_WGHT - pt0.RDCD_WGHT AS ACTL_WGHT , (SELECT FN_GET_STD_GRD_RAWMTR(pt0.APC_CD, pt0.WRHSNO) FROM DUAL) AS STD_GRD , (SELECT FN_GET_STD_GRD_RAWMTR(pt0.APC_CD, pt0.WRHSNO, 'CD') FROM DUAL) AS STD_GRD_CD , (SELECT X.DRVR_NM FROM TB_WRHS_VHCL_INFO X WHERE X.APC_CD = pt0.APC_CD AND X.VHCLNO = pt0.VHCLNO) AS DRVR_NM FROM ( SELECT WC.APC_CD AS APC_CD , WC.WGHNO AS WGHNO , WD.WGH_SN AS WGH_SN , WD.WRHSNO AS WRHSNO , WC.WGH_YMD AS WGH_YMD , WC.VHCLNO AS VHCLNO , WC.PRDCR_CD AS PRDCR_CD , WC.WHOL_WGHT AS WHOL_WGHT , WC.EMPT_VHCL_WGHT AS EMPT_VHCL_WGHT , WC.RDCD_RT AS RDCD_RT , WC.RDCD_WGHT AS RDCD_WGHT , WC.WRHS_WGHT AS WRHS_WGHT , WC.RMRK AS RMRK , WD.GRD_CD AS GRD_CD , WD.PLT_KND AS PLT_KND , WD.PLT_WGHT AS PLT_WGHT , WD.BX_KND AS BX_KND , WD.BX_QNTT AS BX_QNTT , WD.BX_WGHT AS BX_WGHT , WP.WRHS_WGHT AS INVNTR_WGHT , INV.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , INV.ITEM_CD AS ITEM_CD , INV.VRTY_CD AS VRTY_CD , INV.GDS_SE_CD AS GDS_SE_CD , INV.WRHS_SE_CD AS WRHS_SE_CD , INV.TRSPRT_SE_CD AS TRSPRT_SE_CD , INV.PRDCTN_YR AS PRDCTN_YR FROM TB_WGH_PRFMNC_COM WC , TB_WGH_PRFMNC_DTL WD , TB_RAW_MTR_WRHS_PRFMNC WP , TB_RAW_MTR_INVNTR INV WHERE WC.APC_CD = WD.APC_CD AND WC.WGHNO = WD.WGHNO AND WC.APC_CD = WP.APC_CD AND WC.WGHNO = WP.WGHNO AND WD.WRHSNO = WP.WRHSNO AND WP.APC_CD = INV.APC_CD AND WP.WRHSNO = INV.WRHSNO AND WC.DEL_YN = 'N' AND WD.DEL_YN = 'N' AND WP.DEL_YN = 'N' AND INV.DEL_YN = 'N'
AND WC.APC_CD = #{apcCd}
AND WC.WGHNO = #{wghno}
AND WC.WGH_YMD = #{wghYmd}
AND WC.WGH_YMD >= #{wghYmdFrom}
AND WC.WGH_YMD <= #{wghYmdTo}
AND WC.PRDCR_CD = #{prdcrCd}
AND INV.ITEM_CD = #{itemCd}
AND INV.VRTY_CD = #{vrtyCd}
AND INV.WRHS_SE_CD = #{wrhsSeCd}
AND INV.GDS_SE_CD = #{gdsSeCd}
AND INV.TRSPRT_SE_CD = #{trsprtSeCd}
AND WC.VHCLNO = #{vhclno}
ORDER BY APC_CD, WGH_YMD, WGHNO, WGH_SN ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.am.wgh.mapper.WghPrfmncMapper.selectWghPrfmncComList */ WC.APC_CD AS APC_CD , WC.WGHNO AS WGHNO , WC.WGH_YMD AS WGH_YMD , WC.VHCLNO AS VHCLNO , WC.WHOL_WGHT AS WHOL_WGHT , WC.EMPT_VHCL_WGHT AS EMPT_VHCL_WGHT , WC.RDCD_RT AS RDCD_RT , WC.RDCD_WGHT AS RDCD_WGHT , WC.WRHS_WGHT AS WRHS_WGHT , WC.RMRK AS RMRK , WC.DEL_YN AS DEL_YN , (SELECT X.APC_NM FROM TB_APC_INFO X WHERE X.APC_CD = WC.APC_CD) AS APC_NM FROM TB_WGH_PRFMNC_COM WC WHERE 1=1
AND WC.APC_CD = #{apcCd}
AND WC.WGHNO = #{wghno}
AND WC.DEL_YN = #{delYn}
AND WC.WGH_YMD = #{wghYmd}
AND WC.WGH_YMD >= #{wghYmdFrom}
AND WC.WGH_YMD <= #{wghYmdTo}
ORDER BY APC_CD, WGH_YMD, WGHNO
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

