mapper.select:
SELECT /* com.at.apcss.am.wgh.mapper.WghPrfmncMapper.selectWghPrfmncDtlList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_GRD_NM_RAWMTR(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD) FROM DUAL) AS GRD_NM , (SELECT FN_GET_STD_GRD_RAWMTR(pt0.APC_CD, pt0.WRHSNO) FROM DUAL) AS STD_GRD , (SELECT FN_GET_STD_GRD_RAWMTR(pt0.APC_CD, pt0.WRHSNO, 'CD') FROM DUAL) AS STD_GRD_CD , (SELECT FN_GET_PB_CNVRS_UWGHT(pt0.APC_CD, 'P', pt0.PLT_KND) FROM DUAL) AS PLT_UNIT_WGHT , (SELECT FN_GET_PB_CNVRS_UWGHT(pt0.APC_CD, 'B', pt0.BX_KND) FROM DUAL) AS BX_UNIT_WGHT
FROM ( SELECT WD.APC_CD AS APC_CD , WD.WGHNO AS WGHNO , WD.WGH_SN AS WGH_SN , WD.GRD_CD AS GRD_CD , WD.PLT_KND AS PLT_KND , WD.PLT_QNTT AS PLT_QNTT , WD.PLT_WGHT AS PLT_WGHT , WD.BX_KND AS BX_KND , WD.BX_QNTT AS BX_QNTT , WD.BX_WGHT AS BX_WGHT , WD.WRHSNO AS WRHSNO
, INV.ITEM_CD AS ITEM_CD , INV.VRTY_CD AS VRTY_CD , INV.GDS_SE_CD AS GDS_SE_CD , INV.WRHS_SE_CD AS WRHS_SE_CD , INV.TRSPRT_SE_CD AS TRSPRT_SE_CD
FROM TB_WGH_PRFMNC_DTL WD
JOIN TB_RAW_MTR_INVNTR INV ON INV.APC_CD = WD.APC_CD AND INV.WRHSNO = WD.WRHSNO AND INV.DEL_YN = 'N'
WHERE WD.DEL_YN = 'N'
AND WD.DEL_YN = #{delYn}
AND WD.APC_CD = #{apcCd}
AND WD.WGHNO = #{wghno}
AND WD.WGH_SN = #{wghSn}
AND WD.WRHSNO = #{wrhsno}
ORDER BY APC_CD, WGHNO, WGH_SN ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

