mapper.select:
SELECT
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM FROM ( SELECT /* com.at.apcss.am.wrhs.mapper.GdsWrhsMapper.selectGdsWrhsList */ GI.APC_CD AS APC_CD , GI.PCKGNO AS PCKGNO , GI.PCKG_SN AS PCKG_SN , GI.PCKG_YMD AS PCKG_YMD , GI.GDS_SE_CD AS GDS_SE_CD , GI.PRCHSPT_NM AS PRCHSPT_NM , GI.ITEM_CD AS ITEM_CD , GI.VRTY_CD AS VRTY_CD , GI.SPCFCT_CD AS SPCFCT_CD , GI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , GI.PCKG_QNTT AS PCKG_QNTT , GI.PCKG_WGHT AS PCKG_WGHT , GI.RMRK AS RMRK FROM TB_GDS_INVNTR GI WHERE GI.DEL_YN = 'N'
AND GI.APC_CD = #{apcCd}
AND GI.PCKG_YMD = #{pckgYmd}
AND GI.ITEM_CD = #{itemCd}
AND GI.VRTY_CD = #{vrtyCd}
ORDER BY PCKGNO, PCKG_SN ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

    	INSERT /* com.at.apcss.am.wrhs.mapper.GdsWrhsMapper.insertGdsInvntr */
    		INTO TB_GDS_INVNTR
			(
				  APC_CD
				, PCKGNO
				, PCKG_SN
				, PCKG_YMD
				, FCLT_CD
				, RPRS_PRDCR_CD
				, ITEM_CD
				, VRTY_CD
				, SPCFCT_CD
				, GDS_GRD
				, PCKG_SE_CD
				, WAREHOUSE_SE_CD
				, GDS_SE_CD
				, PRDCTN_YR
				, SPMT_PCKG_UNIT_CD
				, PLOR_CD
				, GDS_CD
				, PCKG_QNTT
				, PCKG_WGHT
				, SPMT_QNTT
				, SPMT_WGHT
				, INVNTR_QNTT
				, INVNTR_WGHT
				, PRCHSPT_NM
				, WRHS_SE_CD
				, TRSPRT_SE_CD
				, RMRK
				, DEL_YN
	    		, SYS_FRST_INPT_DT
	    		, SYS_FRST_INPT_USER_ID
	    		, SYS_FRST_INPT_PRGRM_ID
	    		, SYS_LAST_CHG_DT
	    		, SYS_LAST_CHG_USER_ID
	    		, SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
				  #{apcCd}
				, #{pckgno}
				, #{pckgSn}
				, #{pckgYmd}
				, #{fcltCd}
				, #{rprsPrdcrCd}
				, #{itemCd}
				, #{vrtyCd}
				, #{spcfctCd}
				, #{gdsGrd}
				, #{pckgSeCd}
				, #{warehouseSeCd}
				, #{gdsSeCd}
				, #{prdctnYr}
				, #{spmtPckgUnitCd}
				, #{plorCd}
				, #{gdsCd}
				, #{pckgQntt}
				, #{pckgWght}
				, #{spmtQntt}
				, #{spmtWght}
				, #{invntrQntt}
				, #{invntrWght}
				, #{prchsptNm}
				, #{wrhsSeCd}
				, #{trsprtSeCd}
				, #{rmrk}
				, #{delYn}
	    		, SYSDATE
	    		, #{sysFrstInptUserId}
	    		, #{sysFrstInptPrgrmId}
	    		, SYSDATE
	    		, #{sysLastChgUserId}
	    		, #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE /* com.at.apcss.am.wrhs.mapper.GdsWrhsMapper.deleteGdsInvntr */
			TB_GDS_INVNTR
		   SET
		   	  SYS_LAST_CHG_DT 		= SYSDATE
    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID = #{sysLastChgPrgrmId}
			, DEL_YN				= #{delYn}
		 WHERE APC_CD 				= #{apcCd}
		   AND PCKGNO 				= #{pckgno}
		   AND PCKG_SN 				= #{pckgSn}
