mapper.select:
SELECT /* com.at.apcss.am.wrhs.mapper.RawMtrWrhsMapper.selectRawMtrWrhsPrfmncList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_APC_NM(pt0.APC_CD) FROM DUAL) AS APC_NM , (SELECT FN_GET_PRDCR_NM(pt0.PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_GRD_NM_RAWMTR(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD) FROM DUAL) AS GRD_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('BX_KND', pt0.BX_KND, pt0.APC_CD) FROM DUAL) AS BX_KND_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WRHS_SE_CD', pt0.WRHS_SE_CD) FROM DUAL) AS WRHS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('TRSPRT_SE_CD', pt0.TRSPRT_SE_CD) FROM DUAL) AS TRSPRT_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('PRCS_TYPE', pt0.PRCS_TYPE) FROM DUAL) AS PRCS_TYPE_NM FROM ( SELECT WP.APC_CD AS APC_CD , WP.WRHSNO AS WRHSNO , WP.WGHNO AS WGHNO , WP.PLTNO AS PLTNO , WP.WRHS_YMD AS WRHS_YMD , WP.PRDCR_CD AS PRDCR_CD , INV.ITEM_CD AS ITEM_CD , INV.VRTY_CD AS VRTY_CD , INV.GDS_SE_CD AS GDS_SE_CD , INV.WRHS_SE_CD AS WRHS_SE_CD , INV.TRSPRT_SE_CD AS TRSPRT_SE_CD , INV.GRD_CD AS GRD_CD , WP.VHCLNO AS VHCLNO , WP.BX_QNTT AS BX_QNTT , WP.WRHS_WGHT AS WRHS_WGHT , INV.BX_KND AS BX_KND , INV.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , WP.RMRK AS RMRK , TRS.TRSPRT_CST AS TRSPRT_CST , INV.PRCS_TYPE AS PRCS_TYPE , INV.PRCSNO AS PRCSNO FROM TB_RAW_MTR_WRHS_PRFMNC WP , TB_RAW_MTR_INVNTR INV , TB_RAW_MTR_TRSPRT_CST TRS WHERE WP.APC_CD = INV.APC_CD AND WP.WRHSNO = INV.WRHSNO AND WP.WRHS_YMD = TRS.TRSPRT_YMD(+) AND WP.VHCLNO = TRS.VHCLNO(+) AND WP.DEL_YN = 'N' AND INV.DEL_YN = 'N'
AND WP.APC_CD = #{apcCd}
AND WP.WRHSNO = #{wrhsno}
AND WP.WGHNO = #{wghno}
AND WP.WRHS_YMD >= #{wrhsYmdFrom}
AND WP.WRHS_YMD <= #{wrhsYmdTo}
AND WP.PRDCR_CD = #{prdcrCd}
AND INV.ITEM_CD = #{itemCd}
AND INV.VRTY_CD IN (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{vrtyCd})))
AND INV.GDS_SE_CD IN (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{gdsSeCd})))
AND INV.WRHS_SE_CD IN (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{wrhsSeCd})))
AND INV.TRSPRT_SE_CD IN (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{trsprtSeCd})))
AND INV.WAREHOUSE_SE_CD = #{warehouseSeCd}
AND WP.VHCLNO LIKE '%' || #{vhclno} || '%'
ORDER BY APC_CD, WRHS_YMD, WRHSNO ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY
