SELECT
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT X.APC_NM FROM TB_APC_INFO X WHERE X.APC_CD = pt0.APC_CD) AS APC_NM , (SELECT FN_GET_PRDCR_NM(pt0.PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_GRD_NM_RAWMTR(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD) FROM DUAL) AS GRD_NM , (SELECT FN_GET_STD_GRD_RAWMTR(pt0.APC_CD, pt0.WRHSNO) FROM DUAL) AS STD_GRD , (SELECT FN_GET_STD_GRD_RAWMTR(pt0.APC_CD, pt0.WRHSNO, 'CD') FROM DUAL) AS STD_GRD_CD , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT X.PLT_BX_NM FROM TB_PLT_BX_INFO X WHERE X.APC_CD = pt0.APC_CD AND X.PLT_BX_SE_CD = 'B' AND X.PLT_BX_CD = pt0.BX_KND) AS BX_KND_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WRHS_SE_CD', pt0.WRHS_SE_CD) FROM DUAL) AS WRHS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('TRSPRT_SE_CD', pt0.TRSPRT_SE_CD) FROM DUAL) AS TRSPRT_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('PRCS_TYPE', pt0.PRCS_TYPE) FROM DUAL) AS PRCS_TYPE_NM FROM (
SELECT /* com.at.apcss.am.wrhs.mapper.RawMtrWrhsMapper.selectRawMtrWrhsList */ WP.APC_CD AS APC_CD , WP.WRHSNO AS WRHSNO , WP.WGHNO AS WGHNO , WP.PLTNO AS PLTNO , WP.WRHS_YMD AS WRHS_YMD , WP.PRDCR_CD AS PRDCR_CD , INV.ITEM_CD AS ITEM_CD , INV.VRTY_CD AS VRTY_CD , INV.GDS_SE_CD AS GDS_SE_CD , INV.WRHS_SE_CD AS WRHS_SE_CD , INV.TRSPRT_SE_CD AS TRSPRT_SE_CD , INV.GRD_CD AS GRD_CD , WP.VHCLNO AS VHCLNO , WP.BX_QNTT AS BX_QNTT , WP.WRHS_WGHT AS WRHS_WGHT , INV.BX_KND AS BX_KND , INV.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , INV.PRDCTN_YR AS PRDCTN_YR , WP.RMRK AS RMRK , INV.PRCS_TYPE AS PRCS_TYPE
FROM TB_RAW_MTR_WRHS_PRFMNC WP , TB_RAW_MTR_INVNTR INV
WHERE WP.APC_CD = INV.APC_CD AND WP.WRHSNO = INV.WRHSNO AND WP.DEL_YN = 'N' AND INV.DEL_YN = 'N'
AND WP.APC_CD = #{apcCd}
AND WP.WRHSNO = #{wrhsno}
AND WP.PLTNO = #{pltno}
AND WP.WGHNO = #{wghno}
AND WP.WRHS_YMD = #{wrhsYmd}
AND WP.WRHS_YMD >= #{wrhsYmdFrom}
AND WP.WRHS_YMD <= #{wrhsYmdTo}
AND WP.PRDCR_CD = #{prdcrCd}
AND INV.ITEM_CD = #{itemCd}
AND INV.VRTY_CD = #{vrtyCd}
AND INV.GDS_SE_CD = #{gdsSeCd}
AND INV.WRHS_SE_CD = #{wrhsSeCd}
AND INV.TRSPRT_SE_CD = #{trsprtSeCd}
AND INV.GRD_CD = #{grdCd}
INV INV.BX_KND = #{bxKnd}
AND INV.WAREHOUSE_SE_CD = #{warehouseSeCd}
AND WP.VHCLNO = #{vhclno}
ORDER BY APC_CD, WRHS_YMD, WRHSNO desc ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

SELECT WRHS_YMD , PRDCR_NM , PRDCR_CD , SUM(R1) AS R1 , SUM(R2) AS R2 , SUM(R3) AS R3 , SUM(R4) AS R4 , SUM(R5) AS R5 , SUM(RT) AS RT , SUM(Y1) AS Y1 , SUM(Y2) AS Y2 , SUM(Y3) AS Y3 , SUM(Y4) AS Y4 , SUM(Y5) AS Y5 , SUM(YT) AS YT , SUM(O1) AS O1 , SUM(O2) AS O2 , SUM(O3) AS O3 , SUM(O4) AS O4 , SUM(O5) AS O5 , SUM(OT) AS OT , SUM(TOT) AS TOT , MAX(ITEM_NM) AS ITEM_NM , MAX(VRTY_NM) AS VRTY_NM FROM (SELECT WRHS_YMD , PRDCR_NM , PRDCR_CD , CASE WHEN CLR = '1' AND RNK = 1 THEN BX_QNTT END AS R1 , CASE WHEN CLR = '1' AND RNK = 2 THEN BX_QNTT END AS R2 , CASE WHEN CLR = '1' AND RNK = 3 THEN BX_QNTT END AS R3 , CASE WHEN CLR = '1' AND RNK = 4 THEN BX_QNTT END AS R4 , CASE WHEN CLR = '1' AND RNK = 5 THEN BX_QNTT END AS R5 , CASE WHEN CLR = '1' THEN BX_QNTT END AS RT , CASE WHEN CLR = '2' AND RNK = 1 THEN BX_QNTT END AS Y1 , CASE WHEN CLR = '2' AND RNK = 2 THEN BX_QNTT END AS Y2 , CASE WHEN CLR = '2' AND RNK = 3 THEN BX_QNTT END AS Y3 , CASE WHEN CLR = '2' AND RNK = 4 THEN BX_QNTT END AS Y4 , CASE WHEN CLR = '2' AND RNK = 5 THEN BX_QNTT END AS Y5 , CASE WHEN CLR = '2' THEN BX_QNTT END AS YT , CASE WHEN CLR = '3' AND RNK = 1 THEN BX_QNTT END AS O1 , CASE WHEN CLR = '3' AND RNK = 2 THEN BX_QNTT END AS O2 , CASE WHEN CLR = '3' AND RNK = 3 THEN BX_QNTT END AS O3 , CASE WHEN CLR = '3' AND RNK = 4 THEN BX_QNTT END AS O4 , CASE WHEN CLR = '3' AND RNK = 5 THEN BX_QNTT END AS O5 , CASE WHEN CLR = '3' THEN BX_QNTT END AS OT , BX_QNTT AS TOT , ITEM_NM , VRTY_NM FROM (SELECT A.WRHS_YMD AS WRHS_YMD , B.ITEM_CD , FN_GET_PRDCR_NM (A.PRDCR_CD, A.APC_CD) AS PRDCR_NM , CASE WHEN B.VRTY_CD = '0300' THEN '1' WHEN B.VRTY_CD = '0200' THEN '2' WHEN B.VRTY_CD = '0400' THEN '3' END AS CLR , RANK() OVER (PARTITION BY A.PRDCR_CD,B.VRTY_CD,A.WRHS_YMD ORDER BY A.WRHSNO,B.VRTY_CD) AS RNK , A.BX_QNTT , A.WRHS_WGHT , A.PRDCR_CD AS PRDCR_CD , (SELECT FN_GET_ITEM_NM(B.ITEM_CD, B.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(B.ITEM_CD, B.VRTY_CD, B.APC_CD) FROM DUAL) AS VRTY_NM FROM USRAM.TB_RAW_MTR_WRHS_PRFMNC A , USRAM.TB_RAW_MTR_INVNTR B WHERE A.APC_CD = B.APC_CD AND A.WRHSNO = B.WRHSNO AND A.DEL_YN = 'N' AND B.DEL_YN = 'N' AND B.INVNTR_STTS_CD IS NULL
AND A.APC_CD = #{apcCd}
AND B.ITEM_CD = #{itemCd}
AND A.WRHS_YMD >= #{wrhsYmdFrom}
AND A.WRHS_YMD <= #{wrhsYmdTo}
) )GROUP BY WRHS_YMD,PRDCR_CD,PRDCR_NM ORDER BY WRHS_YMD,PRDCR_CD,PRDCR_NM

SELECT WRHS_YMD , PRDCR_NM , PRDCR_CD , SUM(BX_QNTT1) AS BX_QNTT1 , SUM(BX_QNTT2) AS BX_QNTT2 , SUM(BX_QNTT3) AS BX_QNTT3 , SUM(BX_QNTT4) AS BX_QNTT4 , SUM(BX_QNTT5) AS BX_QNTT5 , SUM(WRHS_WGHT1) AS WRHS_WGHT1 , SUM(WRHS_WGHT2) AS WRHS_WGHT2 , SUM(WRHS_WGHT3) AS WRHS_WGHT3 , SUM(WRHS_WGHT4) AS WRHS_WGHT4 , SUM(WRHS_WGHT5) AS WRHS_WGHT5 , SUM(QNTT_TOT) AS QNTT_TOT , SUM(WRHS_TOT) AS WRHS_TOT , MAX(ITEM_NM) AS ITEM_NM , MAX(VRTY_NM) AS VRTY_NM FROM (SELECT WRHS_YMD , PRDCR_NM , PRDCR_CD , CASE WHEN RNK = 1 THEN BX_QNTT END AS BX_QNTT1 , CASE WHEN RNK = 2 THEN BX_QNTT END AS BX_QNTT2 , CASE WHEN RNK = 3 THEN BX_QNTT END AS BX_QNTT3 , CASE WHEN RNK = 4 THEN BX_QNTT END AS BX_QNTT4 , CASE WHEN RNK = 5 THEN BX_QNTT END AS BX_QNTT5 , CASE WHEN RNK = 1 THEN WRHS_WGHT END AS WRHS_WGHT1 , CASE WHEN RNK = 2 THEN WRHS_WGHT END AS WRHS_WGHT2 , CASE WHEN RNK = 3 THEN WRHS_WGHT END AS WRHS_WGHT3 , CASE WHEN RNK = 4 THEN WRHS_WGHT END AS WRHS_WGHT4 , CASE WHEN RNK = 5 THEN WRHS_WGHT END AS WRHS_WGHT5 , BX_QNTT AS QNTT_TOT , WRHS_WGHT AS WRHS_TOT , ITEM_NM , VRTY_NM
FROM (SELECT A.WRHS_YMD AS WRHS_YMD , B.ITEM_CD , FN_GET_PRDCR_NM (A.PRDCR_CD, A.APC_CD) AS PRDCR_NM , RANK() OVER (PARTITION BY A.PRDCR_CD,B.VRTY_CD,A.WRHS_YMD ORDER BY A.WRHSNO,B.VRTY_CD) AS RNK , A.BX_QNTT , A.WRHS_WGHT , A.PRDCR_CD AS PRDCR_CD
, B.VRTY_CD AS VRTY_CD , (SELECT FN_GET_ITEM_NM(B.ITEM_CD, B.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(B.ITEM_CD, B.VRTY_CD, B.APC_CD) FROM DUAL) AS VRTY_NM FROM USRAM.TB_RAW_MTR_WRHS_PRFMNC A , USRAM.TB_RAW_MTR_INVNTR B WHERE A.APC_CD = B.APC_CD AND A.WRHSNO = B.WRHSNO AND A.DEL_YN = 'N' AND B.DEL_YN = 'N' AND B.INVNTR_STTS_CD IS NULL
AND A.APC_CD = #{apcCd}
AND B.ITEM_CD = #{itemCd}
AND B.VRTY_CD = #{vrtyCd}
AND A.WRHS_YMD >= #{wrhsYmdFrom}
AND A.WRHS_YMD <= #{wrhsYmdTo}
) )GROUP BY WRHS_YMD,PRDCR_CD,PRDCR_NM ORDER BY WRHS_YMD,PRDCR_CD,PRDCR_NM,VRTY_NM

