			, QNTT_CYCL_1 = #{qnttCycl1}
			, QNTT_CYCL_2 = #{qnttCycl2}
			, QNTT_CYCL_3 = #{qnttCycl3}
			, QNTT_CYCL_4 = #{qnttCycl4}
			, QNTT_CYCL_5 = #{qnttCycl5}
			, WGHT_CYCL_1 = #{wghtCycl1}
			, WGHT_CYCL_2 = #{wghtCycl2}
			, WGHT_CYCL_3 = #{wghtCycl3}
			, WGHT_CYCL_4 = #{wghtCycl4}
			, WGHT_CYCL_5 = #{wghtCycl5}
			, RMRK = #{rmrk}

		 WHERE APC_CD 	= #{apcCd}
		   AND WRHS_BGNG_YMD	= #{wrhsBgngYmd}
		   AND WRHS_END_YMD		= #{wrhsEndYmd}
		   AND PRDCR_CD = #{prdcrCd}
		   AND ITEM_CD 	= #{itemCd}
		   AND VRTY_CD 	= #{vrtyCd}

mapper.delete:

DELETE /* com.at.apcss.am.wrhs.mapper.RawMtrWrhsMapper.deleteRawMtrWrhsSmmry */
  FROM TB_RAW_MTR_WRHS_SMMRY
