SELECT /* com.at.apcss.am.whrs.mapper.RawMtrWrhsMapper.selectRawMtrWrhsList */ WP.APC_CD AS APC_CD , WP.WRHSNO AS WRHSNO , WP.WGHNO AS WGHNO , WP.PLTNO AS PLTNO , WP.WRHS_YMD AS WRHS_YMD , WP.PRDCR_CD AS PRDCR_CD , INV.ITEM_CD AS ITEM_CD , INV.VRTY_CD AS VRTY_CD , INV.GDS_SE_CD AS GDS_SE_CD , INV.WRHS_SE_CD AS WRHS_SE_CD , INV.TRSPRT_SE_CD AS TRSPRT_SE_CD , INV.GRD_CD AS GRD_CD , WP.VHCLNO AS VHCLNO , WP.BX_QNTT AS BX_QNTT , WP.WRHS_WGHT AS WRHS_WGHT , INV.BX_KND AS BX_KND , INV.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , WP.RMRK AS RMRK FROM TB_RAW_MTR_WRHS_PRFMNC WP , TB_RAW_MTR_INVNTR INV
WHERE WP.APC_CD = INV.APC_CD AND WP.WRHSNO = INV.WRHSNO AND WP.DEL_YN = 'N' AND INV.DEL_YN = 'N'
AND WP.APC_CD = #{apcCd}
AND WP.WRHSNO = #{wrhsno}
AND WP.PLTNO = #{pltno}
AND WP.WGHNO = #{wghno}
AND WP.WRHS_YMD = #{wrhsYmd}
AND WP.WRHS_YMD >= #{wrhsYmdFrom}
AND WP.WRHS_YMD <= #{wrhsYmdTo}
AND WP.PRDCR_CD = #{prdcrCd}
AND INV.ITEM_CD = #{itemCd}
AND INV.VRTY_CD = #{vrtyCd}
AND INV.GDS_SE_CD = #{gdsSeCd}
AND INV.WRHS_SE_CD = #{wrhsSeCd}
AND INV.TRSPRT_SE_CD = #{trsprtSeCd}
AND INV.GRD_CD = #{grdCd}
INV INV.BX_KND = #{bxKnd}
AND INV.WAREHOUSE_SE_CD = #{warehouseSeCd}
AND WP.VHCLNO = #{vhclno}
ORDER BY APC_CD, WRHS_YMD, WRHSNO
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY
