mapper.select:
SELECT /* com.at.apcss.co.log.mapper.ComLogMapper.selectCntnHstryList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_COM_CD_VL_NM('PRSL_TYPE', pt0.PRSL_TYPE) FROM DUAL) AS PRSL_TYPE_NM FROM ( SELECT /* com.at.apcss.am.ordr.mapper.OrdrMapper.selectCntnHstryList */ CMH.MENU_ID AS MENU_ID , CMH.MENU_NM AS MENU_NM , CMH.USER_ID AS USER_ID , CASE WHEN CMH.USER_NM IS NULL THEN (SELECT USER_NM FROM TB_COM_USER X WHERE X.USER_ID = CMH.USER_ID) ELSE CMH.USER_NM END AS USER_NM , CMH.USER_TYPE AS USER_TYPE , TO_CHAR(CMH.PRSL_DT, 'YYYY-MM-DD HH24:MI') AS PRSL_DT , (SELECT TO_CHAR(MIN(X.PRSL_DT), 'YYYY-MM-DD HH24:MI') FROM TB_COM_MENU_LOG_HSTRY X WHERE X.USER_ID = CMH.USER_ID GROUP BY USER_ID) AS FRST_PRSL_DT , CMH.LGN_SCS_YN AS LGN_SCS_YN , CMH.PRSL_TYPE AS PRSL_TYPE , CMH.USER_IP AS USER_IP , CMH.APC_CD AS APC_CD , CMH.HSTRY_SN AS HSTRY_SN , (SELECT FN_GET_APC_NM(CMH.APC_CD) FROM DUAL) AS APC_NM FROM TB_COM_MENU_LOG_HSTRY CMH WHERE CMH.PRSL_TYPE IN ('L1', 'L2', 'L3')
AND TO_CHAR(CMH.PRSL_DT, 'YYYYMMDD') >= #{logYmdFrom}
AND TO_CHAR(CMH.PRSL_DT, 'YYYYMMDD') <= #{logYmdTo}
AND CMH.USER_NM LIKE '%' || #{userNm} || '%'
) pt0 ORDER BY pt0.HSTRY_SN DESC
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

SELECT /* com.at.apcss.co.log.mapper.ComLogMapper.selectMenuHstryList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* FROM ( SELECT CMH.MENU_ID AS MENU_ID , CMH.MENU_NM AS MENU_NM , CMH.USER_ID AS USER_ID , CMH.USER_NM AS USER_NM , CMH.USER_TYPE AS USER_TYPE , TO_CHAR(CMH.PRSL_DT, 'YYYY-MM-DD HH24:MI') AS PRSL_DT , CMH.LGN_SCS_YN AS LGN_SCS_YN , CMH.PRSL_TYPE AS PRSL_TYPE , CMH.USER_IP AS USER_IP , CMH.APC_CD AS APC_CD , CMH.HSTRY_SN AS HSTRY_SN , CMH.FLFMT_TASK_SE_CD AS FLFMT_TASK_SE_CD , (SELECT FN_GET_COM_CD_VL_NM('FLFMT_TASK_SE_CD', CMH.FLFMT_TASK_SE_CD) FROM DUAL ) AS FLFMT_TASK_SE_NM , (SELECT FN_GET_APC_NM(CMH.APC_CD) FROM DUAL) AS APC_NM FROM TB_COM_MENU_LOG_HSTRY CMH WHERE PRSL_TYPE = 'M1' AND (FLFMT_TASK_SE_CD IS NULL OR FLFMT_TASK_SE_CD = '01')
AND TO_CHAR(CMH.PRSL_DT, 'YYYYMMDD') >= #{logYmdFrom}
AND TO_CHAR(CMH.PRSL_DT, 'YYYYMMDD') <= #{logYmdTo}
AND CMH.MENU_NM LIKE '%' || #{menuNm} || '%'
) pt0 ORDER BY pt0.HSTRY_SN DESC
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.am.ordr.mapper.OrdrMapper.selectTrsmHstryList */ CL.LOG_YMD AS LOG_YMD , CL.LOG_SN AS LOG_SN , CL.LOG_SE_CD AS LOG_SE_CD , CL.SEND_RCPTN_SE_CD AS SEND_RCPTN_SE_CD , CL.PRGRM_ID AS PRGRM_ID , CL.PRGRM_NM AS PRGRM_NM , CL.IP_ADDR AS IP_ADDR , TO_CHAR(CL.PRCS_DT, 'YYYY-MM-DD') AS PRCS_DT , CL.RMRK AS RMRK FROM TB_COM_LOG CL WHERE 1=1
AND CL.LOG_YMD >= #{logYmdFrom}
AND CL.LOG_YMD <= #{logYmdTo}
AND CL.SEND_RCPTN_SE_CD = #{sendRcptnSeCd}
AND CL.PRGRM_NM LIKE '%' || #{prgrmNm} || '%'
AND CL.LOG_SE_CD = #{logSeCd}
ORDER BY CL.LOG_YMD DESC
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

SELECT /* com.at.apcss.co.log.mapper.ComLogMapper.selectRawMtrHstryList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('INVNTR_CHG_RSN_CD', pt0.CHG_RSN_CD) FROM DUAL) AS CHG_RSN_NM
FROM ( SELECT RCH.APC_CD AS APC_CD , RCH.WRHSNO AS WRHSNO , RCH.CHG_SN AS CHG_SN , RCH.CHG_YMD AS CHG_YMD , RCH.CHG_BFR_QNTT AS CHG_BFR_QNTT , RCH.CHG_BFR_WGHT AS CHG_BFR_WGHT , RCH.CHG_AFTR_QNTT AS CHG_AFTR_QNTT , RCH.CHG_AFTR_WGHT AS CHG_AFTR_WGHT , RCH.CHG_RMRK AS CHG_RMRK , RCH.CHG_RSN_CD AS CHG_RSN_CD , RCH.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD FROM TB_RAW_MTR_CHG_HSTRY RCH WHERE 1=1
AND RCH.APC_CD = #{apcCd}
AND RCH.CHG_YMD >= #{chgYmdFrom}
AND RCH.CHG_YMD <= #{chgYmdTo}
ORDER BY RCH.CHG_YMD DESC ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

SELECT /* com.at.apcss.am.invntr.mapper.RawMtrInvntrMapper.selectRawMtrHstryList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('INVNTR_CHG_RSN_CD', pt0.CHG_RSN_CD) FROM DUAL) AS CHG_RSN_NM
FROM ( SELECT SCH.APC_CD AS APC_CD , SCH.SORTNO AS SORTNO , SCH.SORT_SN AS SORT_SN , SCH.CHG_SN AS CHG_SN , SCH.CHG_YMD AS CHG_YMD , SCH.CHG_BFR_QNTT AS CHG_BFR_QNTT , SCH.CHG_BFR_WGHT AS CHG_BFR_WGHT , SCH.CHG_AFTR_QNTT AS CHG_AFTR_QNTT , SCH.CHG_AFTR_WGHT AS CHG_AFTR_WGHT , SCH.CHG_RMRK AS CHG_RMRK , SCH.CHG_RSN_CD AS CHG_RSN_CD , SCH.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD FROM TB_SORT_INVNTR_CHG_HSTRY SCH WHERE 1=1
AND SCH.APC_CD = #{apcCd}
AND SCH.CHG_YMD >= #{chgYmdFrom}
AND SCH.CHG_YMD <= #{chgYmdTo}
ORDER BY SCH.CHG_YMD DESC ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

