mapper.select:
SELECT /* com.at.apcss.co.menu.mapper.ComMenuMapper.selectComMenuList */ CM.MENU_ID AS MENU_ID , CM.MENU_NM AS MENU_NM , CM.UP_MENU_ID AS UP_MENU_ID , CM.MENU_TYPE AS MENU_TYPE , CM.USER_TYPE AS USER_TYPE , CM.PAGE_URL AS PAGE_URL , CM.INDCT_SEQ AS INDCT_SEQ , CM.APC_CD AS APC_CD FROM TB_COM_MENU CM WHERE CM.DEL_YN = 'N'
AND CM.MENU_ID = #{menuId}
AND CM.MENU_NM LIKE '%' || #{menuNm} || '%'

SELECT /* com.at.apcss.co.menu.mapper.ComMenuMapper.selectMenuTreeList */ MN.LVL AS MENU_LVL , MN.SEQ AS MENU_SEQ , MN.SYS_ID AS SYS_ID , MN.UP_MENU_ID AS UP_MENU_ID , UM.MENU_NM AS UP_MENU_NM , MN.MENU_ID AS MENU_ID , MN.MENU_NM AS MENU_NM , MN.MENU_TYPE AS MENU_TYPE , MN.USER_TYPE AS USER_TYPE , MN.PAGE_URL AS PAGE_URL , MN.APC_CD AS APC_CD , MN.INDCT_SEQ AS INDCT_SEQ FROM ( SELECT LEVEL AS LVL , ROWNUM AS SEQ , SUBSTR(MN.MENU_ID, 1, 2) AS SYS_ID , MN.UP_MENU_ID AS UP_MENU_ID , MN.MENU_ID AS MENU_ID , MN.MENU_NM AS MENU_NM , MN.MENU_TYPE AS MENU_TYPE , MN.USER_TYPE AS USER_TYPE , MN.PAGE_URL AS PAGE_URL , MN.APC_CD AS APC_CD , MN.INDCT_SEQ AS INDCT_SEQ FROM TB_COM_MENU MN WHERE MN.DEL_YN = 'N'
AND SUBSTR(MN.MENU_ID, 1, 2) = #{sysId}
AND MN.APC_CD = #{apcCd}
START WITH MN.UP_MENU_ID IS NULL CONNECT BY PRIOR MN.MENU_ID = MN.UP_MENU_ID ORDER SIBLINGS BY MN.INDCT_SEQ, MN.MENU_ID ) MN , TB_COM_MENU UM WHERE MN.UP_MENU_ID = UM.MENU_ID(+) AND UM.DEL_YN(+) = 'N'

SELECT /* com.at.apcss.co.menu.mapper.ComMenuMapper.selectTopMenuList */ MN.LVL AS MENU_LVL , MN.SEQ AS MENU_SEQ , MN.SYS_ID AS SYS_ID , MN.UP_MENU_ID AS UP_MENU_ID , UM.MENU_NM AS UP_MENU_NM , MN.MENU_ID AS MENU_ID , MN.MENU_NM AS MENU_NM , MN.MENU_TYPE AS MENU_TYPE , MN.USER_TYPE AS USER_TYPE , MN.PAGE_URL AS PAGE_URL , MN.APC_CD AS APC_CD , MN.INDCT_SEQ AS INDCT_SEQ , MN.RPRS_MENU_YN AS RPRS_MENU_YN
FROM ( SELECT LEVEL AS LVL , ROWNUM AS SEQ , SUBSTR(MN.MENU_ID, 1, 2) AS SYS_ID , MN.UP_MENU_ID AS UP_MENU_ID , MN.MENU_ID AS MENU_ID , MN.MENU_NM AS MENU_NM , MN.MENU_TYPE AS MENU_TYPE , MN.USER_TYPE AS USER_TYPE , MN.PAGE_URL AS PAGE_URL , MN.APC_CD AS APC_CD , MN.INDCT_SEQ AS INDCT_SEQ , MN.RPRS_MENU_YN AS RPRS_MENU_YN FROM TB_COM_MENU MN WHERE MN.DEL_YN = 'N'
AND SUBSTR(MN.MENU_ID, 1, 2) = #{sysId}
AND MN.APC_CD = #{apcCd}
START WITH MN.UP_MENU_ID IS NULL CONNECT BY PRIOR MN.MENU_ID = MN.UP_MENU_ID ORDER SIBLINGS BY MN.INDCT_SEQ, MN.MENU_ID ) MN , TB_COM_MENU UM WHERE MN.UP_MENU_ID = UM.MENU_ID(+) AND MN.LVL <= 2 AND UM.DEL_YN(+) = 'N'

SELECT /* com.at.apcss.co.menu.mapper.ComMenuMapper.selectLeftMenuList */ MN.LVL AS MENU_LVL , MN.SEQ AS MENU_SEQ , MN.SYS_ID AS SYS_ID , MN.UP_MENU_ID AS UP_MENU_ID , UM.MENU_NM AS UP_MENU_NM , MN.MENU_ID AS MENU_ID , MN.MENU_NM AS MENU_NM , MN.MENU_TYPE AS MENU_TYPE , MN.USER_TYPE AS USER_TYPE , MN.PAGE_URL AS PAGE_URL , MN.APC_CD AS APC_CD , MN.INDCT_SEQ AS INDCT_SEQ , MN.RPRS_MENU_YN AS RPRS_MENU_YN
FROM ( SELECT LEVEL AS LVL , ROWNUM AS SEQ , SUBSTR(MN.MENU_ID, 1, 2) AS SYS_ID , MN.UP_MENU_ID AS UP_MENU_ID , MN.MENU_ID AS MENU_ID , MN.MENU_NM AS MENU_NM , MN.MENU_TYPE AS MENU_TYPE , MN.USER_TYPE AS USER_TYPE , MN.PAGE_URL AS PAGE_URL , MN.APC_CD AS APC_CD , MN.INDCT_SEQ AS INDCT_SEQ , MN.RPRS_MENU_YN AS RPRS_MENU_YN FROM TB_COM_MENU MN WHERE MN.DEL_YN = 'N'
AND SUBSTR(MN.MENU_ID, 1, 2) = #{sysId}
AND MN.APC_CD = #{apcCd}
START WITH MN.UP_MENU_ID = #{upMenuId} CONNECT BY PRIOR MN.MENU_ID = MN.UP_MENU_ID ORDER SIBLINGS BY MN.INDCT_SEQ, MN.MENU_ID ) MN , TB_COM_MENU UM WHERE MN.UP_MENU_ID = UM.MENU_ID(+) AND UM.DEL_YN(+) = 'N'
