mapper.select:
SELECT /* com.at.apcss.co.sys.mapper.LoginMapper.selectUser */ CU.USER_ID AS ID , CU.USER_NM AS NAME , CU.PSWD AS PASSWORD , CU.USER_ID AS USER_ID , CU.PSWD AS PSWD , CU.USER_TYPE AS USER_TYPE , CU.USER_STTS AS USER_STTS , CU.EML AS EMAIL , CU.TELNO AS TELNO , CU.APC_CD AS APC_CD , (SELECT FN_GET_APC_NM(CU.APC_CD) FROM DUAL) AS APC_NM , CU.CHRGD_CERT_YN AS CHRGD_CERT_YN , CU.CHRGD_CERT_TYPE AS CHRGD_CERT_TYPE , CU.LGN_FAIL_NMTM AS LGN_FAIL_NMTM , CU.END_LGN_DT AS END_LGN_DT , CU.END_LGN_IP AS END_LGN_IP , CU.DEL_YN AS DEL_YN , CU.LCK_YN AS LCK_YN
, CU.UNTY_OGNZ_ID AS UNTY_OGNZ_ID , CU.UNTY_OGNZ_TYPE AS UNTY_OGNZ_TYPE , CU.UNTY_AUTHRT_TYPE AS UNTY_AUTHRT_TYPE , CU.UNTY_AUTHRT_MNG_YN AS UNTY_AUTHRT_MNG_YN
FROM TB_COM_USER CU WHERE CU.USER_ID = #{id}

mapper.update:

	SELECT LGN_FAIL_NMTM FROM TB_COM_USER WHERE USER_ID=#{id}

UPDATE TB_COM_USER AS CU
	SET LGN_FAIL_NMTM =#{lgnFailNmtm}+1
	WHERE CU.USER_ID = #{id}

UPDATE TB_COM_USER
SET LCK_YN = 'Y'
WHERE USER_ID = #{id}

UPDATE TB_COM_USER
SET LGN_FAIL_NMTM = 0
WHERE USER_ID = #{id}
