mapper.select:
SELECT /* com.at.apcss.co.user.ComUserMapper.selectComUserList */ ROWNUM AS ROW_SEQ , CU.USER_ID AS USER_ID , CU.USER_NM AS USER_NM , CU.USER_TYPE AS USER_TYPE , CU.USER_STTS AS USER_STTS , CU.EML AS EML , CU.TELNO AS TELNO , CU.APC_CD AS APC_CD , CU.OGDP_NM AS OGDP_NM , CU.CHRGD_CERT_YN AS CHRGD_CERT_YN , CU.CHRGD_CERT_TYPE AS CHRGD_CERT_TYPE , CU.LCK_YN AS LCK_YN , CU.LGN_FAIL_NMTM AS LGN_FAIL_NMTM , CU.END_LGN_DT AS END_LGN_DT , CU.END_LGN_IP AS END_LGN_IP , CU.RMRK AS RMRK , CU.JBTTL_NM AS JBTTL_NM , CU.TKCG_TASK_NM AS TKCG_TASK_NM , CU.RMRK AS RMRK , CU.DEL_YN AS DEL_YN , CU.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , CU.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , CU.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , CU.SYS_LAST_CHNG_DT AS SYS_LAST_CHNG_DT , CU.SYS_LAST_CHNG_USER_ID AS SYS_LAST_CHNG_USER_ID , CU.SYS_LAST_CHNG_PRGRM_ID AS SYS_LAST_CHNG_PRGRM_ID
FROM TB_COM_USER CU WHERE CU.DEL_YN = 'N'
AND CU.USER_ID = #{userId}
AND CU.USER_NM LIKE '%' || #{userNm} || '%'
AND CU.USER_TYPE = #{userType}
AND CU.USER_STTS = #{userStts}
AND CU.APC_CD = #{apcCd}
AND CU.OGDP_NM LIKE '%' || #{ogdpNm} || '%'
ORDER BY USER_ID, USER_NM

mapper.update:

UPDATE TB_COM_USER
   SET USER_STTS 				= #{userStts}
     , SYS_LAST_CHG_DT 			= SYSDATE
     , SYS_LAST_CHG_USER_ID 	= #{sysLastChgUserId}
     , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
 WHERE USER_ID 					= #{userId}


    	UPDATE TB_COM_USER
    	   SET SYS_LAST_CHG_DT 			= SYSDATE
    	     , SYS_LAST_CHG_USER_ID 	= #{sysLastChgUserId}
    	     , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}


    		 , JBTTL_NM 				= #{jbttlNm}


    		 , TKCG_TASK_NM 			= #{tkcgTaskNm}


    		 , RMRK 					= #{rmrk}



	    	, DEL_YN 					= 'N'


	    	, DEL_YN 					= 'Y'



    	 WHERE USER_ID 					= #{userId}
