mapper.insert:

INSERT /* com.at.apcss.fm.bbs.mapper.BbsMngMapper.insertBbs */
  INTO TB_BBS
	(
		BBS_NO
	  , BBS_TTL
	  , BBS_SE_CD
	  , BBS_SBJT
	  , PRVT_PST_YN

	  , SYS_FRST_INPT_DT
	  , SYS_FRST_INPT_USER_ID
	  , SYS_FRST_INPT_PRGRM_ID
	  , SYS_LAST_CHG_DT
	  , SYS_LAST_CHG_USER_ID
	  , SYS_LAST_CHG_PRGRM_ID
	)
VALUES
	(
	    (SELECT NVL(MAX(BBS_NO),0)+1 FROM TB_BBS)
	  , #{bbsTtl}
	  , #{bbsSeCd}
	  , #{bbsSbjt}
	  , #{prvtPstYn}

	  , SYSDATE
	  , #{sysFrstInptUserId}
	  , #{sysFrstInptPrgrmId}
	  , SYSDATE
	  , #{sysLastChgUserId}
	  , #{sysLastChgPrgrmId}
	)


SELECT NVL(MAX(BBS_NO),0) FROM TB_BBS

mapper.update:

UPDATE /* com.at.apcss.fm.bbs.mapper.BbsMngMapper.updateBbs */
	TB_BBS
   SET
 	  BBS_TTL		= #{bbsTtl}
 	, BBS_SE_CD 	= #{bbsSeCd}
 	, BBS_SBJT 		= #{bbsSbjt}
 	, PRVT_PST_YN	= #{prvtPstYn}


	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
 WHERE 1=1
   AND BBS_NO	= #{bbsNo}


UPDATE /* com.at.apcss.fm.bbs.mapper.BbsMngMapper.updateBbsAns */
	TB_BBS
   SET
 	  ANS_STTS		= '2'
 	, ANS_USER_ID 	= #{userId}
 	, ANS_DT	 	= SYSDATE
 	, ANS_SBJT 		= #{bbsSbjt}


	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
 WHERE 1=1
   AND BBS_NO	= #{bbsNo}


UPDATE /* com.at.apcss.fm.bbs.mapper.BbsMngMapper.deleteBbs */
	TB_BBS
   SET
	  DEL_YN 				= 'Y'
	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
 WHERE 1=1
   AND BBS_NO = #{bbsNo}

