mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.clt.mapper.CltvtnEnggtAplyMngMapper.selectCltvtnEnggtAplyMngList */ APC_CD AS APC_CD , CLT.PRDCR_CD AS PRDCR_CD , CLT.FRMER_SN AS FRMER_SN , CLT.REQ_SN AS REQ_SN , CLT.FRMER_NM AS FRMER_NM , CLT.ITEM_CD AS ITEM_CD , CLT.VRTY_CD AS VRTY_CD , CLT.SPCFCT_CD AS SPCFCT_CD , CLT.CLTVTN_AREA AS CLTVTN_AREA , CLT.ENGGT_VLM AS ENGGT_VLM , CLT.EXPCT_HRVST_QNTT AS EXPCT_HRVST_QNTT , CLT.PLOR AS PLOR , CLT.CTRT_AMT AS CTRT_AMT , CLT.PRTPAY AS PRTPAY , CLT.SRPLS AS SRPLS , CLT.ENGGT_YMD AS ENGGT_YMD , CLT.ADDR AS ADDR , CLT.ZIP AS ZIP , CLT.TELNO AS TELNO , CLT.FXNO AS FXNO , CLT.BANK_CD AS BANK_CD , CLT.ACTNO AS ACTNO , CLT.DPSTR_NM AS DPSTR_NM , CLT.RMRK AS RMRK , CLT.DEL_YN AS DEL_YN , CLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , CLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , CLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , CLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , CLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , CLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_CLTVTN_ENGGT_APLY_MNG CLT WHERE 1=1 AND TRGT_YR = #{trgtYr} AND APC_CD = #{apcCd}
AND CM.MSG_KEY LIKE #{msgKey} || '%'
AND CM.MSG_CN LIKE '%' || #{msgCn} || '%'
AND CM.MSG_KND = #{msgKnd}
AND CM.DEL_YN = #{delYn}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

