mapper.select:
SELECT /* com.at.apcss.fm.clt.mapper.CltvtnEnggtAplyMngMapper.selectCltvtnEnggtAplyMngList */ APC_CD AS APC_CD , (SELECT X.APC_NM FROM TB_APC_INFO X WHERE X.APC_CD = CLT.APC_CD) AS APC_NM , CLT.PRDCR_CD AS PRDCR_CD , (SELECT X.PRDCR_NM FROM TB_PRDCR_INFO X WHERE X.PRDCR_CD = CLT.PRDCR_CD AND X.APC_CD = CLT.APC_CD) AS PRDCR_NM , CLT.FRMER_SN AS FRMER_SN , CLT.REQ_SN AS REQ_SN , CLT.FRMER_NM AS FRMER_NM , CLT.ITEM_CD AS ITEM_CD , (SELECT FN_GET_ITEM_NM(CLT.ITEM_CD, CLT.APC_CD) FROM DUAL) AS ITEM_NM , CLT.VRTY_CD AS VRTY_CD , (SELECT FN_GET_VRTY_NM(CLT.ITEM_CD, CLT.VRTY_CD, CLT.APC_CD) FROM DUAL) AS VRTY_NM , CLT.SPCFCT_CD AS SPCFCT_CD , CLT.CLTVTN_AREA AS CLTVTN_AREA , CLT.ENGGT_VLM AS ENGGT_VLM , CLT.EXPCT_HRVST_QNTT AS EXPCT_HRVST_QNTT , CLT.PLOR AS PLOR , CLT.CTRT_AMT AS CTRT_AMT , CLT.PRTPAY AS PRTPAY , CLT.SRPLS AS SRPLS , CLT.ENGGT_YMD AS ENGGT_YMD , CLT.ADDR AS ADDR , CLT.ZIP AS ZIP , CLT.TELNO AS TELNO , CLT.FXNO AS FXNO , CLT.BANK_CD AS BANK_CD , CLT.ACTNO AS ACTNO , CLT.DPSTR_NM AS DPSTR_NM , CLT.RMRK AS RMRK , CLT.DEL_YN AS DEL_YN , CLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , CLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , CLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , CLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , CLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , CLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_CLTVTN_ENGGT_APLY_MNG CLT WHERE 1=1 AND DEL_YN = 'N'
AND APC_CD = #{apcCd}
AND ENGGT_YMD BETWEEN TO_DATE(#{enggtSrtYmd}) AND TO_DATE(#{enggtEndYmd})
AND ITEM_CD = #{itemCd}
AND VRTY_CD = #{vrtyCd}
ORDER BY REQ_SN ASC

mapper.insert:

 	INSERT /* com.at.apcss.fm.clt.mapper.CltvtnEnggtAplyMngMapper.insertCltvtnEnggtAplyMng */
 	  INTO TB_CLTVTN_ENGGT_APLY_MNG CLT
 		(
 			APC_CD
 		  , PRDCR_CD
 		  , FRMER_SN
 		  , REQ_SN
 		  , FRMER_NM
 		  , ITEM_CD
 		  , VRTY_CD
 		  , SPCFCT_CD
 		  , CLTVTN_AREA
 		  , ENGGT_VLM
 		  , EXPCT_HRVST_QNTT
 		  , PLOR
 		  , CTRT_AMT
 		  , PRTPAY
 		  , SRPLS
 		  , ENGGT_YMD
 		  , ADDR
 		  , ZIP
 		  , TELNO
 		  , FXNO
 		  , BANK_CD
 		  , ACTNO
 		  , DPSTR_NM
 		  , RMRK
 		  , DEL_YN
 		  , SYS_FRST_INPT_DT
 		  , SYS_FRST_INPT_USER_ID
 		  , SYS_FRST_INPT_PRGRM_ID
 		  , SYS_LAST_CHG_DT
 		  , SYS_LAST_CHG_USER_ID
 		  , SYS_LAST_CHG_PRGRM_ID
 		)
		VALUES
			(
			    #{apcCd}
			  , #{prdcrCd}
			  , #{frmerSn}
			  , (SELECT NVL(MAX(REQ_SN), 0) + 1 FROM TB_CLTVTN_ENGGT_APLY_MNG)
			  , #{frmerNm}
			  , #{itemCd}
			  , #{vrtyCd}
			  , #{spcfctCd}
			  , #{cltvtnArea}
			  , #{enggtVlm}
			  , #{expctHrvstQntt}
			  , #{plor}
			  , #{ctrtAmt}
			  , #{prtpay}
			  , #{srpls}
			  , #{enggtYmd}
			  , #{addr}
			  , #{zip}
			  , #{telno}
			  , #{fXNO}
			  , #{bankCd}
			  , #{actno}
			  , #{dpstrNm}
			  , #{rmrk}
			  , 'N'
			  , SYSDATE
			  , #{sysFrstInptUserId}
			  , #{sysFrstInptPrgrmId}
			  , SYSDATE
			  , #{sysLastChgUserId}
			  , #{sysLastChgPrgrmId}
			)

mapper.update:

	    	UPDATE /* com.at.apcss.fm.clt.mapper.CltvtnEnggtAplyMngMapper.updateCltvtnEnggtAplyMng */
	    		TB_CLTVTN_ENGGT_APLY_MNG CLT
			   SET
    	         ENGGT_VLM          = #{enggtVlm}
    	        , EXPCT_HRVST_QNTT   = #{expctHrvstQntt}
    	        , ENGGT_YMD          = #{enggtYmd}
    	        , RMRK               = #{rmrk}


				, DEL_YN 	= #{delYn}


				, SYS_LAST_CHG_DT		= SYSDATE
	    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    		, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

			 WHERE 1=1
		     AND APC_CD = #{apcCd}
			 AND PRDCR_CD = #{prdcrCd}
			 AND FRMER_SN = #{frmerSn}
    	     AND REQ_SN = #{reqSn}

mapper.delete:

    	DELETE /* com.at.apcss.fm.clt.mapper.CltvtnEnggtAplyMngMapper.deleteCltvtnEnggtAplyMng */
    		TB_CLTVTN_ENGGT_APLY_MNG CLT
		 WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}
