mapper.select:
SELECT /* com.at.apcss.fm.farm.mapper.FarmLandInfoRegMapper.selectFarmLandInfoReg */ MNGMST_REGNO , FRLN_TYPE , CPRTN_FRLN_YN , FRLN_AREA , BRGRND_FCLT_SE_CD , FCLT_KND , FCLT_SN , FCLT_SHAP , FCLT_AREA , FRLN_ADDR , FRLN_MNO , FRLN_OWNR_NM , FRLN_SNO , FRLN_SN , FRMERNO , FCLT_INSTL_YR , LDGR_SE_CD , STDG_CD , FLW_AREA , ABL_FRLN_AREA , POE_FRLN_AREA , POE_LDCG , ITEM_CD , ITEM_NM , ACTL_FRLN_AREA , ACTL_LDCG , DEL_YN , SYS_FRST_INPT_DT , SYS_FRST_INPT_PRGRM_ID , SYS_FRST_INPT_USER_ID , SYS_LAST_CHG_DT , SYS_LAST_CHG_PRGRM_ID , SYS_LAST_CHG_USER_ID FROM USRAM.TB_AX_FARM_LAND_INFO WHERE 1=1
AND FRMERNO = #{frmerno}
AND MNGMST_REGNO = #{mngmstRegNo}
ORDER BY SYS_LAST_CHG_DT DESC

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.farm.mapper.FarmLandInfoRegMapper.selectFarmLandInfoRegList */ MNGMST_REGNO , FRLN_TYPE , CPRTN_FRLN_YN , FRLN_AREA , BRGRND_FCLT_SE_CD , FCLT_KND , FCLT_SN , FCLT_SHAP , FCLT_AREA , FRLN_ADDR , FRLN_MNO , FRLN_OWNR_NM , FRLN_SNO , FRLN_SN , FRMERNO , FCLT_INSTL_YR , LDGR_SE_CD , STDG_CD , FLW_AREA , ABL_FRLN_AREA , POE_FRLN_AREA , POE_LDCG , ITEM_CD , ITEM_NM , ACTL_FRLN_AREA , ACTL_LDCG , DEL_YN , SYS_FRST_INPT_DT , SYS_FRST_INPT_PRGRM_ID , SYS_FRST_INPT_USER_ID , SYS_LAST_CHG_DT , SYS_LAST_CHG_PRGRM_ID , SYS_LAST_CHG_USER_ID FROM USRAM.TB_AX_FARM_LAND_INFO WHERE 1=1
AND FRMERNO = #{frmerno}
AND MNGMST_REGNO = #{mngmstRegNo}
ORDER BY SYS_LAST_CHG_DT DESC
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

MERGE INTO USRAM.TB_AX_FARM_LAND_INFO AS tgt	/* com.at.apcss.fm.farm.mapper.FarmLandInfoRegMapper.insertMegerFarmLandInfoReg */
USING DUAL
	ON (
			tgt.FRMERNO			= #{frmerno}
			AND tgt.FRLN_SN		= #{frlnSn}
			AND tgt.FRLN_SNO	= #{frlnSno}
			AND tgt.FRLN_MNO	= #{frlnMno}
		)
WHEN MATCHED THEN
	UPDATE	/* com.at.apcss.fm.farm.mapper.FarmLandInfoRegMapper.insertMegerFarmLandInfoReg */
	   SET
		  tgt.MNGMST_REGNO				=#{mngmstRegNo}
		, tgt.FRLN_TYPE					=#{frlnType}
		, tgt.CPRTN_FRLN_YN				=#{cprtnFrlnyn}
		, tgt.FRLN_AREA					=#{frlnarea}
		, tgt.BRGRND_FCLT_SE_CD			=#{brgrndFcltSeCd}
		, tgt.FCLT_KND					=#{fcltKnd}
		, tgt.FCLT_SN					=#{fcltSn}
		, tgt.FCLT_SHAP					=#{fcltShap}
		, tgt.FCLT_AREA					=#{fcltArea}
		, tgt.FRLN_ADDR					=#{frlnAddr}
		, tgt.FRLN_OWNR_NM				=#{frlnOwnrNm}
		, tgt.FCLT_INSTL_YR				=#{fcltInstallYr}
		, tgt.LDGR_SE_CD				=#{ldgrSeCd}
		, tgt.STDG_CD					=#{stdgcd}
		, tgt.FLW_AREA					=#{flwArea}
		, tgt.ABL_FRLN_AREA				=#{ablFrlnarea}
		, tgt.POE_FRLN_AREA				=#{poeFrlnarea}
		, tgt.POE_LDCG					=#{poeLdcg}
		, tgt.ITEM_CD					=#{itemCd}
		, tgt.ITEM_NM					=#{itemNm}
		, tgt.ACTL_FRLN_AREA			=#{actlFrlnarea}
		, tgt.ACTL_LDCG					=#{actlLdcg}

		, tgt.SYS_LAST_CHG_DT			= SYSDATE
		, tgt.SYS_LAST_CHG_PRGRM_ID		=#{sysLastChgUserId}
		, tgt.SYS_LAST_CHG_USER_ID		=#{sysLastChgPrgrmId}
WHEN NOT MATCHED
THEN INSERT /* com.at.apcss.fm.farm.mapper.FarmLandInfoRegMapper.insertMegerFarmLandInfoReg */
	(
		MNGMST_REGNO
		, FRLN_TYPE
		, CPRTN_FRLN_YN
		, FRLN_AREA
		, BRGRND_FCLT_SE_CD
		, FCLT_KND
		, FCLT_SN
		, FCLT_SHAP
		, FCLT_AREA
		, FRLN_ADDR
		, FRLN_MNO
		, FRLN_OWNR_NM
		, FRLN_SNO
		, FRLN_SN
		, FRMERNO
		, FCLT_INSTL_YR
		, LDGR_SE_CD
		, STDG_CD
		, FLW_AREA
		, ABL_FRLN_AREA
		, POE_FRLN_AREA
		, POE_LDCG
		, ITEM_CD
		, ITEM_NM
		, ACTL_FRLN_AREA
		, ACTL_LDCG
		, SYS_FRST_INPT_DT
		, SYS_FRST_INPT_PRGRM_ID
		, SYS_FRST_INPT_USER_ID
		, SYS_LAST_CHG_DT
		, SYS_LAST_CHG_PRGRM_ID
		, SYS_LAST_CHG_USER_ID
)VALUES (
		#{mngmstRegNo}
		, #{frlnType}
		, #{cprtnFrlnyn}
		, #{frlnarea}
		, #{brgrndFcltSeCd}
		, #{fcltKnd}
		, #{fcltSn}
		, #{fcltShap}
		, #{fcltArea}
		, #{frlnAddr}
		, #{frlnMno}
		, #{frlnOwnrNm}
		, #{frlnSno}
		, #{frlnSn}
		, #{frmerno}
		, #{fcltInstallYr}
		, #{ldgrSeCd}
		, #{stdgcd}
		, #{flwArea}
		, #{ablFrlnarea}
		, #{poeFrlnarea}
		, #{poeLdcg}
		, #{itemCd}
		, #{itemNm}
		, #{actlFrlnarea}
		, #{actlLdcg}
		, SYSDATE
		, #{sysFrstInptUserId}
		, #{sysFrstInptPrgrmId}
		, SYSDATE
		, #{sysLastChgUserId}
		, #{sysLastChgPrgrmId}
);

mapper.delete:

DELETE FROM TB_AX_FARM_LAND_INFO /* com.at.apcss.fm.farm.mapper.FarmLandInfoRegMapper.deleteFarmLandInfoReg */
WHERE 1=1
  AND FRMERNO		= #{frmerno}
  AND FRLN_SN		= #{frlnSn}
  AND FRLN_SNO		= #{frlnSno}
  AND FRLN_MNO		= #{frlnMno}
