mapper.select:
SELECT /* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.selectFarmerEducationInfo */
FRMERNO , MNGMST_REGNO , CRCLM_CD , FLNM , EDNST_NM , CRCLM_NM , EDU_BGNG_YMD , EDU_END_YMD , IDNTY_YN
, DEL_YN , SYS_FRST_INPT_DT , SYS_FRST_INPT_PRGRM_ID , SYS_FRST_INPT_USER_ID , SYS_LAST_CHG_DT , SYS_LAST_CHG_PRGRM_ID , SYS_LAST_CHG_USER_ID FROM USRAM.TB_AX_FRMER_EDU_INFO WHERE 1=1
AND FRMERNO = #{frmerno}
AND MNGMST_REGNO = #{mngmstRegno}
ORDER BY SYS_LAST_CHG_DT DESC

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.selectFarmerEducationInfoList */ FRMERNO , MNGMST_REGNO , CRCLM_CD , FLNM , EDNST_NM , CRCLM_NM , EDU_BGNG_YMD , EDU_END_YMD , IDNTY_YN
, DEL_YN , SYS_FRST_INPT_DT , SYS_FRST_INPT_PRGRM_ID , SYS_FRST_INPT_USER_ID , SYS_LAST_CHG_DT , SYS_LAST_CHG_PRGRM_ID , SYS_LAST_CHG_USER_ID FROM USRAM.TB_AX_FRMER_EDU_INFO WHERE 1=1
AND FRMERNO = #{frmerno}
AND MNGMST_REGNO = #{mngmstRegno}
ORDER BY SYS_LAST_CHG_DT DESC
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

