mapper.select:
SELECT /* com.at.apcss.fm.farm.mapper.FarmerEducationInfoRegMapper.selectFarmerEducationInfoReg */ FRMERNO , MNGMST_REGNO , CRCLM_CD , FLNM , EDNST_NM , CRCLM_NM , EDU_BGNG_YMD , EDU_END_YMD , IDNTY_YN
, DEL_YN , SYS_FRST_INPT_DT , SYS_FRST_INPT_PRGRM_ID , SYS_FRST_INPT_USER_ID , SYS_LAST_CHG_DT , SYS_LAST_CHG_PRGRM_ID , SYS_LAST_CHG_USER_ID FROM USRAM.TB_AX_FRMER_EDU_INFO WHERE 1=1
AND FRMERNO = #{frmerno}
AND MNGMST_REGNO = #{mngmstRegno}
ORDER BY SYS_LAST_CHG_DT DESC

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.farm.mapper.FarmerEducationInfoRegMapper.selectFarmerEducationInfoRegList */ FRMERNO , MNGMST_REGNO , CRCLM_CD , FLNM , EDNST_NM , CRCLM_NM , EDU_BGNG_YMD , EDU_END_YMD , IDNTY_YN
, DEL_YN , SYS_FRST_INPT_DT , SYS_FRST_INPT_PRGRM_ID , SYS_FRST_INPT_USER_ID , SYS_LAST_CHG_DT , SYS_LAST_CHG_PRGRM_ID , SYS_LAST_CHG_USER_ID FROM USRAM.TB_AX_FRMER_EDU_INFO WHERE 1=1
AND FRMERNO = #{frmerno}
AND MNGMST_REGNO = #{mngmstRegno}
ORDER BY SYS_LAST_CHG_DT DESC
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

MERGE INTO USRAM.TB_AX_FRMER_EDU_INFO AS tgt	/* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.insertMegerFarmerEducationInfo */
USING DUAL
	ON (
			tgt.FRMERNO			= #{frmerno}
			AND tgt.CRCLM_CD		= #{crclmCd}
		)
WHEN MATCHED THEN
	UPDATE	/* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.insertMegerFarmerEducationInfo */
	   SET
		  tgt.MNGMST_REGNO				=#{mngmstRegno}
		, tgt.CRCLM_CD					=#{crclmCd}
		, tgt.FLNM						=#{flNm}
		, tgt.EDNST_NM					=#{ednstNm}
		, tgt.CRCLM_NM					=#{crclmNm}
		, tgt.EDU_BGNG_YMD				=#{eduBgngYmd}
		, tgt.EDU_END_YMD				=#{eduEndYmd}
		, tgt.IDNTY_YN					=#{idntyYn}

		, tgt.SYS_LAST_CHG_DT			= SYSDATE
		, tgt.SYS_LAST_CHG_PRGRM_ID		=#{sysLastChgUserId}
		, tgt.SYS_LAST_CHG_USER_ID		=#{sysLastChgPrgrmId}
WHEN NOT MATCHED
THEN INSERT /* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.insertMegerFarmerEducationInfo */
	(
		  FRMERNO
		, MNGMST_REGNO
		, CRCLM_CD
		, FLNM
		, EDNST_NM
		, CRCLM_NM
		, EDU_BGNG_YMD
		, EDU_END_YMD
		, IDNTY_YN

		, SYS_FRST_INPT_DT
		, SYS_FRST_INPT_PRGRM_ID
		, SYS_FRST_INPT_USER_ID
		, SYS_LAST_CHG_DT
		, SYS_LAST_CHG_PRGRM_ID
		, SYS_LAST_CHG_USER_ID
)VALUES (
		  #{frmerno}
		, #{mngmstRegno}
		, #{crclmCd}
		, #{flNm}
		, #{ednstNm}
		, #{crclmNm}
		, #{eduBgngYmd}
		, #{eduEndYmd}
		, #{idntyYn}

		, SYSDATE
		, #{sysFrstInptUserId}
		, #{sysFrstInptPrgrmId}
		, SYSDATE
		, #{sysLastChgUserId}
		, #{sysLastChgPrgrmId}
);

mapper.delete:

DELETE FROM TB_AX_FRMER_EDU_INFO /* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.deleteFarmerEducationInfo */
WHERE 1=1
  AND FRMERNO		= #{frmerno}
  AND CRCLM_CD		= #{crclmCd}
