mapper.insert:

MERGE INTO USRAM.TB_AX_FRMER_GRANTS_INFO AS tgt	/* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.insertMegerFarmerEducationInfo */
USING DUAL
	ON (
			tgt.FRMERNO			= #{frmerno}
			AND tgt.SN			= #{sn}
		)
WHEN MATCHED THEN
	UPDATE	/* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.insertMegerFarmerEducationInfo */
	   SET
		  tgt.BIZ_BGNG_YMD			=#{bizBgngYmd}
		, tgt.BIZ_END_YMD			=#{bizEndYmd}
		, tgt.CMPTNC_INST			=#{cmptncInst}
		, tgt.BIZ_CD				=#{bizCd}
		, tgt.BIZ_NM				=#{bizNm}
		, tgt.RCFVRS_NM				=#{rcfvrsNm}
		, tgt.GOV_FNNCNG			=#{govFnncng}
		, tgt.LCLT_EXPND_FNNCNG		=#{lcLtExpndFnncng}
		, tgt.GOV_SBSDS				=#{govSbsds}
		, tgt.LCLT_EXPND_SBSDS		=#{lcltExpndSbsds}
		, tgt.PRCS_SYS				=#{prcsSys}

		, tgt.SYS_LAST_CHG_DT			= SYSDATE
		, tgt.SYS_LAST_CHG_PRGRM_ID		=#{sysLastChgUserId}
		, tgt.SYS_LAST_CHG_USER_ID		=#{sysLastChgPrgrmId}
WHEN NOT MATCHED
THEN INSERT /* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.insertMegerFarmerEducationInfo */
	(
		  FRMERNO
		, BIZ_BGNG_YMD
		, BIZ_END_YMD
		, CMPTNC_INST
		, BIZ_CD
		, BIZ_NM
		, RCFVRS_NM
		, GOV_FNNCNG
		, LCLT_EXPND_FNNCNG
		, GOV_SBSDS
		, LCLT_EXPND_SBSDS
		, PRCS_SYS

		, SYS_FRST_INPT_DT
		, SYS_FRST_INPT_PRGRM_ID
		, SYS_FRST_INPT_USER_ID
		, SYS_LAST_CHG_DT
		, SYS_LAST_CHG_PRGRM_ID
		, SYS_LAST_CHG_USER_ID
)VALUES (
		  #{frmerno}
		, #{bizBgngYmd}
		, #{bizEndYmd}
		, #{cmptncInst}
		, #{bizCd}
		, #{bizNm}
		, #{rcfvrsNm}
		, #{govFnncng}
		, #{lcLtExpndFnncng}
		, #{govSbsds}
		, #{lcltExpndSbsds}
		, #{prcsSys}

		, SYSDATE
		, #{sysFrstInptUserId}
		, #{sysFrstInptPrgrmId}
		, SYSDATE
		, #{sysLastChgUserId}
		, #{sysLastChgPrgrmId}
);

mapper.delete:

DELETE FROM TB_AX_FRMER_GRANTS_INFO /* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.deleteFarmerEducationInfo */
WHERE 1=1
  AND FRMERNO		= #{frmerno}
  AND SN			= #{sn}
