mapper.insert:

MERGE INTO USRAM.TB_AX_FRMER_SN_CERT_INFO AS tgt	/* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.insertMegerFarmerEducationInfo */
USING DUAL
	ON (
			tgt.FRMERNO			= #{frmerno}
			AND tgt.SN			= #{sn}
		)
WHEN MATCHED THEN
	UPDATE	/* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.insertMegerFarmerEducationInfo */
	   SET
		  tgt.MNGMST_REGNO				=#{mngmstRegno}
		, tgt.CRCLM_CD					=#{crclmCd}
		, tgt.FLNM						=#{flNm}
		, tgt.EDNST_NM					=#{ednstNm}
		, tgt.CRCLM_NM					=#{crclmNm}
		, tgt.EDU_BGNG_YMD				=#{eduBgngYmd}
		, tgt.EDU_END_YMD				=#{eduEndYmd}
		, tgt.IDNTY_YN					=#{idntyYn}

		, tgt.SYS_LAST_CHG_DT			= SYSDATE
		, tgt.SYS_LAST_CHG_PRGRM_ID		=#{sysLastChgUserId}
		, tgt.SYS_LAST_CHG_USER_ID		=#{sysLastChgPrgrmId}
WHEN NOT MATCHED
THEN INSERT /* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.insertMegerFarmerEducationInfo */
	(
		  FRMERNO
		, MNGMST_REGNO
		, CRCLM_CD
		, FLNM
		, EDNST_NM
		, CRCLM_NM
		, EDU_BGNG_YMD
		, EDU_END_YMD
		, IDNTY_YN

		, SYS_FRST_INPT_DT
		, SYS_FRST_INPT_PRGRM_ID
		, SYS_FRST_INPT_USER_ID
		, SYS_LAST_CHG_DT
		, SYS_LAST_CHG_PRGRM_ID
		, SYS_LAST_CHG_USER_ID
)VALUES (
		  #{frmerno}
		, #{mngmstRegno}
		, #{crclmCd}
		, #{flNm}
		, #{ednstNm}
		, #{crclmNm}
		, #{eduBgngYmd}
		, #{eduEndYmd}
		, #{idntyYn}

		, SYSDATE
		, #{sysFrstInptUserId}
		, #{sysFrstInptPrgrmId}
		, SYSDATE
		, #{sysLastChgUserId}
		, #{sysLastChgPrgrmId}
);

mapper.delete:

DELETE FROM TB_AX_FRMER_SN_CERT_INFO /* com.at.apcss.fm.farm.mapper.FarmerEducationInfoMapper.deleteFarmerEducationInfo */
WHERE 1=1
  AND FRMERNO		= #{frmerno}
  AND CRCLM_CD		= #{crclmCd}
