UPDATE /* com.at.apcss.fm.farm.mapper.NonCompliantFarmerInfoMapper.upateNonCompliantFarmerInfo */
	TB_AX_NCMPLNT_FRMER_INFO
   SET
	  tgt.FRMERNO			=#{frmerno}
	, tgt.BIZ_YR			=#{bizYr}
	, tgt.BIZ_NM			=#{bizNm}
	, tgt.VLTN_CN			=#{vltnCn}
	, tgt.SNCNS_BGNG_YR		=#{sncnsBgngYr}
	, tgt.SNCNS_END_YR		=#{sncnsEndYr}
	, tgt.RDM_PRGRS_CD		=#{rdmPrgrsCd}
	, tgt.RDM_TRGT_PRNCPL	=#{rdmTrgtPrncpl}
	, tgt.RDM_TRGT_AMT		=#{rdmTrgtAmt}



	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

WHERE 1=1
	AND FRMERNO			= #{frmerno}
	AND SN				= #{sn}

