mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltAtmtcMchnInfoMapper.selectFcltAtmtcMchnInfo */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SPECS AS SPECS , FCLT.FCLT_HLD_YN AS FCLT_HLD_YN , FCLT.FCLT_HLD_YN2 AS FCLT_HLD_YN2 , FCLT.FCLT_HLD_YN3 AS FCLT_HLD_YN3 , FCLT.FCLT_HLD_YN4 AS FCLT_HLD_YN4 , FCLT.FCLT_HLD_YN5 AS FCLT_HLD_YN5 , FCLT.FCLT_HLD_YN6 AS FCLT_HLD_YN6 , FCLT.FCLT_HLD_YN7 AS FCLT_HLD_YN7 , FCLT.FCLT_HLD_YN8 AS FCLT_HLD_YN8 , FCLT.FCLT_HLD_YN9 AS FCLT_HLD_YN9 , FCLT.FCLT_HLD_YN10 AS FCLT_HLD_YN10 , FCLT.FCLT_HLD_YN11 AS FCLT_HLD_YN11 , FCLT.OTHER_FCLT AS OTHER_FCLT
FROM TB_FCLT_ATMTC_MCHN_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltAtmtcMchnInfoMapper.selectFcltAtmtcMchnInfoList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SPECS AS SPECS , FCLT.FCLT_HLD_YN AS FCLT_HLD_YN , FCLT.FCLT_HLD_YN2 AS FCLT_HLD_YN2 , FCLT.FCLT_HLD_YN3 AS FCLT_HLD_YN3 , FCLT.FCLT_HLD_YN4 AS FCLT_HLD_YN4 , FCLT.FCLT_HLD_YN5 AS FCLT_HLD_YN5 , FCLT.FCLT_HLD_YN6 AS FCLT_HLD_YN6 , FCLT.FCLT_HLD_YN7 AS FCLT_HLD_YN7 , FCLT.FCLT_HLD_YN8 AS FCLT_HLD_YN8 , FCLT.FCLT_HLD_YN9 AS FCLT_HLD_YN9 , FCLT.FCLT_HLD_YN10 AS FCLT_HLD_YN10 , FCLT.FCLT_HLD_YN11 AS FCLT_HLD_YN11 , FCLT.OTHER_FCLT AS OTHER_FCLT FROM TB_FCLT_ATMTC_MCHN_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

