mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltAtmtcMchnInfoMapper.selectFcltAtmtcMchnInfo */ OTI.CRTR_YR , OTI.APC_CD , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = OTI.APC_CD AND CRTR_YR = OTI.CRTR_YR ) AS APC_NM , OTI.SN , OTI.ITEM_CD , OTI.ITEM_NM --, SPSD.RPRS_ITEM_SPCFCT , SPSD.DPTZ , SPSD.APM , SPSD.ELCTRN_WGHT_SORT_MCHN , SPSD.NDST_BRCK_SORT_MCHN , SPSD.GRD_RAW_MTR_ATMTC_TRNSF_MCHN , SPSD.EMPT_BX_ATMTC_EXHST_MCHN , SPSD.ATMTC_BX_PCKG_MCHN , SPSD.ATMTC_SS_MCHN , SPSD.ATMTC_WRAP_MCHN , SPSD.ATMTC_WGHT_CHCK_MCHN , SPSD.PTZ --, SPSD.ETC_MNFC_MCHN , SPSD.SPCFCT_CNT , SPSD.SPCFCT_GRP , SPSD.SPCFCT_GRD , SPSD.WBG , SPSD.WRHS_BR_QR , SPSD.KSK_TBLT , SPSD.DSHBRD , SPSD.SPMT_BR_QR , SPSD.TH_AC
FROM TB_TE_APC_OPER_TRMT_ITEM OTI ,TB_TE_APC_SMART_PRPLSN_STTN_DTL SPSD WHERE 1=1 AND OTI.APC_CD = SPSD.APC_CD(+) AND OTI.CRTR_YR = SPSD.CRTR_YR(+) AND OTI.SN = SPSD.SN(+) AND OTI.OGNZ_SE_CD = '2' AND OTI.APC_CD IS NOT NULL AND OTI.SN NOT IN ('4')
AND OTI.CRTR_YR = #{crtrYr}
AND OTI.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltAtmtcMchnInfoMapper.selectFcltAtmtcMchnInfo */ OTI.CRTR_YR , OTI.APC_CD , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = OTI.APC_CD AND CRTR_YR = OTI.CRTR_YR ) AS APC_NM , OTI.SN , OTI.ITEM_CD , OTI.ITEM_NM --, SPSD.RPRS_ITEM_SPCFCT , SPSD.DPTZ , SPSD.APM , SPSD.ELCTRN_WGHT_SORT_MCHN , SPSD.NDST_BRCK_SORT_MCHN , SPSD.GRD_RAW_MTR_ATMTC_TRNSF_MCHN , SPSD.EMPT_BX_ATMTC_EXHST_MCHN , SPSD.ATMTC_BX_PCKG_MCHN , SPSD.ATMTC_SS_MCHN , SPSD.ATMTC_WRAP_MCHN , SPSD.ATMTC_WGHT_CHCK_MCHN , SPSD.PTZ --, SPSD.ETC_MNFC_MCHN , SPSD.SPCFCT_CNT , SPSD.SPCFCT_GRP , SPSD.SPCFCT_GRD , SPSD.WBG , SPSD.WRHS_BR_QR , SPSD.KSK_TBLT , SPSD.DSHBRD , SPSD.SPMT_BR_QR , SPSD.TH_AC
FROM TB_TE_APC_OPER_TRMT_ITEM OTI ,TB_TE_APC_SMART_PRPLSN_STTN_DTL SPSD WHERE 1=1 AND OTI.APC_CD = SPSD.APC_CD(+) AND OTI.CRTR_YR = SPSD.CRTR_YR(+) AND OTI.SN = SPSD.SN(+) AND OTI.OGNZ_SE_CD = '2' AND OTI.APC_CD IS NOT NULL AND OTI.SN NOT IN ('4')
AND OTI.CRTR_YR = #{crtrYr}
AND OTI.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
MERGE INTO TB_TE_APC_SMART_PRPLSN_STTN_DTL SPSD /* com.at.apcss.fm.fclt.mapper.FcltAtmtcMchnInfoMapper.insertFcltAtmtcMchnInfo */
USING DUAL
ON (
	SPSD.CRTR_YR = #{crtrYr}
	AND SPSD.APC_CD = #{apcCd}
	AND SPSD.SN = #{sn}
	)
WHEN MATCHED THEN
  UPDATE SET
		  DPTZ							= #{dptz}
		  , APM							= #{apm}
		  , ELCTRN_WGHT_SORT_MCHN		= #{elctrnWghtSortMchn}
		  , NDST_BRCK_SORT_MCHN			= #{ndstBrckSortMchn}
		  , GRD_RAW_MTR_ATMTC_TRNSF_MCHN= #{grdRawMtrAtmtcTrnsfMchn}
		  , EMPT_BX_ATMTC_EXHST_MCHN	= #{emptBxAtmtcExhstMchn}
		  , ATMTC_BX_PCKG_MCHN			= #{atmtcBxPckgMchn}
		  , ATMTC_SS_MCHN				= #{atmtcSsMchn}
		  , ATMTC_WRAP_MCHN				= #{atmtcWrapMchn}
		  , ATMTC_WGHT_CHCK_MCHN		= #{atmtcWghtChckMchn}
		  , PTZ							= #{ptz}

		  , SPCFCT_CNT	= #{spcfctCnt}
		  , SPCFCT_GRP	= #{spcfctGrp}
		  , SPCFCT_GRD	= #{spcfctGrd}
		  , WBG			= #{wbg}
		  , WRHS_BR_QR	= #{wrhsBrQr}
		  , KSK_TBLT	= #{kskTblt}
		  , DSHBRD		= #{dshbrd}
		  , SPMT_BR_QR	= #{spmtBrQr}
		  , TH_AC		= #{thAc}

		  , SYS_LAST_CHG_DT			= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		  CRTR_YR
		  , APC_CD
		  , APC_NM
		  , SN
		  , ITEM_CD
		  , ITEM_NM

		  , DPTZ
		  , APM
		  , ELCTRN_WGHT_SORT_MCHN
		  , NDST_BRCK_SORT_MCHN
		  , GRD_RAW_MTR_ATMTC_TRNSF_MCHN
		  , EMPT_BX_ATMTC_EXHST_MCHN
		  , ATMTC_BX_PCKG_MCHN
		  , ATMTC_SS_MCHN
		  , ATMTC_WRAP_MCHN
		  , ATMTC_WGHT_CHCK_MCHN
		  , PTZ

		  , SPCFCT_CNT
		  , SPCFCT_GRP
		  , SPCFCT_GRD
		  , WBG
		  , WRHS_BR_QR
		  , KSK_TBLT
		  , DSHBRD
		  , SPMT_BR_QR
		  , TH_AC

		  , SYS_FRST_INPT_DT
		  , SYS_FRST_INPT_USER_ID
		  , SYS_FRST_INPT_PRGRM_ID
		  , SYS_LAST_CHG_DT
		  , SYS_LAST_CHG_USER_ID
		  , SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
		  #{crtrYr}
		  , #{apcCd}
		  , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr})
		  , #{sn}
		  , (SELECT
		  			ITEM_CD
		  		FROM  TB_TE_APC_OPER_TRMT_ITEM
		  		WHERE 1=1
		  		  AND APC_CD = #{apcCd}
		  		  AND CRTR_YR = #{crtrYr}
		  		  AND SN = #{sn}
		  		  AND OGNZ_SE_CD = '2'
		  	)
		  , (SELECT
		  			ITEM_NM
		  		FROM  TB_TE_APC_OPER_TRMT_ITEM
		  		WHERE 1=1
		  		  AND APC_CD = #{apcCd}
		  		  AND CRTR_YR = #{crtrYr}
		  		  AND SN = #{sn}
		  		  AND OGNZ_SE_CD = '2'
		  	)

		  , #{dptz}
		  , #{apm}
		  , #{elctrnWghtSortMchn}
		  , #{ndstBrckSortMchn}
		  , #{grdRawMtrAtmtcTrnsfMchn}
		  , #{emptBxAtmtcExhstMchn}
		  , #{atmtcBxPckgMchn}
		  , #{atmtcSsMchn}
		  , #{atmtcWrapMchn}
		  , #{atmtcWghtChckMchn}
		  , #{ptz}

		  , #{spcfctCnt}
		  , #{spcfctGrp}
		  , #{spcfctGrd}
		  , #{wbg}
		  , #{wrhsBrQr}
		  , #{kskTblt}
		  , #{dshbrd}
		  , #{spmtBrQr}
		  , #{thAc}

		  , SYSDATE
		  , #{sysFrstInptUserId}
		  , #{sysFrstInptPrgrmId}
		  , SYSDATE
		  , #{sysLastChgUserId}
		  , #{sysLastChgPrgrmId}
		)


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltAtmtcMchnInfoMapper.updateFcltAtmtcMchnInfo */
	TB_TE_APC_SMART_PRPLSN_STTN_DTL SPSD
   SET
	  DPTZ							= #{dptz}
	  , APM							= #{apm}
	  , ELCTRN_WGHT_SORT_MCHN		= #{elctrnWghtSortMchn}
	  , NDST_BRCK_SORT_MCHN			= #{ndstBrckSortMchn}
	  , GRD_RAW_MTR_ATMTC_TRNSF_MCHN= #{grdRawMtrAtmtcTrnsfMchn}
	  , EMPT_BX_ATMTC_EXHST_MCHN	= #{emptBxAtmtcExhstMchn}
	  , ATMTC_BX_PCKG_MCHN			= #{atmtcBxPckgMchn}
	  , ATMTC_SS_MCHN				= #{atmtcSsMchn}
	  , ATMTC_WRAP_MCHN				= #{atmtcWrapMchn}
	  , ATMTC_WGHT_CHCK_MCHN		= #{atmtcWghtChckMchn}
	  , PTZ							= #{ptz}

	  , SPCFCT_CNT	= #{spcfctCnt}
	  , SPCFCT_GRP	= #{spcfctGrp}
	  , SPCFCT_GRD	= #{spcfctGrd}
	  , WBG			= #{wbg}
	  , WRHS_BR_QR	= #{wrhsBrQr}
	  , KSK_TBLT	= #{kskTblt}
	  , DSHBRD		= #{dshbrd}
	  , SPMT_BR_QR	= #{spmtBrQr}
	  , TH_AC		= #{thAc}


	, DEL_YN			= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHERE 1=1
  AND SPSD.CRTR_YR = #{crtrYr}
  AND SPSD.APC_CD = #{apcCd}
  AND SPSD.SN = #{sn}


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltAtmtcMchnInfoMapper.deleteFcltAtmtcMchnInfo */
	TB_TE_APC_SMART_PRPLSN_STTN_DTL SPSD
   SET
	  DEL_YN 	= 'Y'
WHERE 1=1
  AND SPSD.CRTR_YR = #{crtrYr}
  AND SPSD.APC_CD = #{apcCd}
  AND SPSD.SN = #{sn}
