mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.selectFcltDtaMngInfo */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SN AS SN , FCLT.FCLT_SYS_INSTL_CD AS FCLT_SYS_INSTL_CD , FCLT.FCLT_SYS_HLD_YN AS FCLT_SYS_HLD_YN , FCLT.FCLT_SYS_LINK_CD AS FCLT_SYS_LINK_CD , FCLT.FCLT_SYS_MNG_CD AS FCLT_SYS_MNG_CD , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID , FCLT.FCLT_SYS_HLD_YN2 AS FCLT_SYS_HLD_YN2 , FCLT.FCLT_SYS_HLD_YN3 AS FCLT_SYS_HLD_YN3 , FCLT.FCLT_SYS_HLD_YN4 AS FCLT_SYS_HLD_YN4 , FCLT.FCLT_SYS_HLD_YN5 AS FCLT_SYS_HLD_YN5 , FCLT.FCLT_SYS_HLD_YN6 AS FCLT_SYS_HLD_YN6 , FCLT.FCLT_MNG AS FCLT_MNG , FCLT.FCLT_MNG2 AS FCLT_MNG2 , FCLT.FCLT_MNG3 AS FCLT_MNG3 , FCLT.FCLT_MNG4 AS FCLT_MNG4 , FCLT.FCLT_MNG5 AS FCLT_MNG5 , FCLT.FCLT_MNG6 AS FCLT_MNG6
FROM TB_FCLT_DTA_MNG_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.selectFcltDtaMngInfoList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SN AS SN , FCLT.FCLT_SYS_INSTL_CD AS FCLT_SYS_INSTL_CD , FCLT.FCLT_SYS_HLD_YN AS FCLT_SYS_HLD_YN , FCLT.FCLT_SYS_LINK_CD AS FCLT_SYS_LINK_CD , FCLT.FCLT_SYS_MNG_CD AS FCLT_SYS_MNG_CD , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID , FCLT.FCLT_SYS_HLD_YN2 AS FCLT_SYS_HLD_YN2 , FCLT.FCLT_SYS_HLD_YN3 AS FCLT_SYS_HLD_YN3 , FCLT.FCLT_SYS_HLD_YN4 AS FCLT_SYS_HLD_YN4 , FCLT.FCLT_SYS_HLD_YN5 AS FCLT_SYS_HLD_YN5 , FCLT.FCLT_SYS_HLD_YN6 AS FCLT_SYS_HLD_YN6 , FCLT.FCLT_MNG AS FCLT_MNG , FCLT.FCLT_MNG2 AS FCLT_MNG2 , FCLT.FCLT_MNG3 AS FCLT_MNG3 , FCLT.FCLT_MNG4 AS FCLT_MNG4 , FCLT.FCLT_MNG5 AS FCLT_MNG5 , FCLT.FCLT_MNG6 AS FCLT_MNG6
FROM TB_FCLT_DTA_MNG_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

