mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.selectFcltDtaMngInfo */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SN AS SN , FCLT.FCLT_SYS_INSTL_CD AS FCLT_SYS_INSTL_CD , FCLT.FCLT_SYS_HLD_YN AS FCLT_SYS_HLD_YN , FCLT.FCLT_SYS_LINK_CD AS FCLT_SYS_LINK_CD , FCLT.FCLT_SYS_MNG_CD AS FCLT_SYS_MNG_CD , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_FCLT_DTA_MNG_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.selectFcltDtaMngInfoList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SN AS SN , FCLT_SYS_INSTL_CD AS FCLT_SYS_INSTL_CD , FCLT.FCLT_SYS_HLD_YN AS FCLT_SYS_HLD_YN , FCLT.FCLT_SYS_LINK_CD AS FCLT_SYS_LINK_CD , FCLT.FCLT_SYS_MNG_CD AS FCLT_SYS_MNG_CD , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_FCLT_DTA_MNG_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

 	INSERT /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.insertFcltDtaMngInfo */
 	  INTO TB_FCLT_DTA_MNG_INFO FCLT
 		(

 		  , TRGT_YR
 		  , APC_CD
 		  , SN
 		  , FCLT_SYS_INSTL_CD
 		  , FCLT_SYS_HLD_YN
 		  , FCLT_SYS_LINK_CD
 		  , FCLT_SYS_MNG_CD
 		  , DEL_YN
 		  , SYS_FRST_INPT_DT
 		  , SYS_FRST_INPT_USER_ID
 		  , SYS_FRST_INPT_PRGRM_ID
 		  , SYS_LAST_CHG_DT
 		  , SYS_LAST_CHG_USER_ID
 		  , SYS_LAST_CHG_PRGRM_ID
 		)
		VALUES
			(
 		  #{trgtYr}fclt_
 		, #{apcCd}
 		, #{sn}
 		, #{fcltSysInstlCd}
 		, #{fcltSysHldYn}
 		, #{fcltSysLinkCd}
 		, #{fcltSysMngCd}
 		, #{delYn}
 		, SYSDATE
 		, #{sysFrstInptUserId}
 		, #{sysFrstInptPrgrmId}
 		, SYSDATE
 		, #{sysLastChgUserId}
 		, #{sysLastChgPrgrmId}

			)

mapper.update:

    	UPDATE /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.updateFcltDtaMngInfo */
    		TB_FCLT_DTA_MNG_INFO FCLT
		   SET
		         TRGT_YR			  =     #{trgtYr}
		        , APC_CD    	      =     #{apcCd}
		        , SN            	  =     #{sn}
		        , FCLT_SYS_INSTL_CD   =     #{fcltSysInstlCd}
		        , FCLT_SYS_HLD_YN     =     #{fcltSysHldYn}
		        , FCLT_SYS_LINK_CD    =     #{fcltSysLinkCd}
		        , FCLT_SYS_MNG_CD     =     #{fcltSysMngCd}


			, DEL_YN 	= #{delYn}


			, SYS_LAST_CHG_DT		= SYSDATE
    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

		WHERE 1=1
	      AND TRGT_YR = #{trgtYr}
	      AND APC_CD = #{apcCd}

mapper.delete:

    	DELETE /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.deleteFcltDtaMngInfo */
    		TB_FCLT_DTA_MNG_INFO FCLT
		WHERE 1=1
	      AND TRGT_YR = #{trgtYr}
	      AND APC_CD = #{apcCd}
