mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.selectFcltDtaMngInfo */ ASPS.CRTR_YR , ASPS.APC_CD , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = ASPS.APC_CD AND CRTR_YR = ASPS.CRTR_YR ) AS APC_NM
, PRDCTN_INFO_MNG_MTHD , WRHS_INFO_MNG_MTHD , SORT_INFO_MNG_MTHD , STRG_INFO_MNG_MTHD , PCKG_INFO_MNG_MTHD , JOB_INFO_MNG_MTHD , SPMT_INFO_MNG_MTHD
FROM TB_TE_APC_SMART_PRPLSN_STTN ASPS WHERE 1=1 AND DEL_YN != 'Y' AND APC_CD IS NOT NULL
AND ASPS.CRTR_YR = #{crtrYr}
AND ASPS.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.selectFcltDtaMngInfo */ ASPS.CRTR_YR , ASPS.APC_CD , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = ASPS.APC_CD AND CRTR_YR = ASPS.CRTR_YR ) AS APC_NM
, PRDCTN_INFO_MNG_MTHD , WRHS_INFO_MNG_MTHD , SORT_INFO_MNG_MTHD , STRG_INFO_MNG_MTHD , PCKG_INFO_MNG_MTHD , JOB_INFO_MNG_MTHD , SPMT_INFO_MNG_MTHD
FROM TB_TE_APC_SMART_PRPLSN_STTN ASPS WHERE 1=1 AND DEL_YN != 'Y' AND APC_CD IS NOT NULL
AND ASPS.CRTR_YR = #{crtrYr}
AND ASPS.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
MERGE INTO TB_TE_APC_SMART_PRPLSN_STTN ASPS /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.insertFcltDtaMngInfo */
USING DUAL
ON (
	ASPS.CRTR_YR = #{crtrYr}
	AND ASPS.APC_CD = #{apcCd}
	)
WHEN MATCHED THEN
  UPDATE SET
		  PRDCTN_INFO_MNG_MTHD = #{prdctnInfoMngMthd}
		  , WRHS_INFO_MNG_MTHD = #{wrhsInfoMngMthd}
		  , SORT_INFO_MNG_MTHD = #{sortInfoMngMthd}
		  , STRG_INFO_MNG_MTHD = #{strgInfoMngMthd}
		  , PCKG_INFO_MNG_MTHD = #{pckgInfoMngMthd}
		  , JOB_INFO_MNG_MTHD = #{jobInfoMngMthd}
		  , SPMT_INFO_MNG_MTHD = #{spmtInfoMngMthd}

		  , SYS_LAST_CHG_DT			= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		  CRTR_YR
		  , APC_CD
		  , APC_NM

		  , PRDCTN_INFO_MNG_MTHD
		  , WRHS_INFO_MNG_MTHD
		  , SORT_INFO_MNG_MTHD
		  , STRG_INFO_MNG_MTHD
		  , PCKG_INFO_MNG_MTHD
		  , JOB_INFO_MNG_MTHD
		  , SPMT_INFO_MNG_MTHD

		  , SYS_FRST_INPT_DT
		  , SYS_FRST_INPT_USER_ID
		  , SYS_FRST_INPT_PRGRM_ID
		  , SYS_LAST_CHG_DT
		  , SYS_LAST_CHG_USER_ID
		  , SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
		  #{crtrYr}
		  , #{apcCd}
		  , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr})

		  , #{prdctnInfoMngMthd}
		  , #{wrhsInfoMngMthd}
		  , #{sortInfoMngMthd}
		  , #{strgInfoMngMthd}
		  , #{pckgInfoMngMthd}
		  , #{jobInfoMngMthd}
		  , #{spmtInfoMngMthd}

		  , SYSDATE
		  , #{sysFrstInptUserId}
		  , #{sysFrstInptPrgrmId}
		  , SYSDATE
		  , #{sysLastChgUserId}
		  , #{sysLastChgPrgrmId}
		)


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.updateFcltDtaMngInfo */
	TB_TE_APC_SMART_PRPLSN_STTN ASPS
   SET
	  PRDCTN_INFO_MNG_MTHD = #{prdctnInfoMngMthd}
	  , WRHS_INFO_MNG_MTHD = #{wrhsInfoMngMthd}
	  , SORT_INFO_MNG_MTHD = #{sortInfoMngMthd}
	  , STRG_INFO_MNG_MTHD = #{strgInfoMngMthd}
	  , PCKG_INFO_MNG_MTHD = #{pckgInfoMngMthd}
	  , JOB_INFO_MNG_MTHD = #{jobInfoMngMthd}
	  , SPMT_INFO_MNG_MTHD = #{spmtInfoMngMthd}


	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

WHERE 1=1
  AND CRTR_YR = #{crtrYr}
  AND APC_CD = #{apcCd}

mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.selectFcltDtaMngInfo */ ASDM.CRTR_YR , ASDM.APC_CD , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = ASDM.APC_CD AND CRTR_YR = ASDM.CRTR_YR ) AS APC_NM
, ASDM.DATA_MNG_TYPE , ASDM.DATA_MNG_TYPE_DTL , ASDM.DATA_MNG_YN , ASDM.DATA_MNG_ETC
FROM TB_TE_APC_SMART_DATA_MNG ASDM WHERE 1=1 AND DEL_YN != 'Y' AND APC_CD IS NOT NULL
AND ASDM.CRTR_YR = #{crtrYr}
AND ASDM.APC_CD = #{apcCd}

mapper.update:
MERGE INTO TB_TE_APC_SMART_DATA_MNG ASDM /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.insertFcltDataMng */
USING DUAL
ON (
	ASDM.CRTR_YR = #{crtrYr}
	AND ASDM.APC_CD = #{apcCd}
	AND ASDM.DATA_MNG_TYPE_DTL = #{dataMngTypeDtl}
	)
WHEN MATCHED THEN
  UPDATE SET
		  DATA_MNG_YN = #{dataMngYn}
		  ,DATA_MNG_ETC = #{dataMngEtc}

		  , SYS_LAST_CHG_DT			= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		  CRTR_YR
		  , APC_CD
		  , APC_NM

		  , DATA_MNG_TYPE
		  , DATA_MNG_TYPE_DTL
		  , DATA_MNG_YN
		  , DATA_MNG_ETC

		  , SYS_FRST_INPT_DT
		  , SYS_FRST_INPT_USER_ID
		  , SYS_FRST_INPT_PRGRM_ID
		  , SYS_LAST_CHG_DT
		  , SYS_LAST_CHG_USER_ID
		  , SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
		  #{crtrYr}
		  , #{apcCd}
		  , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr})

		  , #{dataMngType}
		  , #{dataMngTypeDtl}
		  , #{dataMngYn}
		  , #{dataMngEtc}

		  , SYSDATE
		  , #{sysFrstInptUserId}
		  , #{sysFrstInptPrgrmId}
		  , SYSDATE
		  , #{sysLastChgUserId}
		  , #{sysLastChgPrgrmId}
		)


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.updateFcltDataMng */
	TB_TE_APC_SMART_DATA_MNG ASDM
   SET
	  DATA_MNG_YN = #{dataMngYn}
	  ,DATA_MNG_ETC = #{dataMngEtc}


	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

WHERE 1=1
  AND ASDM.CRTR_YR = #{crtrYr}
  AND ASDM.APC_CD = #{apcCd}
  AND ASDM.DATA_MNG_TYPE_DTL = #{dataMngTypeDtl}


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltDtaMngInfoMapper.deleteFcltDataMng */
	TB_TE_APC_SMART_DATA_MNG ASDM
   SET
	  DEL_YN 	= 'Y'
WHERE 1=1
  AND ASDM.CRTR_YR = #{crtrYr}
  AND ASDM.APC_CD = #{apcCd}
  AND ASDM.DATA_MNG_TYPE_DTL = #{dataMngTypeDtl}
