mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltGdsMchnInfoMapper.selectFcltGdsMchnInfo */ OTI.CRTR_YR , OTI.APC_CD , OTI.APC_NM , OTI.ITEM_CD , OTI.ITEM_NM , OTI.SN
, FSD.SORT_MCHN_SPCFCT , FSD.SORT_BRCK_MCHN , FSD.COLOR_SORT , FSD.INR_DMG_IDNTF , FSD.SORT_MCHN_ETC , FSD.SHAP_SORT , FSD.MNFC_MCHN , FSD.PCKG_MCHN_GNRL , FSD.PCKG_MCHN_ATMTC , FSD.TOT_FRSH_CNV_MCHN , FSD.RBT_PLTZ , FSD.ETC_MNFC_MCHN_PRCS , FSD.ETC_MCHN , FSD.RMRK , FSD.MKR_NM , FSD.SORT_MCHN_HOLD_YN FROM TB_TE_APC_OPER_TRMT_ITEM OTI ,TB_TE_APC_FCLT_STTS_DTL FSD WHERE 1=1 AND OTI.APC_CD = FSD.APC_CD(+) AND OTI.CRTR_YR = FSD.CRTR_YR(+) AND OTI.SN = FSD.SN(+) AND OTI.OGNZ_SE_CD = '2' AND OTI.APC_CD IS NOT NULL
AND OTI.CRTR_YR = #{crtrYr}
AND OTI.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltGdsMchnInfoMapper.selectFcltGdsMchnInfoList */ OTI.CRTR_YR , OTI.APC_CD , OTI.APC_NM , OTI.ITEM_CD , OTI.ITEM_NM , OTI.SN
, FSD.SORT_MCHN_SPCFCT , FSD.SORT_BRCK_MCHN , FSD.COLOR_SORT , FSD.INR_DMG_IDNTF , FSD.SORT_MCHN_ETC , FSD.SHAP_SORT , FSD.MNFC_MCHN , FSD.PCKG_MCHN_GNRL , FSD.PCKG_MCHN_ATMTC , FSD.TOT_FRSH_CNV_MCHN , FSD.RBT_PLTZ , FSD.ETC_MNFC_MCHN_PRCS , FSD.ETC_MCHN , FSD.RMRK , FSD.MKR_NM , FSD.SORT_MCHN_HOLD_YN FROM TB_TE_APC_OPER_TRMT_ITEM OTI ,TB_TE_APC_FCLT_STTS_DTL FSD WHERE 1=1 AND OTI.APC_CD = FSD.APC_CD(+) AND OTI.CRTR_YR = FSD.CRTR_YR(+) AND OTI.SN = FSD.SN(+) AND OTI.OGNZ_SE_CD = '2' AND OTI.APC_CD IS NOT NULL
AND OTI.CRTR_YR = #{crtrYr}
AND OTI.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
MERGE INTO TB_TE_APC_FCLT_STTS_DTL FSD /* com.at.apcss.fm.fclt.mapper.FcltGdsMchnInfoMapper.insertFcltGdsMchnInfo */
USING DUAL
ON (
		FSD.CRTR_YR = #{crtrYr}
		AND FSD.APC_CD = #{apcCd}
		AND FSD.SN = #{sn}
	)
WHEN MATCHED THEN
  UPDATE SET
		  SORT_MCHN_SPCFCT 	= #{sortMchnSpcect}
		  , SORT_BRCK_MCHN 		= #{sortBrckMchn}
		  , COLOR_SORT 			= #{colorSort}
		  , SHAP_SORT 			= #{shapSort}
		  , MNFC_MCHN 			= #{mnfcMchn}
		  , MKR_NM	 			= #{mkrNm}
		  , SORT_MCHN_HOLD_YN	= #{sortMchnHoldYn}

		  , SYS_LAST_CHG_DT		= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		  CRTR_YR
		  , APC_CD
		  , APC_NM
		  , ITEM_CD
		  , ITEM_NM
		  , SORT_MCHN_SPCFCT
		  , SORT_BRCK_MCHN
		  , COLOR_SORT
		  , SHAP_SORT
		  , MNFC_MCHN
		  , MKR_NM
		  , SORT_MCHN_HOLD_YN

		  , SN

		  , SYS_FRST_INPT_DT
		  , SYS_FRST_INPT_USER_ID
		  , SYS_FRST_INPT_PRGRM_ID
		  , SYS_LAST_CHG_DT
		  , SYS_LAST_CHG_USER_ID
		  , SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
		  #{crtrYr}
		  , #{apcCd}
		  , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr})
		  , (SELECT
		  			ITEM_CD
		  		FROM  TB_TE_APC_OPER_TRMT_ITEM
		  		WHERE 1=1
		  		  AND APC_CD = #{apcCd}
		  		  AND CRTR_YR = #{crtrYr}
		  		  AND SN = #{sn}
		  		  AND OGNZ_SE_CD = '2'
		  	)
		  , (SELECT
		  			ITEM_NM
		  		FROM  TB_TE_APC_OPER_TRMT_ITEM
		  		WHERE 1=1
		  		  AND APC_CD = #{apcCd}
		  		  AND CRTR_YR = #{crtrYr}
		  		  AND SN = #{sn}
		  		  AND OGNZ_SE_CD = '2'
		  	)
		  , #{sortMchnSpcect}
		  , #{sortBrckMchn}
		  , #{colorSort}
		  , #{shapSort}
		  , #{mnfcMchn}
		  , #{mkrNm}
		  , #{sortMchnHoldYn}

		  , #{sn}

		  , SYSDATE
		  , #{sysFrstInptUserId}
		  , #{sysFrstInptPrgrmId}
		  , SYSDATE
		  , #{sysLastChgUserId}
		  , #{sysLastChgPrgrmId}
		)


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltGdsMchnInfoMapper.updateFcltGdsMchnInfo */
	TB_TE_APC_FCLT_STTS_DTL FSD
   SET
	  SORT_MCHN_SPCFCT 		= #{sortMchnSpcect}
	  , SORT_BRCK_MCHN 		= #{sortBrckMchn}
	  , COLOR_SORT 			= #{colorSort}
	  , SHAP_SORT 			= #{shapSort}
	  , MNFC_MCHN 			= #{mnfcMchn}
	  , MKR_NM	 			= #{mkrNm}
	  , SORT_MCHN_HOLD_YN	= #{sortMchnHoldYn}



	  , DEL_YN 	= #{delYn}


	  , SYS_LAST_CHG_DT			= SYSDATE
	  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

WHERE 1=1
  AND CRTR_YR = #{crtrYr}
  AND APC_CD = #{apcCd}
  AND SN = #{sn}


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltGdsMchnInfoMapper.deleteFcltGdsMchnInfo */
	TB_TE_APC_FCLT_STTS_DTL
   SET
	  DEL_YN 	= 'Y'
WHERE 1=1
  AND TRGT_YR = #{trgtYr}
  AND APC_CD = #{apcCd}
  AND SN = #{sn}
