mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltGdsMchnInfoMapper.selectFcltGdsMchnInfoList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SORT_MCHN_ITEM_CD AS SORT_MCHN_ITEM_CD , FCLT.SORT_MCHN_CD AS SORT_MCHN_CD , FCLT.SORT_MCHN_HLD_YN AS SORT_MCHN_HLD_YN
, FCLT.SORT_MCHN_HLD_YN11 AS SORT_MCHN_HLD_YN11 , FCLT.SORT_MCHN_SPCFCT1 AS SORT_MCHN_SPCFCT1 , FCLT.SORT_MCHN_HLD_YN12 AS SORT_MCHN_HLD_YN12 , FCLT.SORT_MCHN_HLD_YN13 AS SORT_MCHN_HLD_YN13 , FCLT.SORT_MCHN_HLD_YN14 AS SORT_MCHN_HLD_YN14 , FCLT.SORT_MCHN_HLD_YN15 AS SORT_MCHN_HLD_YN15
, FCLT.SORT_MCHN_HLD_YN21 AS SORT_MCHN_HLD_YN21 , FCLT.SORT_MCHN_SPCFCT2 AS SORT_MCHN_SPCFCT2 , FCLT.SORT_MCHN_HLD_YN22 AS SORT_MCHN_HLD_YN22 , FCLT.SORT_MCHN_HLD_YN23 AS SORT_MCHN_HLD_YN23 , FCLT.SORT_MCHN_HLD_YN24 AS SORT_MCHN_HLD_YN24 , FCLT.SORT_MCHN_HLD_YN25 AS SORT_MCHN_HLD_YN25
, FCLT.SORT_MCHN_HLD_YN31 AS SORT_MCHN_HLD_YN31 , FCLT.SORT_MCHN_SPCFCT3 AS SORT_MCHN_SPCFCT3 , FCLT.SORT_MCHN_HLD_YN32 AS SORT_MCHN_HLD_YN32 , FCLT.SORT_MCHN_HLD_YN33 AS SORT_MCHN_HLD_YN33 , FCLT.SORT_MCHN_HLD_YN34 AS SORT_MCHN_HLD_YN34 , FCLT.SORT_MCHN_HLD_YN35 AS SORT_MCHN_HLD_YN35
, FCLT.SORT_MCHN_HLD_YN41 AS SORT_MCHN_HLD_YN41 , FCLT.SORT_MCHN_SPCFCT4 AS SORT_MCHN_SPCFCT4 , FCLT.SORT_MCHN_HLD_YN42 AS SORT_MCHN_HLD_YN42 , FCLT.SORT_MCHN_HLD_YN43 AS SORT_MCHN_HLD_YN43 , FCLT.SORT_MCHN_HLD_YN44 AS SORT_MCHN_HLD_YN44 , FCLT.SORT_MCHN_HLD_YN45 AS SORT_MCHN_HLD_YN45 FROM TB_FCLT_GDS_MCHN_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

