		, STRG_PLC_LWTP_STRG_AREA
		, STRG_PLC_LWTP_STRG_RMRK
		, STRG_PLC_CA_STRG_PLC_AREA
		, STRG_PLC_CA_STRG_PLC_RMRK
		, STRG_PLC_CURN_AREA
