mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltItemPrfmncMapper.selectFcltItemPrfmnc */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.FCLT_ITEM_CD AS FCLT_ITEM_CD , FCLT.FCLT_ITEM_TRMT_AMT AS FCLT_ITEM_TRMT_AMT , FCLT.FCLT_ITEM_SPMT_RT AS FCLT_ITEM_SPMT_RT
, FCLT.FCLT_ITEM_SPMT_AMT AS FCLT_ITEM_SPMT_AMT , FCLT.FCLT_ITEM_SPMT_AMT2 AS FCLT_ITEM_SPMT_AMT2 , FCLT.FCLT_ITEM_SPMT_AMT3 AS FCLT_ITEM_SPMT_AMT3 , FCLT.FCLT_ITEM_SPMT_AMT4 AS FCLT_ITEM_SPMT_AMT4 , FCLT.FCLT_ITEM_SPMT_AMT5 AS FCLT_ITEM_SPMT_AMT5
, FCLT.FCLT_ITEM_TRMT_VLM AS FCLT_ITEM_TRMT_VLM , FCLT.FCLT_ITEM_TRMT_VLM2 AS FCLT_ITEM_TRMT_VLM2 , FCLT.FCLT_ITEM_TRMT_VLM3 AS FCLT_ITEM_TRMT_VLM3 , FCLT.FCLT_ITEM_TRMT_VLM4 AS FCLT_ITEM_TRMT_VLM4 , FCLT.FCLT_ITEM_TRMT_VLM5 AS FCLT_ITEM_TRMT_VLM5
, FCLT.FCLT_ITEM_MRKT_AMT AS FCLT_ITEM_MRKT_AMT , FCLT.FCLT_ITEM_MRKT_AMT2 AS FCLT_ITEM_MRKT_AMT2 , FCLT.FCLT_ITEM_MRKT_AMT3 AS FCLT_ITEM_MRKT_AMT3 , FCLT.FCLT_ITEM_MRKT_AMT4 AS FCLT_ITEM_MRKT_AMT4 , FCLT.FCLT_ITEM_MRKT_AMT5 AS FCLT_ITEM_MRKT_AMT5 FROM TB_FCLT_ITEM_PRFMNC FCLT WHERE 1=1
AND TRGT_YR = #{trgtYr}
AND APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltItemPrfmncMapper.selectFcltItemPrfmncList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.FCLT_ITEM_CD AS FCLT_ITEM_CD , FCLT.FCLT_ITEM_TRMT_AMT AS FCLT_ITEM_TRMT_AMT , FCLT.FCLT_ITEM_SPMT_RT AS FCLT_ITEM_SPMT_RT
, FCLT.FCLT_ITEM_SPMT_AMT AS FCLT_ITEM_SPMT_AMT , FCLT.FCLT_ITEM_SPMT_AMT2 AS FCLT_ITEM_SPMT_AMT2 , FCLT.FCLT_ITEM_SPMT_AMT3 AS FCLT_ITEM_SPMT_AMT3 , FCLT.FCLT_ITEM_SPMT_AMT4 AS FCLT_ITEM_SPMT_AMT4 , FCLT.FCLT_ITEM_SPMT_AMT5 AS FCLT_ITEM_SPMT_AMT5
, FCLT.FCLT_ITEM_TRMT_VLM AS FCLT_ITEM_TRMT_VLM , FCLT.FCLT_ITEM_TRMT_VLM2 AS FCLT_ITEM_TRMT_VLM2 , FCLT.FCLT_ITEM_TRMT_VLM3 AS FCLT_ITEM_TRMT_VLM3 , FCLT.FCLT_ITEM_TRMT_VLM4 AS FCLT_ITEM_TRMT_VLM4 , FCLT.FCLT_ITEM_TRMT_VLM5 AS FCLT_ITEM_TRMT_VLM5
, FCLT.FCLT_ITEM_MRKT_AMT AS FCLT_ITEM_MRKT_AMT , FCLT.FCLT_ITEM_MRKT_AMT2 AS FCLT_ITEM_MRKT_AMT2 , FCLT.FCLT_ITEM_MRKT_AMT3 AS FCLT_ITEM_MRKT_AMT3 , FCLT.FCLT_ITEM_MRKT_AMT4 AS FCLT_ITEM_MRKT_AMT4 , FCLT.FCLT_ITEM_MRKT_AMT5 AS FCLT_ITEM_MRKT_AMT5 FROM TB_FCLT_ITEM_PRFMNC FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
     	MERGE INTO TB_FCLT_ITEM_PRFMNC FCLT /* com.at.apcss.fm.fclt.mapper.FcltItemPrfmncMapper.insertFcltItemPrfmnc */
		USING DUAL
		ON (
			FCLT.TRGT_YR = #{trgtYr}
			AND FCLT.APC_CD = #{apcCd}
			)
		WHEN MATCHED THEN
		  UPDATE SET
				  FCLT_ITEM_CD         =  #{fcltItemCd}
    	        , FCLT_ITEM_TRMT_AMT   =  #{fcltItemTrmtAmt}
    	        , FCLT_ITEM_SPMT_RT    =  #{fcltItemSpmtRt}

    	        , FCLT_ITEM_SPMT_AMT   =  #{fcltItemSpmtAmt}
		    	, FCLT_ITEM_SPMT_AMT2  =  #{fcltItemSpmtAmt2}
		    	, FCLT_ITEM_SPMT_AMT3  =  #{fcltItemSpmtAmt3}
		    	, FCLT_ITEM_SPMT_AMT4  =  #{fcltItemSpmtAmt4}
		    	, FCLT_ITEM_SPMT_AMT5  =  #{fcltItemSpmtAmt5}

    	        , FCLT_ITEM_TRMT_VLM   =  #{fcltItemTrmtVlm}
		    	, FCLT_ITEM_TRMT_VLM2  =  #{fcltItemTrmtVlm2}
		    	, FCLT_ITEM_TRMT_VLM3  =  #{fcltItemTrmtVlm3}
		    	, FCLT_ITEM_TRMT_VLM4  =  #{fcltItemTrmtVlm4}
		    	, FCLT_ITEM_TRMT_VLM5  =  #{fcltItemTrmtVlm5}

		    	, FCLT_ITEM_MRKT_AMT   =  #{fcltItemMrktAmt}
		    	, FCLT_ITEM_MRKT_AMT2  =  #{fcltItemMrktAmt2}
		    	, FCLT_ITEM_MRKT_AMT3  =  #{fcltItemMrktAmt3}
		    	, FCLT_ITEM_MRKT_AMT4  =  #{fcltItemMrktAmt4}
		    	, FCLT_ITEM_MRKT_AMT5  =  #{fcltItemMrktAmt5}
				  , SYS_LAST_CHG_DT		= SYSDATE
	    		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
		WHEN NOT MATCHED THEN
		  INSERT (
				  TRGT_YR
	    		  , APC_CD
	    		  , FCLT_ITEM_CD
	    		  , FCLT_ITEM_TRMT_AMT
	    		  , FCLT_ITEM_SPMT_RT

	    		  , FCLT_ITEM_SPMT_AMT
			      , FCLT_ITEM_SPMT_AMT2
				  , FCLT_ITEM_SPMT_AMT3
				  , FCLT_ITEM_SPMT_AMT4
				  , FCLT_ITEM_SPMT_AMT5

	    		  , FCLT_ITEM_TRMT_VLM
				  , FCLT_ITEM_TRMT_VLM2
				  , FCLT_ITEM_TRMT_VLM3
				  , FCLT_ITEM_TRMT_VLM4
				  , FCLT_ITEM_TRMT_VLM5

				  , FCLT_ITEM_MRKT_AMT
				  , FCLT_ITEM_MRKT_AMT2
				  , FCLT_ITEM_MRKT_AMT3
				  , FCLT_ITEM_MRKT_AMT4
				  , FCLT_ITEM_MRKT_AMT5

	    		  , SYS_FRST_INPT_DT
	    		  , SYS_FRST_INPT_USER_ID
	    		  , SYS_FRST_INPT_PRGRM_ID
	    		  , SYS_LAST_CHG_DT
	    		  , SYS_LAST_CHG_USER_ID
	    		  , SYS_LAST_CHG_PRGRM_ID
	    		)
	   	VALUES
	   		    (
	   			  #{trgtYr}
	   			  , #{apcCd}
	   			  , #{fcltItemCd}
	   			  , #{fcltItemSpmtRt}
	   			  , #{fcltItemTrmtAmt}

	   			  , #{fcltItemSpmtAmt}
	   			  , #{fcltItemSpmtAmt2}
			   	  , #{fcltItemSpmtAmt3}
			   	  , #{fcltItemSpmtAmt4}
			   	  , #{fcltItemSpmtAmt5}

	   			  , #{fcltItemTrmtVlm}
			   	  , #{fcltItemTrmtVlm2}
			   	  , #{fcltItemTrmtVlm3}
			   	  , #{fcltItemTrmtVlm4}
			   	  , #{fcltItemTrmtVlm5}

			   	  , #{fcltItemMrktAmt}
			   	  , #{fcltItemMrktAmt2}
			   	  , #{fcltItemMrktAmt3}
			   	  , #{fcltItemMrktAmt4}
			   	  , #{fcltItemMrktAmt5}
	   			  , SYSDATE
	   			  , #{sysFrstInptUserId}
	   			  , #{sysFrstInptPrgrmId}
	   			  , SYSDATE
	   			  , #{sysLastChgUserId}
	   			  , #{sysLastChgPrgrmId}
		     	)


	    	UPDATE /* com.at.apcss.fm.fclt.mapper.FcltItemPrfmncMapper.updateFcltItemPrfmnc */
	    		TB_FCLT_ITEM_PRFMNC FCLT
			   SET
    	        FCLT_ITEM_CD         =  #{fcltItemCd}
    	        , FCLT_ITEM_TRMT_AMT   =  #{fcltItemTrmtAmt}
    	        , FCLT_ITEM_SPMT_RT    =  #{fcltItemSpmtRt}

    	        , FCLT_ITEM_SPMT_AMT   =  #{fcltItemSpmtAmt}
		    	, FCLT_ITEM_SPMT_AMT2  =  #{fcltItemSpmtAmt2}
		    	, FCLT_ITEM_SPMT_AMT3  =  #{fcltItemSpmtAmt3}
		    	, FCLT_ITEM_SPMT_AMT4  =  #{fcltItemSpmtAmt4}
		    	, FCLT_ITEM_SPMT_AMT5  =  #{fcltItemSpmtAmt5}

    	        , FCLT_ITEM_TRMT_VLM   =  #{fcltItemTrmtVlm}
		    	, FCLT_ITEM_TRMT_VLM2  =  #{fcltItemTrmtVlm2}
		    	, FCLT_ITEM_TRMT_VLM3  =  #{fcltItemTrmtVlm3}
		    	, FCLT_ITEM_TRMT_VLM4  =  #{fcltItemTrmtVlm4}
		    	, FCLT_ITEM_TRMT_VLM5  =  #{fcltItemTrmtVlm5}

		    	, FCLT_ITEM_MRKT_AMT   =  #{fcltItemMrktAmt}
		    	, FCLT_ITEM_MRKT_AMT2  =  #{fcltItemMrktAmt2}
		    	, FCLT_ITEM_MRKT_AMT3  =  #{fcltItemMrktAmt3}
		    	, FCLT_ITEM_MRKT_AMT4  =  #{fcltItemMrktAmt4}
		    	, FCLT_ITEM_MRKT_AMT5  =  #{fcltItemMrktAmt5}


				, DEL_YN 	= #{delYn}


				, SYS_LAST_CHG_DT		= SYSDATE
	    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    		, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

			 WHERE 1=1
		     AND TRGT_YR = #{trgtYr}
		     AND  APC_CD = #{apcCd}

mapper.delete:

    	DELETE /* com.at.apcss.fm.fclt.mapper.FcltItemPrfmncMapper.deleteFcltItemPrfmnc */
    		TB_FCLT_ITEM_PRFMNC FCLT
		 WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}
