mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltLwtpStrgMchnInfoMapper.selectFcltLwtpStrgMchnInfo */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.SN AS SN , FCLT.FCLT_CD AS FCLT_CD , FCLT.FCLT_HLD_YN AS FCLT_HLD_YN , FCLT.FCLT_PRCS_CD AS FCLT_PRCS_CD , FCLT.FCLT_PRCS_HR AS FCLT_PRCS_HR , FCLT.STOR_CAP AS STOR_CAP , FCLT.ST_STOR_PERFM AS ST_STOR_PERFM , FCLT.LT_STOR_PERFM AS LT_STOR_PERFM , FCLT.STOR_OP_RATE AS STOR_OP_RATE
FROM TB_FCLT_LWTP_STRG_MCHN_INFO FCLT WHERE 1=1
AND TRGT_YR = #{trgtYr}
AND APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltLwtpStrgMchnInfoMapper.selectFcltLwtpStrgMchnInfoList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.FCLT_CD AS FCLT_CD , FCLT.FCLT_HLD_YN AS FCLT_HLD_YN , FCLT.FCLT_PRCS_CD AS FCLT_PRCS_CD , FCLT.FCLT_PRCS_HR AS FCLT_PRCS_HR , FCLT.STOR_CAP AS STOR_CAP , FCLT.ST_STOR_PERFM AS ST_STOR_PERFM , FCLT.LT_STOR_PERFM AS LT_STOR_PERFM , FCLT.STOR_OP_RATE AS STOR_OP_RATE FROM TB_FCLT_LWTP_STRG_MCHN_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

 	INSERT /* com.at.apcss.fm.fclt.mapper.FcltLwtpStrgMchnInfoMapper.insertFcltLwtpStrgMchnInfo */
 	  INTO TB_FCLT_LWTP_STRG_MCHN_INFO FCLT
 		(
 			TRGT_YR
 		  , APC_CD
 		  , FCLT_CD
 		  , FCLT_HLD_YN
 		  , STOR_CAP
 		  , ST_STOR_PERFM
 		  , LT_STOR_PERFM
 		  , STOR_OP_RATE
 		  , SYS_FRST_INPT_DT
 		  , SYS_FRST_INPT_USER_ID
 		  , SYS_FRST_INPT_PRGRM_ID
 		  , SYS_LAST_CHG_DT
 		  , SYS_LAST_CHG_USER_ID
 		  , SYS_LAST_CHG_PRGRM_ID


 		)
		VALUES
			(
			    #{trgtYr}
			  , #{apcCd}
			  , #{fcltCd}
			  , #{fcltHldYn}
			  , #{storCap}
			  , #{stStorPerfm}
			  , #{ltStorPerfm}
			  , #{storOpRate}
			  , SYSDATE
			  , #{sysFrstInptUserId}
	          , #{sysFrstInptPrgrmId}
	          , SYSDATE
	          , #{sysLastChgUserId}
	          , #{sysLastChgPrgrmId}
			)

mapper.update:

	    	UPDATE /* com.at.apcss.fm.fclt.mapper.FcltLwtpStrgMchnInfoMapper.updateFcltLwtpStrgMchnInfo */
	    		TB_FCLT_LWTP_STRG_MCHN_INFO FCLT
			   SET
    	          FCLT_CD      = #{fcltCd}
    	          , FCLT_HLD_YN  = #{fcltHldYn}
    	          , FCLT_PRCS_CD = #{fcltPrcsCd}
    	          , FCLT_PRCS_HR = #{fcltPrcsHr}
 				  , STOR_CAP      =  #{storCap}
	    		  , ST_STOR_PERFM =  #{stStorPerfm}
	    		  , LT_STOR_PERFM =  #{ltStorPerfm}
	    		  , STOR_OP_RATE  =  #{storOpRate}



				, DEL_YN 	= #{delYn}


				, SYS_LAST_CHG_DT		= SYSDATE
	    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    		, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

			 WHERE 1=1
		      AND TRGT_YR = #{trgtYr}
		      AND APC_CD = #{apcCd}

mapper.delete:

    	DELETE /* com.at.apcss.fm.fclt.mapper.FcltLwtpStrgMchnInfoMapper.deleteFcltLwtpStrgMchnInfo */
    		TB_FCLT_LWTP_STRG_MCHN_INFO FCLT
		WHERE 1=1
	      AND TRGT_YR = #{trgtYr}
	      AND APC_CD = #{apcCd}
