mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltLwtpStrgMchnInfoMapper.selectFcltLwtpStrgMchnInfo */ OASP.CRTR_YR , OASP.APC_CD , OASP.APC_NM , OASP.STRG_PLC_ITEM_CD , OASP.STRG_PLC_ITEM_NM , OASP.LWTP_STRG_PLC_HLD_MTHD , OASP.LWTP_STRG_PLC_HLD_YN , OASP.STRG_PLC_STRG_ABLT , OASP.STRG_PLC_STRM_STRG_ABLT , OASP.STRG_PLC_LTRM_STRG_ABLT , OASP.STRG_PLC_OPRTNG_RT
, MOP.OPER_YN , MOP.OPER_PERIOD_YN_1 , MOP.OPER_PERIOD_YN_2 , MOP.OPER_PERIOD_YN_3 , MOP.OPER_PERIOD_YN_4 , MOP.OPER_PERIOD_YN_5 , MOP.OPER_PERIOD_YN_6 , MOP.OPER_PERIOD_YN_7 , MOP.OPER_PERIOD_YN_8 , MOP.OPER_PERIOD_YN_9 , MOP.OPER_PERIOD_YN_10 , MOP.OPER_PERIOD_YN_11 , MOP.OPER_PERIOD_YN_12
FROM TB_TE_APC_OPER_ABLT_STRG_PLC OASP ,(SELECT * FROM TB_TE_APC_MCHN_OPER_PERIOD WHERE 1=1 AND SN = 0 AND MCHN_SE_CD = '2' ) MOP WHERE 1=1 AND DEL_YN != 'Y' AND APC_CD IS NOT NULL AND OASP.APC_CD = MOP.APC_CD(+) AND OASP.CRTR_YR = MOP.CRTR_YR(+)
AND OASP.CRTR_YR = #{crtrYr}
AND OASP.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltLwtpStrgMchnInfoMapper.selectFcltLwtpStrgMchnInfoList */ OASP.CRTR_YR , OASP.APC_CD , OASP.APC_NM , OASP.STRG_PLC_ITEM_CD , OASP.STRG_PLC_ITEM_NM , OASP.LWTP_STRG_PLC_HLD_MTHD , OASP.LWTP_STRG_PLC_HLD_YN , OASP.STRG_PLC_STRG_ABLT , OASP.STRG_PLC_STRM_STRG_ABLT , OASP.STRG_PLC_LTRM_STRG_ABLT , OASP.STRG_PLC_OPRTNG_RT
, MOP.OPER_YN , MOP.OPER_PERIOD_YN_1 , MOP.OPER_PERIOD_YN_2 , MOP.OPER_PERIOD_YN_3 , MOP.OPER_PERIOD_YN_4 , MOP.OPER_PERIOD_YN_5 , MOP.OPER_PERIOD_YN_6 , MOP.OPER_PERIOD_YN_7 , MOP.OPER_PERIOD_YN_8 , MOP.OPER_PERIOD_YN_9 , MOP.OPER_PERIOD_YN_10 , MOP.OPER_PERIOD_YN_11 , MOP.OPER_PERIOD_YN_12
FROM TB_TE_APC_OPER_ABLT_STRG_PLC OASP ,(SELECT * FROM TB_TE_APC_MCHN_OPER_PERIOD WHERE 1=1 AND SN = 0 AND MCHN_SE_CD = '2' ) MOP WHERE 1=1 AND OASP.DEL_YN != 'Y' AND OASP.APC_CD IS NOT NULL AND OASP.APC_CD = MOP.APC_CD(+) AND OASP.CRTR_YR = MOP.CRTR_YR(+)
AND OASP.CRTR_YR = #{crtrYr}
AND OASP.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
MERGE INTO TB_TE_APC_OPER_ABLT_STRG_PLC OASP /* com.at.apcss.fm.fclt.mapper.FcltLwtpStrgMchnInfoMapper.insertFcltLwtpStrgMchnInfo */
USING DUAL
ON (
	OASP.CRTR_YR = #{crtrYr}
	AND OASP.APC_CD = #{apcCd}
	)
WHEN MATCHED THEN
  UPDATE SET
		  LWTP_STRG_PLC_HLD_MTHD 	= #{lwtpStrgPlcHldMthd}
		  , STRG_PLC_STRG_ABLT 		= #{strgPlcStrgAblt}
		  , STRG_PLC_STRM_STRG_ABLT = #{strgPlcStrmStrgAblt}
		  , STRG_PLC_LTRM_STRG_ABLT = #{strgPlcLtrmStrgAblt}
		  , STRG_PLC_OPRTNG_RT 		= #{strgPlcOprtngRt}

		  , LWTP_STRG_PLC_HLD_YN 	= #{lwtpStrgPlcHldYn}

		  , SYS_LAST_CHG_DT			= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		  CRTR_YR
		  , APC_CD
		  , APC_NM
		  , LWTP_STRG_PLC_HLD_MTHD
		  , STRG_PLC_STRG_ABLT
		  , STRG_PLC_STRM_STRG_ABLT
		  , STRG_PLC_LTRM_STRG_ABLT
		  , STRG_PLC_OPRTNG_RT

		  , LWTP_STRG_PLC_HLD_YN

		  , SYS_FRST_INPT_DT
		  , SYS_FRST_INPT_USER_ID
		  , SYS_FRST_INPT_PRGRM_ID
		  , SYS_LAST_CHG_DT
		  , SYS_LAST_CHG_USER_ID
		  , SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
		  #{crtrYr}
		  , #{apcCd}
		  , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr})

		  , #{lwtpStrgPlcHldMthd}
		  , #{strgPlcStrgAblt}
		  , #{strgPlcStrmStrgAblt}
		  , #{strgPlcLtrmStrgAblt}
		  , #{strgPlcOprtngRt}

		  , #{lwtpStrgPlcHldYn}

		  , SYSDATE
		  , #{sysFrstInptUserId}
		  , #{sysFrstInptPrgrmId}
		  , SYSDATE
		  , #{sysLastChgUserId}
		  , #{sysLastChgPrgrmId}
		)


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltLwtpStrgMchnInfoMapper.updateFcltLwtpStrgMchnInfo */
	TB_TE_APC_OPER_ABLT_STRG_PLC OASP
   SET
	  LWTP_STRG_PLC_HLD_MTHD 	= #{lwtpStrgPlcHldMthd}
	  , STRG_PLC_STRG_ABLT 		= #{strgPlcStrgAblt}
	  , STRG_PLC_STRM_STRG_ABLT = #{strgPlcStrmStrgAblt}
	  , STRG_PLC_LTRM_STRG_ABLT = #{strgPlcLtrmStrgAblt}
	  , STRG_PLC_OPRTNG_RT 		= #{strgPlcOprtngRt}

	  , LWTP_STRG_PLC_HLD_YN 	= #{lwtpStrgPlcHldYn}



	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

WHERE 1=1
  AND CRTR_YR = #{crtrYr}
  AND APC_CD = #{apcCd}

MERGE INTO TB_TE_APC_MCHN_OPER_PERIOD AMOP /* com.at.apcss.fm.fclt.mapper.FcltLwtpStrgMchnInfoMapper.insertFcltLwtpStrgMchnInfoPeriod */
USING DUAL
ON (
	AMOP.CRTR_YR = #{crtrYr}
	AND AMOP.APC_CD = #{apcCd}
	AND AMOP.SN = '0'
	AND AMOP.MCHN_SE_CD = '2'
	)
WHEN MATCHED THEN
  UPDATE SET
		  OPER_YN				= #{operYn}
		  , OPER_PERIOD_YN_1	= #{operPeriodYn1}
		  , OPER_PERIOD_YN_2	= #{operPeriodYn2}
		  , OPER_PERIOD_YN_3	= #{operPeriodYn3}
		  , OPER_PERIOD_YN_4	= #{operPeriodYn4}
		  , OPER_PERIOD_YN_5	= #{operPeriodYn5}
		  , OPER_PERIOD_YN_6	= #{operPeriodYn6}
		  , OPER_PERIOD_YN_7	= #{operPeriodYn7}
		  , OPER_PERIOD_YN_8	= #{operPeriodYn8}
		  , OPER_PERIOD_YN_9	= #{operPeriodYn9}
		  , OPER_PERIOD_YN_10	= #{operPeriodYn10}
		  , OPER_PERIOD_YN_11	= #{operPeriodYn11}
		  , OPER_PERIOD_YN_12	= #{operPeriodYn12}

		  , SYS_LAST_CHG_DT			= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		CRTR_YR
		, APC_CD
		, SN
		, MCHN_SE_CD
		, OPER_YN
		, OPER_PERIOD_YN_1
		, OPER_PERIOD_YN_2
		, OPER_PERIOD_YN_3
		, OPER_PERIOD_YN_4
		, OPER_PERIOD_YN_5
		, OPER_PERIOD_YN_6
		, OPER_PERIOD_YN_7
		, OPER_PERIOD_YN_8
		, OPER_PERIOD_YN_9
		, OPER_PERIOD_YN_10
		, OPER_PERIOD_YN_11
		, OPER_PERIOD_YN_12

		, SYS_FRST_INPT_DT
		, SYS_FRST_INPT_USER_ID
		, SYS_FRST_INPT_PRGRM_ID
		, SYS_LAST_CHG_DT
		, SYS_LAST_CHG_USER_ID
		, SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
		  #{crtrYr}
		  , #{apcCd}
		  , '0'
		  , '2'
		  , #{operYn}
		  , #{operPeriodYn1}
