mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltLwtpStrgMchnOperInfoMapper.selectFcltLwtpStrgMchnOperInfo */ FCLT.TRGT_YR AS TRGT_YR , FCLT.SN AS SN , FCLT.FCLT_CD AS FCLT_CD , FCLT.SORT_MCHN_OPER_CD AS SORT_MCHN_OPER_CD , FCLT.SORT_MCHN_OPER_YN AS SORT_MCHN_OPER_YN , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_FCLT_LWTP_STRG_MCHN_OPER_INFO FCLT WHERE 1=1
AND TRGT_YR = #{trgtYr}
AND APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
FCLT.TRGT_YR AS TRGT_YR , FCLT.SN AS SN , FCLT.FCLT_CD AS FCLT_CD , FCLT.SORT_MCHN_OPER_CD AS SORT_MCHN_OPER_CD , FCLT.SORT_MCHN_OPER_YN AS SORT_MCHN_OPER_YN , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_FCLT_LWTP_STRG_MCHN_OPER_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

