mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltOgnzItemPrfmncMapper.selectFcltOgnzItemPrfmnc */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.FCLT_OGNZ_TRMT_AMT AS FCLT_OGNZ_TRMT_AMT , FCLT.FCLT_OGNZ_TRMT_VLM AS FCLT_OGNZ_TRMT_VLM , FCLT.FCLT_OGNZ_TRMT_AMT2 AS FCLT_OGNZ_TRMT_AMT2 , FCLT.FCLT_OGNZ_TRMT_AMT3 AS FCLT_OGNZ_TRMT_AMT3 , FCLT.FCLT_OGNZ_TRMT_AMT4 AS FCLT_OGNZ_TRMT_AMT4 , FCLT.FCLT_OGNZ_TRMT_VLM2 AS FCLT_OGNZ_TRMT_VLM2 , FCLT.FCLT_OGNZ_TRMT_VLM3 AS FCLT_OGNZ_TRMT_VLM3 , FCLT.FCLT_OGNZ_TRMT_VLM4 AS FCLT_OGNZ_TRMT_VLM4
FROM TB_FCLT_OGNZ_ITEM_PRFMNC FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltOgnzItemPrfmncMapper.selectFcltOgnzItemPrfmncList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.FCLT_OGNZ_TRMT_AMT AS FCLT_OGNZ_TRMT_AMT , FCLT.FCLT_OGNZ_TRMT_VLM AS FCLT_OGNZ_TRMT_VLM , FCLT.FCLT_OGNZ_TRMT_AMT2 AS FCLT_OGNZ_TRMT_AMT2 , FCLT.FCLT_OGNZ_TRMT_AMT3 AS FCLT_OGNZ_TRMT_AMT3 , FCLT.FCLT_OGNZ_TRMT_AMT4 AS FCLT_OGNZ_TRMT_AMT4 , FCLT.FCLT_OGNZ_TRMT_VLM2 AS FCLT_OGNZ_TRMT_VLM2 , FCLT.FCLT_OGNZ_TRMT_VLM3 AS FCLT_OGNZ_TRMT_VLM3 , FCLT.FCLT_OGNZ_TRMT_VLM4 AS FCLT_OGNZ_TRMT_VLM4
FROM TB_FCLT_OGNZ_ITEM_PRFMNC FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
     	MERGE INTO TB_FCLT_OGNZ_ITEM_PRFMNC FCLT /* com.at.apcss.fm.fclt.mapper.FcltOgnzItemPrfmncMapper.insertFcltOgnzItemPrfmnc */
		USING DUAL
		ON (
			FCLT.TRGT_YR = #{trgtYr}
			AND FCLT.APC_CD = #{apcCd}
			)
		WHEN MATCHED THEN
		  UPDATE SET
				  FCLT_OGNZ_TRMT_AMT  = #{fcltOgnzTrmtAmt}
    	        , FCLT_OGNZ_TRMT_AMT2  = #{fcltOgnzTrmtAmt2}
    	        , FCLT_OGNZ_TRMT_AMT3  = #{fcltOgnzTrmtAmt3}
    	        , FCLT_OGNZ_TRMT_AMT4  = #{fcltOgnzTrmtAmt4}
			    , FCLT_OGNZ_TRMT_VLM  = #{fcltOgnzTrmtVlm}
    	        , FCLT_OGNZ_TRMT_VLM2  = #{fcltOgnzTrmtVlm2}
    	        , FCLT_OGNZ_TRMT_VLM3  = #{fcltOgnzTrmtVlm3}
    	        , FCLT_OGNZ_TRMT_VLM4  = #{fcltOgnzTrmtVlm4}
				  , SYS_LAST_CHG_DT		= SYSDATE
	    		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
		WHEN NOT MATCHED THEN
		  INSERT (
				  TRGT_YR
	    		  , APC_CD
	    		  , FCLT_OGNZ_TRMT_AMT
	    		  , FCLT_OGNZ_TRMT_AMT2
				  , FCLT_OGNZ_TRMT_AMT3
				  , FCLT_OGNZ_TRMT_AMT4
	    		  , FCLT_OGNZ_TRMT_VLM
				  , FCLT_OGNZ_TRMT_VLM2
				  , FCLT_OGNZ_TRMT_VLM3
				  , FCLT_OGNZ_TRMT_VLM4
				  , SYS_FRST_INPT_DT
	    		  , SYS_FRST_INPT_USER_ID
	    		  , SYS_FRST_INPT_PRGRM_ID
	    		  , SYS_LAST_CHG_DT
	    		  , SYS_LAST_CHG_USER_ID
	    		  , SYS_LAST_CHG_PRGRM_ID
	    		)
	   	VALUES
	   		    (
	   			  #{trgtYr}
	   			  , #{apcCd}
	   			  , #{fcltOgnzTrmtAmt}
          	   	  , #{fcltOgnzTrmtAmt2}
          		  , #{fcltOgnzTrmtAmt3}
          		  , #{fcltOgnzTrmtAmt4}
	   			  , #{fcltOgnzTrmtVlm}
          		  , #{fcltOgnzTrmtVlm2}
          		  , #{fcltOgnzTrmtVlm3}
          		  , #{fcltOgnzTrmtVlm4}
          		  , SYSDATE
	   			  , #{sysFrstInptUserId}
	   			  , #{sysFrstInptPrgrmId}
                  , SYSDATE
                  , #{sysLastChgUserId}
                  , #{sysLastChgPrgrmId}
		     	)


	    	UPDATE /* com.at.apcss.fm.fclt.mapper.FcltOgnzItemPrfmncMapper.updateFcltOgnzItemPrfmnc */
	    		TB_FCLT_OGNZ_ITEM_PRFMNC FCLT
			   SET
			 	FCLT_OGNZ_TRMT_AMT  = #{fcltOgnzTrmtAmt}
    	        , FCLT_OGNZ_TRMT_AMT2  = #{fcltOgnzTrmtAmt2}
    	        , FCLT_OGNZ_TRMT_AMT3  = #{fcltOgnzTrmtAmt3}
    	        , FCLT_OGNZ_TRMT_AMT4  = #{fcltOgnzTrmtAmt4}
			    , FCLT_OGNZ_TRMT_VLM  = #{fcltOgnzTrmtVlm}
    	        , FCLT_OGNZ_TRMT_VLM2  = #{fcltOgnzTrmtVlm2}
    	        , FCLT_OGNZ_TRMT_VLM3  = #{fcltOgnzTrmtVlm3}
    	        , FCLT_OGNZ_TRMT_VLM4  = #{fcltOgnzTrmtVlm4}


				, DEL_YN 	= #{delYn}


				, SYS_LAST_CHG_DT		= SYSDATE
	    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    		, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

			 WHERE 1=1
		     AND TRGT_YR = #{trgtYr}
		     AND  APC_CD = #{apcCd}

mapper.delete:

    	DELETE /* com.at.apcss.fm.fclt.mapper.FcltOgnzItemPrfmncMapper.deleteFcltOgnzItemPrfmnc */
    		TB_FCLT_OGNZ_ITEM_PRFMNC FCLT
		 WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}
