mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltOgnzPrcsPrfmncMapper.selectFcltOgnzPrcsPrfmnc */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.FCLT_OGNZ_TRMT_AMT AS FCLT_OGNZ_TRMT_AMT , FCLT.FCLT_OGNZ_TRMT2_AMT AS FCLT_OGNZ_TRMT2_AMT , FCLT.FCLT_OGNZ_TRMT3_AMT AS FCLT_OGNZ_TRMT3_AMT , FCLT.FCLT_OGNZ_TRMT4_AMT AS FCLT_OGNZ_TRMT4_AMT
FROM TB_FCLT_OGNZ_PRCS_PRFMNC FCLT WHERE 1=1
AND TRGT_YR = #{trgtYr}
AND APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltOgnzPrcsPrfmncMapper.selectFcltOgnzPrcsPrfmncList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.FCLT_OGNZ_TRMT_AMT AS FCLT_OGNZ_TRMT_AMT , FCLT.FCLT_OGNZ_TRMT2_AMT AS FCLT_OGNZ_TRMT2_AMT , FCLT.FCLT_OGNZ_TRMT3_AMT AS FCLT_OGNZ_TRMT3_AMT , FCLT.FCLT_OGNZ_TRMT4_AMT AS FCLT_OGNZ_TRMT4_AMT FROM TB_FCLT_OGNZ_PRCS_PRFMNC FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

