mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltOgnzPrcsPrfmncMapper.selectFcltOgnzPrcsPrfmnc */ OTI.CRTR_YR , OTI.APC_CD ,(SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = OTI.APC_CD) AS APC_NM , OTI.SN , OTI.ITEM_CD , OTI.ITEM_NM
, OPD.RTL_OGNZ_TOT_TRMT_AMT , OPD.RTL_OGNZ_TOT_TRMT_VLM , OPD.RTL_OGNZ_GNRL_SUM , OPD.RTL_OGNZ_GNRL_SMPL_TRST , OPD.RTL_OGNZ_GNRL_SMPL_EMSPAP , OPD.RTL_OGNZ_OGNZ_SUM , OPD.RTL_OGNZ_OGNZ_CPRTN_SORT_TRST , OPD.RTL_OGNZ_OGNZ_CTRT_EMSPAP
FROM TB_TE_APC_OPER_TRMT_ITEM OTI ,TB_TE_APC_OPER_PRFMNC_DTL OPD WHERE 1=1 AND OTI.APC_CD = OPD.APC_CD(+) AND OTI.CRTR_YR = OPD.CRTR_YR(+) AND OTI.SN = OPD.SN(+) AND OTI.OGNZ_SE_CD = '1' AND OTI.APC_CD IS NOT NULL
AND OTI.CRTR_YR = #{crtrYr}
AND OTI.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltOgnzPrcsPrfmncMapper.selectFcltOgnzPrcsPrfmncList */ OTI.CRTR_YR , OTI.APC_CD ,(SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = OTI.APC_CD) AS APC_NM , OTI.SN , OTI.ITEM_CD , OTI.ITEM_NM
, OPD.RTL_OGNZ_TOT_TRMT_AMT , OPD.RTL_OGNZ_TOT_TRMT_VLM , OPD.RTL_OGNZ_GNRL_SUM , OPD.RTL_OGNZ_GNRL_SMPL_TRST , OPD.RTL_OGNZ_GNRL_SMPL_EMSPAP , OPD.RTL_OGNZ_OGNZ_SUM , OPD.RTL_OGNZ_OGNZ_CPRTN_SORT_TRST , OPD.RTL_OGNZ_OGNZ_CTRT_EMSPAP
FROM TB_TE_APC_OPER_TRMT_ITEM OTI ,TB_TE_APC_OPER_PRFMNC_DTL OPD WHERE 1=1 AND OTI.APC_CD = OPD.APC_CD(+) AND OTI.CRTR_YR = OPD.CRTR_YR(+) AND OTI.SN = OPD.SN(+) AND OTI.OGNZ_SE_CD = '1' AND OTI.APC_CD IS NOT NULL
AND OTI.CRTR_YR = #{crtrYr}
AND OTI.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
MERGE INTO TB_TE_APC_OPER_PRFMNC_DTL OPD /* com.at.apcss.fm.fclt.mapper.FcltOgnzPrcsPrfmncMapper.insertFcltOgnzPrcsPrfmnc */
USING DUAL
ON (
	OPD.CRTR_YR = #{crtrYr}
	AND OPD.APC_CD = #{apcCd}
	AND OPD.SN = #{sn}
	)
WHEN MATCHED THEN
  UPDATE SET
		  RTL_OGNZ_TOT_TRMT_AMT				= #{rtlOgnzTotTrmtAmt}
		  , RTL_OGNZ_TOT_TRMT_VLM			= #{rtlOgnzTotTrmtVlm}
		  , RTL_OGNZ_GNRL_SUM				= #{rtlOgnzGnrlSum}
		  , RTL_OGNZ_GNRL_SMPL_TRST			= #{rtlOgnzGnrlSmplTrst}
		  , RTL_OGNZ_GNRL_SMPL_EMSPAP		= #{rtlOgnzGnrlSmplEmspap}
		  , RTL_OGNZ_OGNZ_SUM				= #{rtlOgnzOGnzSum}
		  , RTL_OGNZ_OGNZ_CPRTN_SORT_TRST	= #{rtlOgnzOgnzCprtnSortTrst}
		  , RTL_OGNZ_OGNZ_CTRT_EMSPAP		= #{rtlOgnzOgnzCtrtEmspap}

		  , SYS_LAST_CHG_DT			= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		  CRTR_YR
		  , APC_CD
		  , APC_NM
		  , SN

		  , ITEM_CD
		  , ITEM_NM

		  , RTL_OGNZ_TOT_TRMT_AMT
		  , RTL_OGNZ_TOT_TRMT_VLM
		  , RTL_OGNZ_GNRL_SUM
		  , RTL_OGNZ_GNRL_SMPL_TRST
		  , RTL_OGNZ_GNRL_SMPL_EMSPAP
		  , RTL_OGNZ_OGNZ_SUM
		  , RTL_OGNZ_OGNZ_CPRTN_SORT_TRST
		  , RTL_OGNZ_OGNZ_CTRT_EMSPAP

		  , SYS_FRST_INPT_DT
		  , SYS_FRST_INPT_USER_ID
		  , SYS_FRST_INPT_PRGRM_ID
		  , SYS_LAST_CHG_DT
		  , SYS_LAST_CHG_USER_ID
		  , SYS_LAST_CHG_PRGRM_ID
		)
VALUES
	    (
		  #{crtrYr}
		  , #{apcCd}
		  , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr})
		  , #{sn}

		  ,(SELECT ITEM_CD FROM TB_TE_APC_OPER_TRMT_ITEM WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr} AND SN = #{sn} AND OGNZ_SE_CD = '2')
		  ,(SELECT ITEM_NM FROM TB_TE_APC_OPER_TRMT_ITEM WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr} AND SN = #{sn} AND OGNZ_SE_CD = '2')

		  , #{rtlOgnzTotTrmtAmt}
		  , #{rtlOgnzTotTrmtVlm}
		  , #{rtlOgnzGnrlSum}
		  , #{rtlOgnzGnrlSmplTrst}
		  , #{rtlOgnzGnrlSmplEmspap}
		  , #{rtlOgnzOGnzSum}
		  , #{rtlOgnzOgnzCprtnSortTrst}
		  , #{rtlOgnzOgnzCtrtEmspap}

		  , SYSDATE
		  , #{sysFrstInptUserId}
		  , #{sysFrstInptPrgrmId}
		  , SYSDATE
		  , #{sysLastChgUserId}
		  , #{sysLastChgPrgrmId}
		)


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltOgnzPrcsPrfmncMapper.updateFcltOgnzPrcsPrfmnc */
	TB_TE_APC_OPER_PRFMNC_DTL OPD
   SET
 	RTL_OGNZ_TOT_TRMT_AMT			= #{rtlOgnzTotTrmtAmt}
	, RTL_OGNZ_TOT_TRMT_VLM			= #{rtlOgnzTotTrmtVlm}
	, RTL_OGNZ_GNRL_SUM				= #{rtlOgnzGnrlSum}
	, RTL_OGNZ_GNRL_SMPL_TRST		= #{rtlOgnzGnrlSmplTrst}
	, RTL_OGNZ_GNRL_SMPL_EMSPAP	 	= #{rtlOgnzGnrlSmplEmspap}
	, RTL_OGNZ_OGNZ_SUM				= #{rtlOgnzOGnzSum}}
	, RTL_OGNZ_OGNZ_CPRTN_SORT_TRST	= #{rtlOgnzOgnzCprtnSortTrst}
	, RTL_OGNZ_OGNZ_CTRT_EMSPAP		= #{rtlOgnzOgnzCtrtEmspap}



	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

 WHERE 1=1
  AND TRGT_YR 	= #{crtrYr}
  AND APC_CD 	= #{apcCd}
  AND SN 		= #{sn}
