mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltOperHfInfoMapper.selectFcltOperHfInfo */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SN AS SN , FCLT.HIRE_SE_CD AS HIRE_SE_CD , FCLT.CTZN_FRGNR_SE_CD AS CTZN_FRGNR_SE_CD , FCLT.HIRE_NOPE AS HIRE_NOPE , FCLT.HIRE_NOPE2 AS HIRE_NOPE2 , FCLT.HIRE_NOPE3 AS HIRE_NOPE3 , FCLT.HIRE_NOPE4 AS HIRE_NOPE4 , (HIRE_NOPE+HIRE_NOPE2+HIRE_NOPE3+HIRE_NOPE4) AS HIRE_NOPE0 , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_FCLT_OPER_HF_INFO FCLT WHERE 1=1
AND TRGT_YR = #{trgtYr}
AND APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltOperHfInfoMapper.selectFcltOperHfInfo */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SN AS SN , FCLT.HIRE_SE_CD AS HIRE_SE_CD , FCLT.CTZN_FRGNR_SE_CD AS CTZN_FRGNR_SE_CD , FCLT.HIRE_NOPE AS HIRE_NOPE , FCLT.HIRE_NOPE2 AS HIRE_NOPE2 , FCLT.HIRE_NOPE3 AS HIRE_NOPE3 , FCLT.HIRE_NOPE4 AS HIRE_NOPE4 , (HIRE_NOPE+HIRE_NOPE2+HIRE_NOPE3+HIRE_NOPE4) AS HIRE_NOPE0 , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_FCLT_OPER_HF_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

	    	INSERT /* com.at.apcss.fm.fclt.mapper.FcltOperHfInfoMapper.insertFcltOperHfInfo */
