mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltOperHfInfoMapper.selectFcltOperHfInfo */ APC_CD ,APC_NM ,CRTR_YR ,HIRE_RGLLBR_OFC ,HIRE_RGLLBR_SPT
,HIRE_TMPR_WG_TOT_SUM ,HIRE_TMPR_AVG_WG ,HIRE_TMPR_MIN ,HIRE_TMPR_MAX
,HIRE_FRGNR_MIN ,HIRE_FRGNR_MAX ,HIRE_FRGNR_AVG ,HIRE_FRGNR_TASK_CN
FROM TB_TE_APC_OPER_ABLT AOA WHERE 1=1 AND DEL_YN != 'Y' AND APC_CD IS NOT NULL
AND CRTR_YR = #{crtrYr}
AND APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltOperHfInfoMapper.selectFcltOperHfInfoList */ APC_CD ,APC_NM ,CRTR_YR ,HIRE_RGLLBR_OFC ,HIRE_RGLLBR_SPT
,HIRE_TMPR_WG_TOT_SUM ,HIRE_TMPR_AVG_WG ,HIRE_TMPR_MIN ,HIRE_TMPR_MAX
,HIRE_FRGNR_MIN ,HIRE_FRGNR_MAX ,HIRE_FRGNR_AVG ,HIRE_FRGNR_TASK_CN
FROM TB_TE_APC_OPER_ABLT AOA WHERE 1=1 AND DEL_YN != 'Y' AND APC_CD IS NOT NULL
AND CRTR_YR = #{crtrYr}
AND APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
MERGE INTO TB_TE_APC_OPER_ABLT AOA /* com.at.apcss.fm.fclt.mapper.FcltOperHfInfoMapper.insertFcltOperHfInfo */
USING DUAL
ON (
	AOA.CRTR_YR = #{crtrYr}
	AND AOA.APC_CD = #{apcCd}
	)
WHEN MATCHED THEN
  UPDATE SET
		  HIRE_RGLLBR_OFC			= #{hireRgllbrOfc}
		  , HIRE_RGLLBR_SPT			= #{hireRgllbrSpt}

		  , HIRE_TMPR_WG_TOT_SUM	= #{hireTmprWgTotSum}
		  , HIRE_TMPR_AVG_WG		= #{hireTmprAvgWg}
		  , HIRE_TMPR_MIN			= #{hireTmprMin}
		  , HIRE_TMPR_MAX			= #{hireTmprMax}

		  , HIRE_FRGNR_MIN			= #{hireFrgnrMin}
		  , HIRE_FRGNR_MAX			= #{hireFrgnrMax}
		  , HIRE_FRGNR_AVG			= #{hireFrgnrAvg}
		  , HIRE_FRGNR_TASK_CN		= #{hireFrgnrTaskCn}

		  , TMPR_STRG_YN			= #{tmprStrgYn}

		  , SYS_LAST_CHG_DT			= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		  CRTR_YR
		  , APC_CD
		  , APC_NM
		  , HIRE_RGLLBR_OFC
		  , HIRE_RGLLBR_SPT

		  , HIRE_TMPR_WG_TOT_SUM
		  , HIRE_TMPR_AVG_WG
		  , HIRE_TMPR_MIN
		  , HIRE_TMPR_MAX

		  , HIRE_FRGNR_MIN
		  , HIRE_FRGNR_MAX
		  , HIRE_FRGNR_AVG
		  , HIRE_FRGNR_TASK_CN

		  , TMPR_STRG_YN

		  , SYS_FRST_INPT_DT
		  , SYS_FRST_INPT_USER_ID
		  , SYS_FRST_INPT_PRGRM_ID
		  , SYS_LAST_CHG_DT
		  , SYS_LAST_CHG_USER_ID
		  , SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
		  #{crtrYr}
		  , #{apcCd}
		  , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr})
		  , #{hireRgllbrOfc}
		  , #{hireRgllbrSpt}

		  , #{hireTmprWgTotSum}
		  , #{hireTmprAvgWg}
		  , #{hireTmprMin}
		  , #{hireTmprMax}

		  , #{hireFrgnrMin}
		  , #{hireFrgnrMax}
		  , #{hireFrgnrAvg}
		  , #{hireFrgnrTaskCn}

		  , #{tmprStrgYn}

		  , SYSDATE
		  , #{sysFrstInptUserId}
		  , #{sysFrstInptPrgrmId}
		  , SYSDATE
		  , #{sysLastChgUserId}
		  , #{sysLastChgPrgrmId}
		)


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltOperHfInfoMapper.updateFcltOperHfInfo */
	TB_TE_APC_OPER_ABLT AOA
  SET
	HIRE_RGLLBR_OFC			= #{hireRgllbrOfc}
	, HIRE_RGLLBR_SPT		= #{hireRgllbrSpt}

	, HIRE_TMPR_WG_TOT_SUM	= #{hireTmprWgTotSum}
	, HIRE_TMPR_AVG_WG		= #{hireTmprAvgWg}
	, HIRE_TMPR_MIN			= #{hireTmprMin}
	, HIRE_TMPR_MAX			= #{hireTmprMax}

	, HIRE_FRGNR_MIN		= #{hireFrgnrMin}
	, HIRE_FRGNR_MAX		= #{hireFrgnrMax}
	, HIRE_FRGNR_AVG		= #{hireFrgnrAvg}
	, HIRE_FRGNR_TASK_CN	= #{hireFrgnrTaskCn}

	, TMPR_STRG_YN			= #{tmprStrgYn}


	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

WHERE 1=1
  AND CRTR_YR = #{trgtYr}
  AND APC_CD = #{apcCd}
