mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltOperHfInfoMapper.selectFcltOperHfInfo */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , FCLT.SN AS SN , FCLT.HIRE_SE_CD AS HIRE_SE_CD , FCLT.CTZN_FRGNR_SE_CD AS CTZN_FRGNR_SE_CD , FCLT.HIRE_NOPE AS HIRE_NOPE , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM USRFM.TB_FCLT_OPER_HF_INFO FCLT WHERE 1=1 AND TRGT_YR = #{trgtYr} AND APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

	    	INSERT /* com.at.apcss.fm.fclt.mapper.FcltOperHfInfoMapper.insertFcltOperHfInfo */
	    	  INTO USRFM.TB_FCLT_OPER_HF_INFO FCLT
	    		(
	    			TRGT_YR
	    		  , APC_CD
	    		  , SN
	    		  , HIRE_SE_CD
	    		  , CTZN_FRGNR_SE_CD
	    		  , HIRE_NOPE
	    		  , DEL_YN
	    		  , SYS_FRST_INPT_DT
	    		  , SYS_FRST_INPT_USER_ID
	    		  , SYS_FRST_INPT_PRGRM_ID
	    	      , SYS_LAST_CHG_DT
	    	      , SYS_LAST_CHG_USER_ID
	    	      , SYS_LAST_CHG_PRGRM_ID
	    		)
	   		VALUES
	   			(
	   			    #{TRGT_YR}
	   			  , #{APC_CD}
	   			  , #{SN}
	   			  , #{HIRE_SE_CD}
	   			  , #{CTZN_FRGNR_SE_CD}
	   			  , #{HIRE_NOPE}
	   			  , #{DEL_YN}
	   			  , #{SYS_FRST_INPT_DT}
	   			  , #{SYS_FRST_INPT_USER_ID}
	   			  , #{SYS_FRST_INPT_PRGRM_ID}
	   			  , #{SYS_LAST_CHG_DT}
    	          , #{SYS_LAST_CHG_USER_ID}
    	          , #{SYS_LAST_CHG_PRGRM_ID}
    			)

mapper.update:

    	UPDATE /* com.at.apcss.fm.fclt.mapper.FcltOperHfInfoMapper.updateFcltOperHfInfo */
    		USRFM.TB_FCLT_OPER_HF_INFO FCLT
		   SET
		 	  TRGT_YR          = #{TRGT_YR}
		 	, APC_CD           = #{APC_CD}
		 	, SN               = #{SN}
		 	, HIRE_SE_CD       = #{HIRE_SE_CD}
		 	, CTZN_FRGNR_SE_CD = #{CTZN_FRGNR_SE_CD}
		 	, HIRE_NOPE        = #{HIRE_NOPE}


			, DEL_YN 	= #{delYn}


			, SYS_LAST_CHG_DT		= SYSDATE
    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

		 WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}

mapper.delete:

    	DELETE /* com.at.apcss.fm.fclt.mapper.FcltOperHfInfoMapper.deleteFcltOperHfInfo */
    		USRFM.TB_FCLT_OPER_HF_INFO FCLT
		WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}
