mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltOperInfoMapper.selectFcltOperInfoList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD FCLT.DADDR AS DADDR , FCLT.APC_BRNO AS APC_BRNO , FCLT.APC_RGN_CD AS APC_RGN_CD , FCLT.APC_NM AS APC_NM , FCLT.APC_ADDR AS APC_ADDR , FCLT.PSN_OGNZ_CD AS PSN_OGNZ_CD , FCLT.PSN_OGNZ_NM AS PSN_OGNZ_NM , FCLT.PSN_OGNZ_BRNO AS PSN_OGNZ_BRNO , FCLT.OPER_OGNZ_CD AS OPER_OGNZ_CD , FCLT.OPER_OGNZ_NM AS OPER_OGNZ_NM , FCLT.OPER_OGNZ_BRNO AS OPER_OGNZ_BRNO , FCLT.OPER_OGNZ_RPRSV AS OPER_OGNZ_RPRSV , FCLT.OPER_OGNZ_ADDR AS OPER_OGNZ_ADDR , FCLT.OPER_OGNZ_DEPT_CD AS OPER_OGNZ_DEPT_CD , FCLT.OPER_OGNZ_DEPT_NM AS OPER_OGNZ_DEPT_NM , FCLT.OPER_OGNZ_TRMT_ITEM_CD AS OPER_OGNZ_TRMT_ITEM_CD , FCLT.OPER_OGNZ_TRMT_ITEM_CN AS OPER_OGNZ_TRMT_ITEM_CN , FCLT.APC_TRMT_ITEM_CD AS APC_TRMT_ITEM_CD , FCLT.APC_TRMT_ITEM_CN AS APC_TRMT_ITEM_CN , FCLT.DEL_YN AS DEL_YN , FCLT.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT , FCLT.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID , FCLT.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID , FCLT.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT , FCLT.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID , FCLT.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID FROM TB_FCLT_TB_FCLT_OPER_INFO FCLT WHERE 1=1 AND TRGT_YR = #{trgtYr} AND APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

