mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltPrcsNtslInfoMapper.selectFcltPrcsNtslInfo */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.FCLT_STR_CD AS FCLT_STR_CD , FCLT.FCLT_NTSL_CD AS FCLT_NTSL_CD , FCLT.FCLT_NTSL_CD2 AS FCLT_NTSL_CD2 , FCLT.FCLT_NTSL_CD3 AS FCLT_NTSL_CD3 , FCLT.FCLT_NTSL_CD4 AS FCLT_NTSL_CD4 , FCLT.FCLT_NTSL_CD5 AS FCLT_NTSL_CD5 , FCLT.FCLT_NTSL_CD6 AS FCLT_NTSL_CD6 , FCLT.FCLT_NTSL_CD7 AS FCLT_NTSL_CD7 , FCLT.FCLT_NTSL_CD8 AS FCLT_NTSL_CD8 , FCLT.FCLT_NTSL_CD9 AS FCLT_NTSL_CD9
FROM TB_FCLT_PRCS_NTSL_INFO FCLT
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltPrcsNtslInfoMapper.selectFcltPrcsNtslInfoList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.FCLT_STR_CD AS FCLT_STR_CD , FCLT.FCLT_NTSL_CD AS FCLT_NTSL_CD , FCLT.FCLT_NTSL_CD2 AS FCLT_NTSL_CD2 , FCLT.FCLT_NTSL_CD3 AS FCLT_NTSL_CD3 , FCLT.FCLT_NTSL_CD4 AS FCLT_NTSL_CD4 , FCLT.FCLT_NTSL_CD5 AS FCLT_NTSL_CD5 , FCLT.FCLT_NTSL_CD6 AS FCLT_NTSL_CD6 , FCLT.FCLT_NTSL_CD7 AS FCLT_NTSL_CD7 , FCLT.FCLT_NTSL_CD8 AS FCLT_NTSL_CD8 , FCLT.FCLT_NTSL_CD9 AS FCLT_NTSL_CD9 FROM TB_FCLT_PRCS_NTSL_INFO FCLT WHERE 1=1
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
     	MERGE INTO TB_FCLT_PRCS_NTSL_INFO FCLT /* com.at.apcss.fm.fclt.mapper.FcltPrcsNtslInfoMapper.insertFcltPrcsNtslInfo */
		USING DUAL
		ON (
			FCLT.TRGT_YR = #{trgtYr}
			AND FCLT.APC_CD = #{apcCd}
			)
		WHEN MATCHED THEN
		  UPDATE SET
				  FCLT_STR_CD    = #{fcltStrCd}
    	        , FCLT_NTSL_CD   = #{fcltNtslCd}
    			, FCLT_NTSL_CD2	 = #{fcltNtslCd2}
    	        , FCLT_NTSL_CD3  = #{fcltNtslCd3}
    	        , FCLT_NTSL_CD4  = #{fcltNtslCd4}
    	        , FCLT_NTSL_CD5  = #{fcltNtslCd5}
    	        , FCLT_NTSL_CD6  = #{fcltNtslCd6}
    	        , FCLT_NTSL_CD7  = #{fcltNtslCd7}
    	        , FCLT_NTSL_CD8  = #{fcltNtslCd8}
    	        , FCLT_NTSL_CD9  = #{fcltNtslCd9}
				  , SYS_LAST_CHG_DT		= SYSDATE
	    		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
		WHEN NOT MATCHED THEN
		  INSERT (
				  TRGT_YR
	    		  , APC_CD
	    		  , FCLT_STR_CD
	    		  , FCLT_NTSL_CD
	    		  , FCLT_NTSL_CD2
				  , FCLT_NTSL_CD3
				  , FCLT_NTSL_CD4
				  , FCLT_NTSL_CD5
				  , FCLT_NTSL_CD6
				  , FCLT_NTSL_CD7
				  , FCLT_NTSL_CD8
				  , FCLT_NTSL_CD9
	    		  , SYS_FRST_INPT_DT
	    		  , SYS_FRST_INPT_USER_ID
	    		  , SYS_FRST_INPT_PRGRM_ID
	    		  , SYS_LAST_CHG_DT
	    		  , SYS_LAST_CHG_USER_ID
	    		  , SYS_LAST_CHG_PRGRM_ID
	    		)
	   	VALUES
	   		    (
	   			  #{trgtYr}
	   			  , #{apcCd}
	   			  , #{fcltStrCd}
	   			  , #{fcltNtslCd}
	   			  , #{fcltNtslCd2}
	   			  , #{fcltNtslCd3}
	   			  , #{fcltNtslCd4}
	   			  , #{fcltNtslCd5}
	   			  , #{fcltNtslCd6}
	   			  , #{fcltNtslCd7}
	   			  , #{fcltNtslCd8}
	   			  , #{fcltNtslCd9}
	   			  , SYSDATE
	   			  , #{sysFrstInptUserId}
	   			  , #{sysFrstInptPrgrmId}
	   			  , SYSDATE
	   			  , #{sysLastChgUserId}
	   			  , #{sysLastChgPrgrmId}
		     	)


	    	UPDATE /* com.at.apcss.fm.fclt.mapper.FcltPrcsNtslInfoMapper.updateFcltPrcsNtslInfo */
	    		TB_FCLT_PRCS_NTSL_INFO FCLTT
			 	  FCLT_STR_CD    = #{fcltStrCd}
    	        , FCLT_NTSL_CD   = #{fcltNtslCd}
    			, FCLT_NTSL_CD2	 = #{fcltNtslCd2}
    	        , FCLT_NTSL_CD3  = #{fcltNtslCd3}
    	        , FCLT_NTSL_CD4  = #{fcltNtslCd4}
    	        , FCLT_NTSL_CD5  = #{fcltNtslCd5}
    	        , FCLT_NTSL_CD6  = #{fcltNtslCd6}
    	        , FCLT_NTSL_CD7  = #{fcltNtslCd7}
    	        , FCLT_NTSL_CD8  = #{fcltNtslCd8}
    	        , FCLT_NTSL_CD9  = #{fcltNtslCd9}


				, DEL_YN 	= #{delYn}


				, SYS_LAST_CHG_DT		= SYSDATE
	    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    		, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

			 WHERE 1=1
		     AND TRGT_YR = #{trgtYr}
		     AND  APC_CD = #{apcCd}

mapper.delete:

    	DELETE /* com.at.apcss.fm.fclt.mapper.FcltPrcsNtslInfoMapper.deleteFcltPrcsNtslInfo */
    		TB_FCLT_PRCS_NTSL_INFO FCLT
		 WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}
