,(SELECT APC_TRMT_AMT FROM TB_TE_APC_OPER_PRFMNC_DTL WHERE CRTR_YR = AOPO.CRTR_YR AND APC_CD = AOPO.APC_CD AND SN = '1' ) AS APC_TRMT_AMT_1 ,(SELECT APC_TRMT_AMT FROM TB_TE_APC_OPER_PRFMNC_DTL WHERE CRTR_YR = AOPO.CRTR_YR AND APC_CD = AOPO.APC_CD AND SN = '2' ) AS APC_TRMT_AMT_2 ,(SELECT APC_TRMT_AMT FROM TB_TE_APC_OPER_PRFMNC_DTL WHERE CRTR_YR = AOPO.CRTR_YR AND APC_CD = AOPO.APC_CD AND SN = '3' ) AS APC_TRMT_AMT_3 ,(SELECT APC_TRMT_AMT FROM TB_TE_APC_OPER_PRFMNC_DTL WHERE CRTR_YR = AOPO.CRTR_YR AND APC_CD = AOPO.APC_CD AND SN = '4' ) AS APC_TRMT_AMT_4 ,(SELECT SUM(APC_TRMT_AMT) FROM TB_TE_APC_OPER_PRFMNC_DTL WHERE CRTR_YR = AOPO.CRTR_YR AND APC_CD = AOPO.APC_CD GROUP BY APC_CD ) AS APC_TRMT_AMT_TOT
,(SELECT RTL_OGNZ_TOT_TRMT_AMT FROM TB_TE_APC_OPER_PRFMNC_DTL WHERE CRTR_YR = AOPO.CRTR_YR AND APC_CD = AOPO.APC_CD AND SN = '1' ) AS RTL_OGNZ_TOT_TRMT_AMT_1 ,(SELECT RTL_OGNZ_TOT_TRMT_AMT FROM TB_TE_APC_OPER_PRFMNC_DTL WHERE CRTR_YR = AOPO.CRTR_YR AND APC_CD = AOPO.APC_CD AND SN = '2' ) AS RTL_OGNZ_TOT_TRMT_AMT_2 ,(SELECT RTL_OGNZ_TOT_TRMT_AMT FROM TB_TE_APC_OPER_PRFMNC_DTL WHERE CRTR_YR = AOPO.CRTR_YR AND APC_CD = AOPO.APC_CD AND SN = '3' ) AS RTL_OGNZ_TOT_TRMT_AMT_3 ,(SELECT RTL_OGNZ_TOT_TRMT_AMT FROM TB_TE_APC_OPER_PRFMNC_DTL WHERE CRTR_YR = AOPO.CRTR_YR AND APC_CD = AOPO.APC_CD AND SN = '4' ) AS RTL_OGNZ_TOT_TRMT_AMT_4 ,(SELECT SUM(RTL_OGNZ_TOT_TRMT_AMT) FROM TB_TE_APC_OPER_PRFMNC_DTL WHERE CRTR_YR = AOPO.CRTR_YR AND APC_CD = AOPO.APC_CD GROUP BY APC_CD ) AS RTL_OGNZ_TOT_TRMT_AMT_TOT
FROM TB_TE_APC_OPER_PRSN_OTLN AOPO ,TB_TE_APC_OPER_PRFMNC AOP WHERE 1=1 AND AOPO.APC_CD = AOP.APC_CD(+) AND AOPO.CRTR_YR = AOP.CRTR_YR(+) AND AOPO.APC_CD IS NOT NULL
AND AOPO.CRTR_YR = #{crtrYr}
AND AOPO.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
