mapper.update:
MERGE INTO TB_TE_APC_OPER_PRFMNC AOP /* com.at.apcss.fm.fclt.mapper.FcltPrcsNtslInfoMapper.insertFcltPrcsNtslInfo */
USING DUAL
ON (
	AOP.CRTR_YR = #{crtrYr}
	AND AOP.APC_CD = #{apcCd}
	)
WHEN MATCHED THEN
  UPDATE SET
		  APC_NTSL_AMT_LGSZ_RTL				= #{apcNtslAmtLgszRtl}
		  , APC_NTSL_AMT_FOOD_MTRL			= #{apcNtslAmtFoodMtrl}
