		  , APC_NTSL_AMT_ONLN_B2C			= #{apcNtslAmtOnlnB2c}
		  , APC_NTSL_AMT_ETC				= #{apcNtslAmtEtc}
		  , APC_NTSL_AMT_ONLN_WHLSL_MRKT	= #{apcNtslAmtOnlnWhlslMrkt}

		  , SYS_LAST_CHG_DT			= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
