mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.selectFcltPrcsPrfmnc */ OTI.CRTR_YR , OTI.APC_CD ,(SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = OTI.APC_CD) AS APC_NM , OTI.SN , OTI.ITEM_CD , OTI.ITEM_NM
, AOPD.APC_TRMT_AMT , AOPD.APC_TRMT_VLM , AOPD.TM_SPMT_AMT , AOPD.TM_SPMT_RT , AOPD.APC_GNRL_TRMT_AMT , AOPD.APC_OGNZ_CPRTN_SORT_TRST , AOPD.APC_CTRT_EMSPAP FROM TB_TE_APC_OPER_TRMT_ITEM OTI , TB_TE_APC_OPER_PRFMNC_DTL AOPD WHERE 1=1 AND OTI.APC_CD = AOPD.APC_CD(+) AND OTI.CRTR_YR = AOPD.CRTR_YR(+) AND OTI.SN = AOPD.SN(+) AND OTI.OGNZ_SE_CD = '2' AND OTI.APC_CD IS NOT NULL
AND OTI.CRTR_YR = #{crtrYr}
AND OTI.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.selectFcltPrcsPrfmncList */ OTI.CRTR_YR , OTI.APC_CD ,(SELECT X.APC_NM FROM TB_APC_INFO X WHERE X.APC_CD = OTI.APC_CD) AS APC_NM , OTI.SN , OTI.ITEM_CD , OTI.ITEM_NM
, AOPD.APC_TRMT_AMT , AOPD.APC_TRMT_VLM , AOPD.TM_SPMT_AMT , AOPD.TM_SPMT_RT , AOPD.APC_GNRL_TRMT_AMT , AOPD.APC_OGNZ_CPRTN_SORT_TRST , AOPD.APC_CTRT_EMSPAP FROM TB_TE_APC_OPER_TRMT_ITEM OTI LEFT JOIN TB_TE_APC_OPER_PRFMNC_DTL AOPD ON AOPD.APC_CD = OTI.APC_CD AND AOPD.CRTR_YR = OTI.CRTR_YR AND AOPD.SN = OTI.SN AND AOPD.DEL_YN = 'N' WHERE OTI.CRTR_YR = #{crtrYr} AND OTI.OGNZ_SE_CD = '2' AND OTI.APC_CD IS NOT NULL AND OTI.DEL_YN = 'N'
AND OTI.APC_CD = #{apcCd}
UNION ALL SELECT AOPD.CRTR_YR , AOPD.APC_CD , (SELECT APC_NM FROM TB_APC_INFO X WHERE X.APC_CD = AOPD.APC_CD) AS APC_NM , AOPD.SN , AOPD.ITEM_CD , AOPD.ITEM_NM
, AOPD.APC_TRMT_AMT , AOPD.APC_TRMT_VLM , AOPD.TM_SPMT_AMT , AOPD.TM_SPMT_RT , AOPD.APC_GNRL_TRMT_AMT , AOPD.APC_OGNZ_CPRTN_SORT_TRST , AOPD.APC_CTRT_EMSPAP FROM TB_TE_APC_OPER_PRFMNC_DTL AOPD WHERE AOPD.CRTR_YR = #{crtrYr} AND AOPD.ITEM_CD = 'XC01' AND AOPD.DEL_YN = 'N'
AND AOPD.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
MERGE INTO TB_TE_APC_OPER_PRFMNC_DTL AOPD /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.insertFcltPrcsPrfmnc */
USING DUAL
ON (
	AOPD.CRTR_YR = #{crtrYr}
	AND AOPD.APC_CD = #{apcCd}
	AND AOPD.SN = #{sn}
	)
WHEN MATCHED THEN
  UPDATE SET
		  AOPD.APC_GNRL_TRMT_AMT			= #{apcGnrlTrmtAmt}
		  , AOPD.APC_OGNZ_CPRTN_SORT_TRST	= #{apcOgnzCprtnSortTrst}
		  , AOPD.APC_CTRT_EMSPAP			= #{apcCtrtEmspap}

		  , AOPD.APC_TRMT_AMT	= #{apcTrmtAmt}
		  , AOPD.APC_TRMT_VLM	= #{apcTrmtVlm}

		  , AOPD.TM_SPMT_AMT	= #{tmSpmtAmt}
		  , AOPD.TM_SPMT_RT		= #{tmSpmtRt}

		  , SYS_LAST_CHG_DT		= SYSDATE
		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
WHEN NOT MATCHED THEN
  INSERT (
		  CRTR_YR
		  , APC_CD
		  , APC_NM
		  , ITEM_CD
		  , ITEM_NM
		  , SN

		  , AOPD.APC_GNRL_TRMT_AMT
		  , AOPD.APC_OGNZ_CPRTN_SORT_TRST
		  , AOPD.APC_CTRT_EMSPAP

		  , AOPD.APC_TRMT_AMT
		  , AOPD.APC_TRMT_VLM

		  , AOPD.TM_SPMT_AMT
		  , AOPD.TM_SPMT_RT

		  , SYS_FRST_INPT_DT
		  , SYS_FRST_INPT_USER_ID
		  , SYS_FRST_INPT_PRGRM_ID
		  , SYS_LAST_CHG_DT
		  , SYS_LAST_CHG_USER_ID
		  , SYS_LAST_CHG_PRGRM_ID
		)
VALUES
		(
		  #{crtrYr}
		  , #{apcCd}
		  , (SELECT APC_NM FROM TB_TE_APC_OPER_PRSN_OTLN WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr})
		  , (SELECT ITEM_CD FROM TB_TE_APC_OPER_TRMT_ITEM WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr} AND SN = #{sn} AND OGNZ_SE_CD = '2')
		  , (SELECT ITEM_NM FROM TB_TE_APC_OPER_TRMT_ITEM WHERE APC_CD = #{apcCd} AND CRTR_YR = #{crtrYr} AND SN = #{sn} AND OGNZ_SE_CD = '2')

		  , #{sn}

		  , #{apcGnrlTrmtAmt}
		  , #{apcOgnzCprtnSortTrst}
		  ,	#{apcCtrtEmspap}

		  , #{apcTrmtAmt}
		  , #{apcTrmtVlm}

		  , #{tmSpmtAmt}
		  , #{tmSpmtRt}

		  , SYSDATE
		  , #{sysFrstInptUserId}
		  , #{sysFrstInptPrgrmId}
		  , SYSDATE
		  , #{sysLastChgUserId}
		  , #{sysLastChgPrgrmId}
		)


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.updateFcltPrcsPrfmnc */
	TB_TE_APC_OPER_PRFMNC_DTL AOPD
   SET
	  AOPD.APC_GNRL_TRMT_AMT			= #{apcGnrlTrmtAmt}
	  , AOPD.APC_OGNZ_CPRTN_SORT_TRST	= #{apcOgnzCprtnSortTrst}
	  , AOPD.APC_CTRT_EMSPAP			= #{apcCtrtEmspap}

	  , AOPD.APC_TRMT_AMT	= #{apcTrmtAmt}
	  , AOPD.APC_TRMT_VLM	= #{apcTrmtVlm}

	  , AOPD.TM_SPMT_AMT	= #{tmSpmtAmt}
	  , AOPD.TM_SPMT_RT		= #{tmSpmtRt}


	, DEL_YN 	= #{delYn}


	, SYS_LAST_CHG_DT		= SYSDATE
	, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

WHERE 1=1
  AND CRTR_YR = #{crtrYr}
  AND APC_CD = #{apcCd}
  AND SN = #{sn}


UPDATE /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.deleteFcltPrcsPrfmnc */
	TB_TE_APC_OPER_PRFMNC_DTL AOPD
   SET
	  DEL_YN 	= 'Y'
WHERE 1=1
  AND CRTR_YR = #{crtrYr}
  AND APC_CD = #{apcCd}
  AND SN = #{sn}
