mapper.select:
SELECT /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.selectFcltPrcsPrfmnc */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.FCLT_PRCS_SE_CD AS FCLT_PRCS_SE_CD , FCLT.FCLT_PRCS_TRMT_CD AS FCLT_PRCS_TRMT_CD , FCLT.FCLT_PRCS_TRMT_AMT AS FCLT_PRCS_TRMT_AMT , FCLT.FCLT_PRCS_TRMT_AMT2 AS FCLT_PRCS_TRMT_AMT2 , FCLT.FCLT_PRCS_TRMT_AMT3 AS FCLT_PRCS_TRMT_AMT3 , FCLT.FCLT_PRCS_TRMT_AMT4 AS FCLT_PRCS_TRMT_AMT4
FROM TB_FCLT_PRCS_PRFMNC FCLT WHERE 1=1 AND DEL_YN != 'Y' AND APC_CD IS NOT NULL
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.selectFcltPrcsPrfmncList */ FCLT.TRGT_YR AS TRGT_YR , FCLT.APC_CD AS APC_CD , (SELECT APC_NM FROM TB_APC_INFO WHERE APC_CD = FCLT.APC_CD) AS APC_NM , FCLT.FCLT_PRCS_SE_CD AS FCLT_PRCS_SE_CD , FCLT.FCLT_PRCS_TRMT_CD AS FCLT_PRCS_TRMT_CD , FCLT.FCLT_PRCS_TRMT_AMT AS FCLT_PRCS_TRMT_AMT , FCLT.FCLT_PRCS_TRMT_AMT2 AS FCLT_PRCS_TRMT_AMT2 , FCLT.FCLT_PRCS_TRMT_AMT3 AS FCLT_PRCS_TRMT_AMT3 , FCLT.FCLT_PRCS_TRMT_AMT4 AS FCLT_PRCS_TRMT_AMT4 FROM TB_FCLT_PRCS_PRFMNC FCLT WHERE 1=1 AND DEL_YN != 'Y' AND APC_CD IS NOT NULL
AND FCLT.TRGT_YR = #{trgtYr}
AND FCLT.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.update:
     	MERGE INTO TB_FCLT_PRCS_PRFMNC FCLT /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.insertFcltPrcsPrfmnc */
		USING DUAL
		ON (
			FCLT.TRGT_YR = #{trgtYr}
			AND FCLT.APC_CD = #{apcCd}
			)
		WHEN MATCHED THEN
		  UPDATE SET
				  FCLT_PRCS_SE_CD     = #{fcltPrcsSeCd}
			 	, FCLT_PRCS_TRMT_CD   = #{fcltPrcsTrmtCd}
			 	, FCLT_PRCS_TRMT_AMT  = #{fcltPrcsTrmtAmt}
		    	, FCLT_PRCS_TRMT_AMT2 = #{fcltPrcsTrmtAmt2}
		    	, FCLT_PRCS_TRMT_AMT3 = #{fcltPrcsTrmtAmt3}
		    	, FCLT_PRCS_TRMT_AMT4 = #{fcltPrcsTrmtAmt4}
				  , SYS_LAST_CHG_DT		= SYSDATE
	    		  , SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
	    		  , SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}
		WHEN NOT MATCHED THEN
		  INSERT (
				  TRGT_YR
	    		  , APC_CD
	    		  , FCLT_PRCS_SE_CD
	    		  , FCLT_PRCS_TRMT_CD
	    		  , FCLT_PRCS_TRMT_AMT
		    	  ,	FCLT_PRCS_TRMT_AMT2
		    	  ,	FCLT_PRCS_TRMT_AMT3
		    	  ,	FCLT_PRCS_TRMT_AMT4
		    	  , SYS_FRST_INPT_DT
	    		  , SYS_FRST_INPT_USER_ID
	    		  , SYS_FRST_INPT_PRGRM_ID
	              , SYS_LAST_CHG_DT
	              , SYS_LAST_CHG_USER_ID
	              , SYS_LAST_CHG_PRGRM_ID
	    		)
	   	VALUES
	   		    (
	   			  #{trgtYr}
	   			  , #{apcCd}
	   			  , #{fcltPrcsSeCd}
	   			  , #{fcltPrcsTrmtCd}
	   			  , #{fcltPrcsTrmtAmt}
	   			  ,	#{fcltPrcsTrmtAmt2}
		    	  , #{fcltPrcsTrmtAmt3}
		    	  , #{fcltPrcsTrmtAmt4}
	   			  , SYSDATE
	   			  , #{sysFrstInptUserId}
	   			  , #{sysFrstInptPrgrmId}
	   			  , SYSDATE
	   			  , #{sysLastChgUserId}
	   			  , #{sysLastChgPrgrmId}
		     	)


    	UPDATE /* com.at.apcss.fm.fclt.mapper.FcltPrcsPrfmncMapper.updateFcltPrcsPrfmnc */
    		TB_FCLT_PRCS_PRFMNC FCLT
		   SET
		 	FCLT_PRCS_SE_CD     = #{fcltPrcsSeCd}
		 	, FCLT_PRCS_TRMT_CD   = #{fcltPrcsTrmtCd}
		 	, FCLT_PRCS_TRMT_AMT  = #{fcltPrcsTrmtAmt}
	    	, FCLT_PRCS_TRMT_AMT2 = #{fcltPrcsTrmtAmt2}
	    	, FCLT_PRCS_TRMT_AMT3 = #{fcltPrcsTrmtAmt3}
	    	, FCLT_PRCS_TRMT_AMT4 = #{fcltPrcsTrmtAmt4}


			, DEL_YN 	= #{delYn}


			, SYS_LAST_CHG_DT		= SYSDATE
    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID	= #{sysLastChgPrgrmId}

		 WHERE 1=1
	     AND TRGT_YR = #{trgtYr}
	     AND  APC_CD = #{apcCd}
